            "juz": 14,
            "manzil": 3,
            "page": 263,
            "ruku": 219,
            "hizbQuarter": 105,
            "sajda": false
          },
          {
            "number": 1825,
            "text": "Ih, d Nekwni i Issidiren, i Ineqqen. D Nekwni ay d Iweôôaten.",
