            "juz": 14,
            "manzil": 3,
            "page": 263,
            "ruku": 220,
            "hizbQuarter": 105,
            "sajda": false
          },
          {
            "number": 1829,
            "text": "Nexleq loenn, iu lêal, si tmes n ccili.",
