            "hizbQuarter": 132,
            "sajda": false
          },
          {
            "number": 2593,
            "text": "Neppa, s tidep, Issen awal d iffen, Issen ayen tessusmem.",
            "numberInSurah": 110,
            "juz": 17,
            "manzil": 4,
            "page": 331,
            "ruku": 284,
