            "ruku": 314,
            "hizbQuarter": 146,
            "sajda": {
              "id": 8,
              "recommended": true,
              "obligatory": false
            }
          },
          {
            "number": 2916,
            "text": "Ad Iôqu Win Irran itran deg igenni. Irna degs, lefnaô, akked waggur bu tziri.",
