            "manzil": 6,
            "page": 483,
            "ruku": 419,
            "hizbQuarter": 193,
            "sajda": false
          },
          {
            "number": 4275,
            "text": "Akka i Iweêêa i keçç, akked wid ik id izwaren, Öebbi, Uzmir, Bab n ûûwab.",
            "numberInSurah": 3,
            "juz": 25,
            "manzil": 6,
            "page": 483,
