            "juz": 26,
            "manzil": 7,
            "page": 521,
            "ruku": 456,
            "hizbQuarter": 208,
            "sajda": false
          },
          {
            "number": 4702,
            "text": "Iqeôb asen t id; inna: 'day ur teppeppem ara'?",
