            "juz": 27,
            "manzil": 7,
            "page": 528,
            "ruku": 462,
            "hizbQuarter": 211,
            "sajda": false
          },
          {
            "number": 4831,
            "text": "FellaS, axlaq nniven.",
            "numberInSurah": 47,
            "juz": 27,
            "manzil": 7,
            "page": 528,
            "ruku": 462,
            "hizbQuarter": 211,
            "sajda": false
          },
          {
            "number": 4832,
            "text": "D Neppa i Iennun, i Iqenâan.",
            "numberInSurah": 48,
            "juz": 27,
            "manzil": 7,
            "page": 528,
            "ruku": 462,
            "hizbQuarter": 211,
            "sajda": false
          },
          {
            "number": 4833,
            "text": "D Neppa i d Mass n Ciâra.",
            "numberInSurah": 49,
            "juz": 27,
            "manzil": 7,
            "page": 528,
            "ruku": 462,
            "hizbQuarter": 211,
            "sajda": false
          },
          {
            "number": 4834,
            "text": "D Neppa i Isnegren Âad imezwura.",
            "numberInSurah": 50,
            "juz": 27,
            "manzil": 7,
            "page": 528,
            "ruku": 462,
            "hizbQuarter": 211,
            "sajda": false
          },
          {
            "number": 4835,
            "text": "Si Tamud, ur d Iooi ara.",
            "numberInSurah": 51,
            "juz": 27,
