            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5422,
            "text": "Âabdet Öebbi, têezzbem aS, tessemêessem iyi.",
