      {
        "number": 56,
        "name": "سُورَةُ الوَاقِعَةِ",
        "englishName": "Al-Waaqia",
        "englishNameTranslation": "The Inevitable",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 4980,
            "text": "When there happeneth Event.",
            "numberInSurah": 1,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4981,
            "text": "There is about its happening no lie.",
            "numberInSurah": 2,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4982,
            "text": "Abasing exalting.",
            "numberInSurah": 3,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4983,
            "text": "This will happen when the earth is shaken, shaken",
            "numberInSurah": 4,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4984,
            "text": "And the mountains are crumbled, crumbled.",
            "numberInSurah": 5,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4985,
            "text": "So that they become dust seattered.",
            "numberInSurah": 6,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
