            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5685,
            "text": "and placed therein a hot, shining lamp,",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
