            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5803,
            "text": "when the mountains shall be set in motion,",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5804,
            "text": "when the ten-months pregnant camels shall be abandoned,",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5805,
            "text": "when the savage beasts shall be brought together,",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5806,
            "text": "when the seas shall be set boiling,",
