            "numberInSurah": 146,
            "juz": 19,
            "manzil": 5,
            "page": 373,
            "ruku": 323,
            "hizbQuarter": 149,
            "sajda": false
          },
          {
            "number": 3079,
            "text": "among gardens and fountains,",
            "numberInSurah": 147,
            "juz": 19,
            "manzil": 5,
            "page": 373,
            "ruku": 323,
            "hizbQuarter": 149,
            "sajda": false
          },
          {
            "number": 3080,
            "text": "sown fields, and palms with slender spathes?",
