            "numberInSurah": 149,
            "juz": 19,
            "manzil": 5,
            "page": 373,
            "ruku": 323,
            "hizbQuarter": 149,
            "sajda": false
          },
          {
            "number": 3082,
            "text": "So fear you God, and obey you me;",
            "numberInSurah": 150,
            "juz": 19,
            "manzil": 5,
            "page": 373,
            "ruku": 323,
            "hizbQuarter": 149,
            "sajda": false
          },
