            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5788,
            "text": "and dense-tree'd gardens,",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5789,
            "text": "and fruits, and pastures,",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
