            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5996,
            "text": "by the even and the odd,",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
