            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5998,
            "text": "Is there in that an oath for a mindful man?",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5999,
            "text": "Hast thou not seen how thy Lord did with Ad,",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6000,
            "text": "Iram of the pillars,",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6001,
            "text": "the like of which was never created in the land,",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
