            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6020,
            "text": "'O soul at peace,",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6021,
            "text": "return unto thy Lord, well-pleased, well-pleasing!",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6022,
            "text": "Enter thou among My servants!",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6023,
            "text": "Enter thou My Paradise!'",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
