            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6204,
            "text": "and refuse charity.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          }
