      {
        "number": 109,
        "name": "سُورَةُ الكَافِرُونَ",
        "englishName": "Al-Kaafiroon",
        "englishNameTranslation": "The Disbelievers",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6208,
            "text": "Say: 'O unbelievers,",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6209,
            "text": "I serve not what you serve",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6210,
            "text": "and you are not serving what I serve,",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6211,
            "text": "nor am I serving what you have served,",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6212,
            "text": "neither are you serving what I serve",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6213,
            "text": "To you your religion, and to me my religion!'",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 110,
        "name": "سُورَةُ النَّصۡرِ",
