            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6215,
            "text": "and thou seest men entering God's religion in throngs,",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6216,
            "text": "then proclaim the praise of thy Lord, and seek His forgiveness; for He turns again unto men.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
