            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6218,
            "text": "His wealth avails him not, neither what he has earned;",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
