            "text": "Say: 'He is God, One,",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6223,
            "text": "God, the Everlasting Refuge,",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6224,
            "text": "who has not begotten, and has not been begotten,",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6225,
            "text": "and equal to Him is not any one.'",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
