            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6228,
            "text": "from the evil of darkness when it gathers,",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
