            "juz": 27,
            "manzil": 7,
            "page": 531,
            "ruku": 466,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4909,
            "text": "Do not transgress the scales.",
            "numberInSurah": 8,
            "juz": 27,
            "manzil": 7,
            "page": 531,
            "ruku": 466,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
