            "numberInSurah": 10,
            "juz": 27,
            "manzil": 7,
            "page": 531,
            "ruku": 466,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4912,
            "text": "Therein are fruits and palm trees with sheaths,",
            "numberInSurah": 11,
            "juz": 27,
            "manzil": 7,
            "page": 531,
            "ruku": 466,
            "hizbQuarter": 213,
            "sajda": false
          },
