        "englishNameTranslation": "The Ascending Stairways",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5376,
            "text": "A caller supplicated about a punishment to fall on",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
