            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5793,
            "text": "his mother and his father,",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5794,
            "text": "his wife and his children.",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5795,
            "text": "Everyone on that Day will have affairs to keep him occupied.",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5796,
            "text": "Some faces will shine,",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5797,
