            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5819,
            "text": "it is indeed the word of an Honorable Messenger,",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
