            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5912,
            "text": "By the witness and the witnessed!",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5913,
