            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5393,
            "text": "and who accumulated wealth without spending it for a good purpose.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
