            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5499,
            "text": "cleanse your clothes,",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5500,
