            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5680,
            "text": "Have We not created you in pairs,",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
