          },
          {
            "number": 4962,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 61,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4963,
            "text": "Kuma baicinsu akwai waɗansu gidãjen Aljanna biyu.",
            "numberInSurah": 62,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4964,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 63,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4965,
            "text": "Mãsu duhun inuwa.",
            "numberInSurah": 64,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4966,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 65,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4967,
            "text": "A cikinsu akwai marẽmari biyu masu kwarãrar ruwa.",
            "numberInSurah": 66,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4968,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 67,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4969,
            "text": "A cikinsu akwai 'ya'yan itãcen marmari da dabĩno darummãni.",
            "numberInSurah": 68,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4970,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 69,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4971,
            "text": "A cikinsu, akwai wasu mãtã mãsu kyaun hãlãye, mãsu kyaun halitta.",
            "numberInSurah": 70,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4972,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 71,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4973,
            "text": "Mãsu farin idãnu da baƙinsu waɗanda aka tsare a cikin haimõmi.",
            "numberInSurah": 72,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4974,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 73,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4975,
            "text": "Wani mutum, gabanin mazajensu bai ɗẽbe budurcinsu ba, kuma haka wani aljani.",
            "numberInSurah": 74,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4976,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 75,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4977,
            "text": "Sunã gincire a kan wasu matãsai mãsu kõren launi da katĩfun Abkara kyãwãwa.",
            "numberInSurah": 76,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4978,
            "text": "To, sabõda wanne daga ni'imõmin Ubangijinku, kuke ƙaryatãwa?",
            "numberInSurah": 77,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4979,
            "text": "Sũnan Ubangjinka, Mai girman Jalãla da Karimci, ya tsarkaka.",
            "numberInSurah": 78,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          }
        ]
      },
      {
        "number": 56,
        "name": "سُورَةُ الوَاقِعَةِ",
        "englishName": "Al-Waaqia",
        "englishNameTranslation": "The Inevitable",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 4980,
            "text": "Idan mai aukuwa ta auku.",
            "numberInSurah": 1,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4981,
            "text": "Bãbu wani (rai) mai ƙaryatãwa ga aukuwarta.",
            "numberInSurah": 2,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4982,
            "text": "(Ita) mai ƙasƙantãwa ce, mai ɗaukakãwa.",
            "numberInSurah": 3,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4983,
            "text": "Idan aka girgiza ƙasã girgizwa.",
            "numberInSurah": 4,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4984,
            "text": "Kuma aka niƙe duwãtsu, niƙẽwa.",
            "numberInSurah": 5,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4985,
            "text": "Sai suka kasance ƙũra da ake wãtsarwa.",
            "numberInSurah": 6,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4986,
            "text": "Kuma kun kasance nau'i uku.",
            "numberInSurah": 7,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4987,
            "text": "Watau mazõwa dãma. Mẽne ne mazõwa dãma?",
            "numberInSurah": 8,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4988,
            "text": "Da mazõwa hagu. Mẽne ne mazõwa hagu?",
            "numberInSurah": 9,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4989,
            "text": "Da waɗanda suka tsẽre. Sũ wɗanda suka tsẽren nan,",
            "numberInSurah": 10,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4990,
            "text": "Waɗancan, sũ ne waɗanda aka kusantar.",
            "numberInSurah": 11,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4991,
            "text": "A ckin Aljannar ni'ima.",
            "numberInSurah": 12,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4992,
            "text": "Jama'a ne daga mutãnen farko.",
            "numberInSurah": 13,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4993,
            "text": "Da kaɗan daga mutãnen ƙarshe.",
            "numberInSurah": 14,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4994,
            "text": "(Sunã) a kan wasu gadãje sãƙaƙƙuu.",
            "numberInSurah": 15,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4995,
            "text": "Sunã gincire a kansu, sunã mãsu kallon jũna.",
            "numberInSurah": 16,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4996,
            "text": "Wasu yara samãri na dindindin gẽwaya a kansu.",
            "numberInSurah": 17,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4997,
            "text": "Da wasu kõfuna da shantula da hinjãlai daga (giya) mai ɓuɓɓuga.",
            "numberInSurah": 18,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4998,
            "text": "Bã a sanya musu cĩwon jirĩ sabõda ita, kuma bã su buguwa.",
            "numberInSurah": 19,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4999,
            "text": "Da wasu 'ya'yan itãcen marmari daga irin waɗanda suke zãɓe.",
            "numberInSurah": 20,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5000,
            "text": "Da nãman tsuntsãye daga wanda suke ganin sha'awa.",
            "numberInSurah": 21,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5001,
            "text": "Da wasu mãtã mãsu fararen idanu da girmansu.",
            "numberInSurah": 22,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5002,
            "text": "Kamar misãlan lu'ulu'u wanda aka ɓõye.",
            "numberInSurah": 23,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5003,
            "text": "A kan sakamakon, dõmin abin da suka kasance sunã aikatãwa.",
            "numberInSurah": 24,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5004,
            "text": "Bã su jin wata yãsassar magana a cikinta, kuma bã su jin sun yi laifi.",
            "numberInSurah": 25,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5005,
            "text": "Sai dai wata magana (mai dãɗi): Salãmun, Salãmun.",
            "numberInSurah": 26,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5006,
            "text": "Da mazõwa dãma. Mẽne ne mazõwa dãma?",
            "numberInSurah": 27,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5007,
            "text": "(Sunã) a cikin itãcen magarya maras ƙaya.",
            "numberInSurah": 28,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5008,
            "text": "Da wata ayaba mai yawan 'ya'ya.",
            "numberInSurah": 29,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5009,
            "text": "Da wata inuwa mĩƙaƙƙiya.",
            "numberInSurah": 30,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5010,
            "text": "Da wani ruwa mai gudãna.",
            "numberInSurah": 31,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5011,
            "text": "Da wasu 'ya'yan itacen marmari mãsu yawa.",
            "numberInSurah": 32,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5012,
            "text": "Bã su yankẽwa kuma bã a hana su.",
            "numberInSurah": 33,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5013,
            "text": "Da wasu shimfiɗu maɗaukaka.",
            "numberInSurah": 34,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5014,
            "text": "Lalle Mũ, Mun ƙãga halittarsu ƙãgãwa.",
            "numberInSurah": 35,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5015,
            "text": "Sa'an nan Muka sanya su budurwai.",
            "numberInSurah": 36,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5016,
            "text": "Mãsu son mazansu, a cikin tsãrã ɗaya.",
            "numberInSurah": 37,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5017,
            "text": "Ga mazõwa dãma.",
            "numberInSurah": 38,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5018,
            "text": "Wata ƙungiya ce daga mutãnen farko.",
            "numberInSurah": 39,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5019,
            "text": "Da wata ƙungiya daga mutãnen ƙarshe.",
            "numberInSurah": 40,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5020,
            "text": "Mazõwa hagu, Mẽne ne mazõwa hagu?",
            "numberInSurah": 41,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5021,
            "text": "Sunã a cikin wata iskar zãfi da wani ruwan zãfi.",
            "numberInSurah": 42,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5022,
            "text": "Da wata inuwa ta hayãƙi mai baƙi.",
            "numberInSurah": 43,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5023,
            "text": "Bã mai sanyi ba, kuma bã mai wata ni'ima ba.",
            "numberInSurah": 44,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5024,
            "text": "Lalle sũ, sun kasance a gabãnin wannan waɗanda aka jiyar dãɗi.",
            "numberInSurah": 45,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5025,
            "text": "Kuma sun kasance sunã dõgẽwa a kan mummũnan zunubi mai girma.",
            "numberInSurah": 46,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5026,
            "text": "Kuma sun kasance sunã cẽwa: 'Shin idan mun mutukuma muko kasance turɓãya da ƙasũsuwa shin lalle mũ waɗanda zã a kõma rãyarwa ne haƙĩƙatan?'",
            "numberInSurah": 47,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5027,
            "text": "'Shin, kuma da ubanninmu na farko?'",
            "numberInSurah": 48,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5028,
            "text": "Ka ce: 'Lalle mutãnen farko da na ƙarshe.'",
            "numberInSurah": 49,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5029,
            "text": "'Tabbas, waɗanda ake tãrãwa ne a cikin wani yini sananne.'",
            "numberInSurah": 50,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5030,
            "text": "'Sa'an nan lalle ku, ya kũ ɓatattu, mãsu ƙaryatãwa!'",
            "numberInSurah": 51,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5031,
            "text": "'Lalle mãsu cĩ ne daga wata itãciya ta zaƙƙum (ɗanyen wutã).'",
            "numberInSurah": 52,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5032,
            "text": "'Har za ku zama mãsu cika cikunna daga gare ta.'",
            "numberInSurah": 53,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5033,
            "text": "'Sa'an nan kuma mãsu shã ne, a kan wannan abin cin, daga ruwan zãfi.'",
            "numberInSurah": 54,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5034,
            "text": "'Ku zama mãsu shã irin shan rãƙuma mãsu ƙishirwa.'",
            "numberInSurah": 55,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5035,
            "text": "Wannan ita ce liyãfarsu a rãnar sakamako.",
            "numberInSurah": 56,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5036,
            "text": "Mũ ne Muka halitta ku, to, don me bã zã ku gaskata ba?",
            "numberInSurah": 57,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5037,
            "text": "Shin kuma kun ga abin da kuke fitarwa na maniyyi?",
            "numberInSurah": 58,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5038,
            "text": "Shin kũ ne kuke halitta shi, kõ kuwa mũ ne Mãsu halittãwa?",
            "numberInSurah": 59,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5039,
            "text": "Mũ ne Muka ƙaddara mutuwa a tsakãninku, kuma ba Mu zama Mãsu gajiyãwa ba,",
            "numberInSurah": 60,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5040,
            "text": "A kan Mu musanya waɗansu (mutãne) kamarku, kuma Mu mayar da ku a cikin wata halitta da ba ku sani ba.",
            "numberInSurah": 61,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5041,
            "text": "Kuma lalle, ne haƙĩƙa, kun san halittar farko, to, don me ba zã ku yi tunãni ba?",
            "numberInSurah": 62,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5042,
            "text": "Shin, kuma kun ga abin da kũke nõmãwa?",
            "numberInSurah": 63,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5043,
            "text": "Shin, kũ ne ke tsirar da shi kõ kuwa Mũ ne Mãsu tsirarwa?",
            "numberInSurah": 64,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5044,
            "text": "Dã Munã so lalle, da Mun sanya shi bũsasshiyar ciyãwa, sai ku yini kunã mãmãkin bãƙin ciki.",
            "numberInSurah": 65,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5045,
            "text": "(Kunã cẽwa) 'Lalle haƙĩƙa an azã mana tãra!'",
            "numberInSurah": 66,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5046,
            "text": "'Ã'a, mun dai zama waɗanda aka hanã wa!'",
            "numberInSurah": 67,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5047,
            "text": "Shin, kuma kun ga ruwa wannan da kuke sha?",
            "numberInSurah": 68,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5048,
            "text": "Shin, kũ ne kuke saukar da shi daga girgije, kõ kuwa Mũne Mãsu saukarwa?",
            "numberInSurah": 69,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5049,
            "text": "Dã Mun so, dã Mun mayar da shi ruwan zartsi. To don me bã ku gõdẽwa?",
            "numberInSurah": 70,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5050,
            "text": "Shin, kuma kun ga wutã wannan da kuke ƙyastãwa?",
            "numberInSurah": 71,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5051,
            "text": "Shin, kũ ne kuke ƙagã halittar itãciyarta, kõ kuwa Mũ ne Mãsu ƙãgãwa?",
            "numberInSurah": 72,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5052,
            "text": "Mũ ne Muka sanya ta wata abar wa'azi da jin dãɗi ga matafiya a cikin jẽji.",
            "numberInSurah": 73,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5053,
            "text": "Sai ka tsarkake sũnan Ubangijinka Mai girma.",
            "numberInSurah": 74,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5054,
            "text": "To, bã sai Na yi rantsuwa ba da lõkutan fãɗuwar taurãri.",
            "numberInSurah": 75,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5055,
            "text": "Kuma lalle ne' haƙĩƙa, rantsuwa ce mai girma, dã kun sani.",
            "numberInSurah": 76,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5056,
            "text": "Lalle shi (wannan littãfi), haƙĩƙa, abin karantãwa ne mai daraja.",
            "numberInSurah": 77,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5057,
            "text": "A cikin wani littafi tsararre.",
            "numberInSurah": 78,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5058,
            "text": "Bãbu mai shãfa shi fãce waɗanda aka tsarkake.",
            "numberInSurah": 79,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5059,
            "text": "Wanda aka saukar ne daga Ubangijin halitta.",
            "numberInSurah": 80,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5060,
            "text": "Shin, to, wannan lãbãrin ne kuke mãsu wulãkantãwa?",
            "numberInSurah": 81,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5061,
            "text": "Kuma kunã sanya arzikinku (game da shi) lalle kũ, ku ƙaryata (shi)?",
            "numberInSurah": 82,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5062,
            "text": "To, don me idan rai ya kai ga maƙõshi? (Kusa da mutuwa).",
            "numberInSurah": 83,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5063,
            "text": "Alhãli kuwa kũ, a lõklcin nan, kunã kallo.",
            "numberInSurah": 84,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5064,
            "text": "Kuma Mũ ne mafi kusanta gare shi daga gare ku, to, amma kũ bã ku gani.",
            "numberInSurah": 85,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5065,
            "text": "To, don me in dai kun kasance bã waɗanda zã a yi wa sakamako ba?",
            "numberInSurah": 86,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5066,
            "text": "Ku mayar da shi (cikin jikinsa) har idan kun kasance mãsu gaskiya.",
            "numberInSurah": 87,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5067,
            "text": "To, amma idan (mai mutuwar) ya kasance daga makusanta,",
            "numberInSurah": 88,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5068,
            "text": "Sai hũtawa da kyakkyawan abinci da Aljannar ni'ima.",
            "numberInSurah": 89,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5069,
            "text": "Kuma amma idan ya kasance daga mazõwa dãma,",
            "numberInSurah": 90,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5070,
            "text": "Sai (a ce masa) aminci ya tabbata a gare ka daga mazõwa dãma.",
            "numberInSurah": 91,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5071,
            "text": "Kuma amma idan ya kasance daga mãsu ƙaryatãwar, ɓatattun,",
            "numberInSurah": 92,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5072,
            "text": "Sai wata liyãfa ta ruwan zãfi.",
            "numberInSurah": 93,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5073,
            "text": "Da ƙõnuwa da Jahĩm,",
            "numberInSurah": 94,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5074,
            "text": "Lalle wannan, haƙĩƙa, ita ce gaskiya ta yaƙĩni.",
            "numberInSurah": 95,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5075,
            "text": "Sabõda haka, ka tsarkake sũnan Ubangijinka, Mai karimci.",
            "numberInSurah": 96,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          }
        ]
      },
      {
        "number": 57,
        "name": "سُورَةُ الحَدِيدِ",
        "englishName": "Al-Hadid",
        "englishNameTranslation": "The Iron",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5076,
            "text": "Abin da ke cikin sammai da ƙasa yã yi tasbĩhi ga Allah. Kuma Shĩ ne Mabuwãyi, Mai hikima.",
            "numberInSurah": 1,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5077,
            "text": "Shĩ ne da mulkin sammai da ƙasa: Yanã rãyarwa kuma Yanã kashẽwa, kuma Shĩ Mai ikon yi ne a kan kõme.",
            "numberInSurah": 2,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5078,
            "text": "Shĩ ne Na farko, Na ƙarshe, Bayyananne, Bõyayye, kuma Shĩ Masani ne ga dukkan kõme.",
            "numberInSurah": 3,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5079,
            "text": "Shĩ ne wanda Ya halitta sammai da ƙasa a cikin wasu kwãnuka shida, sa'an nan Ya daidaitu a kan Al'arshi, Yanã sanin abin da ke shiga cikin ƙasa da abin da ke fita daga gare ta, da abin da ke sauka daga sama da abin da ke hawa cikinta, kuma Shĩ yanã tareda ku duk inda kuka kasance. Kuma Allah Mai gani ne ga abin da kuke aikatãwa.",
            "numberInSurah": 4,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5080,
            "text": "Shĩ ne da mulkin sammai da ƙasa, kuma zuwa gare Shĩ (Allah) kawai ake mayar da al'amura.",
            "numberInSurah": 5,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5081,
            "text": "Yanã shigar da dare a cikin yini, kuma Yanã shigar da yini a cikin dare, kuma Shi Masani ne ga abin da yake a cikin ƙirãza.",
            "numberInSurah": 6,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5082,
            "text": "Ku yi ĩmãni da Allah da ManzonSa, kuma ku ciyar daga abin da Allah Ya wakilta ku a cikinsa (na dũkiya). To, waɗannan da suka yi ĩmãni daga cikinku, kuma suka ciyar, sunã da wani sakamako mai girma.",
            "numberInSurah": 7,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5083,
            "text": "kuma me ya same ku, bã zã ku yi ĩmãni ba da Allah, alhãli kuwa manzonSa na kiran ku dõmin ku yi ĩmãni da Ubangijinku, kuma lalle Allah Ya karɓi alkawarinku (cẽwa zã ku bauta Masa)? (Ku bauta Masa) idan kun kasance mãsu ĩmãni.",
            "numberInSurah": 8,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5084,
            "text": "Shĩ ne wanda ke sassaukar da ãyõyi bayyanannu ga BãwanSa, dõmin Ya fitar da ku daga duffai zuwa ga haske, kuma lalle Allah haƙĩƙa Mai tausayi ne gare ku, Mai jin ƙai.",
            "numberInSurah": 9,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5085,
            "text": "Kuma me ya sãme ku, bã zã ku ciyar ba dõmin ɗaukaka kalmar Allah, alhãli kuwa gãdon sammai da ƙasã na Allah ne? Wanda ya ciyar a gabãnin cin nasara, kuma ya yi yãƙi daga cikinku, bã ya zama daidai (da wanda bai yi haka ba). waɗancan ne mafĩfĩta girman daraja bisa ga waɗanda suka ciyar daga bãya kuma suka yi yãƙi. Kuma dukansu Allah Ya yi musu wa'adi da (sakamako) mai kyau, kuma Allah Masani ne ga abin da kuke aikatãwa.",
            "numberInSurah": 10,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5086,
            "text": "Wãne ne wanda zai ranta wa Allah rance mai kyau dõmin Allah Ya ninka masa shi (a dũniya) kuma Yanã da wani sakamako na karimci (a Lãhira)?",
            "numberInSurah": 11,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5087,
            "text": "Rãnar da zã ka ga mũminai maza da mũminai mãtã haskensu nã tafiya a gaba gare su, da kuma dãma gare su. (Anã ce musu) 'Bushãrarku a yau, ita ce gidajen Aljanna.' Ruwa na gudãna daga ƙarƙashinsu sunã mãsu dawwama a cikinsu. Wannan, shĩ ne babban rabo mai girma.",
            "numberInSurah": 12,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5088,
            "text": "Rãnar da munafikai maza da munãfikai mãtã ke cẽwa waɗanda suka yi ĩmãni: 'Ku dũbẽ mu, mu yi makãmashi daga haskenku!' A ce (musu): 'Ku kõma a bãyanku, dõmin ku nẽmo wani haske.' Sai a danne a tsakãninsu da wani gãru yanã da wani ƙyaure, a cikinsa nan rahama take, kuma a bãyansa daga wajensa azaba take.",
            "numberInSurah": 13,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5089,
            "text": "Sunã kiran su, 'Ashe, bã tãre muke da ku ba?' Su ce: 'Ĩ, amma kũ, kun fitini kanku, kuma kun yi jiran wata masĩfa, kuma kunyi shakka, kuma waɗansu gũrace-gũrace sun rũɗe ku, har umurnin Allah ya jẽ muku, kumamarũɗi ya rũɗe ku game da Allah.'",
            "numberInSurah": 14,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5090,
            "text": "'To, a yau bã zã a karɓi fansa daga gare ku ba. Kuma bã zã a karɓa daga waɗanda suka kãfirta ba. Makõmarku wutã ce, ita ce mai dacẽwa da ku, kuma makõmarku ɗin nan tã mũnana.'",
            "numberInSurah": 15,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5091,
            "text": "Shin, lõkaci bai yi ba ga wa ɗanda suka yi ĩmãnĩ zukãtansu su yi tawãlu'i ga ambaton Allah da abin da ya sauka daga gaskiya? Kada su kasance kamar waɗanda aka bai wa littãfi a gabãnin haka, sai zãmani ya yi tsawo a kansu, sabõda haka zukãtansu suka ƙẽƙashe, kuma masu yawa daga cikinsu fãsiƙai ne.",
            "numberInSurah": 16,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5092,
            "text": "Ku sani cẽwa Allah Yanã rãyar da ƙasã a bãyan mutuwarta. Lalle Mun bayyanamuku ãyõyi da fatan zã ku yi hankali.",
            "numberInSurah": 17,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5093,
            "text": "Lalle mãsu gaskatãwa maza da mãsu gaskatãwa mãtã, kuma suka ranta wa Allah rance mai kyau, anã riɓanya musu, kuma suna da wani sakamako na karimci.",
            "numberInSurah": 18,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5094,
            "text": "Kuma waɗanda suka yi ĩmãni da Allah da MazanninSa waɗannan sũ ne kamãlar gaskatãwa kuma su ne mãsu shahãda awurin Ubangjinsu. sunã da sakamakonsu da haskensu. waɗanda suka kafirta kuma suka ƙaryata game da ãyõyinMu, waɗannan su ne'yan Jahĩm.",
            "numberInSurah": 19,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5095,
            "text": "Ku sani cẽwa rãyuwar dũniya wãsã ɗai ce da shagala da ƙawa da alafahri a tsakãninku da gãsar wadãta ta dũkiya da ɗiya, kamar misãlin shũka wadda yabanyarta ta bãyar da sha'awa ga manõma, sa'an nan ta ƙẽƙashe, har ka ganta tã zama rawaya sa'an nan ta kõma rauno. Kuma, alãmra akwai azãba mai tsanani da gãfara daga Allah da yarda. Kuma rãyuwar dũniya ba ta zama ba fãce ɗan jin dãɗin rũɗi kawai.",
            "numberInSurah": 20,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5096,
            "text": "Ku yi tsẽre zuwa ga (nẽman) gãfara daga Ubangjinku, da Aljanna fãɗinta kamar fãɗin samã da ƙasã ne, an yi tattalinta dõmin waɗanda suka yi ĩmãni da Allah da ManzanninSa. Wannan falalar Allah ce, Yanã bãyar da ita ga wanda Ya so. Kuma Allah Mai falala ne Mai girma.",
            "numberInSurah": 21,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5097,
            "text": "Wata masifa bã zã ta auku ba a cikin ƙasã kõ a cikin rayukanku fãce tanã a cikin littãfi a gabãnin Mu halitta ta. Lalle wannan, ga Allah, mai sauƙi ne.",
            "numberInSurah": 22,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5098,
            "text": "Dõmin kada ku yi baƙin ciki a kan abin da ya kuɓuce muku, kuma kada ku yi mumar alfahari da abin da Ya bã ku. Kuma Allah bã Ya son dukkan mai tãƙama, mai alfahari.",
            "numberInSurah": 23,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5099,
            "text": "Watau, waɗanda ke yin rõwa, kuma sunã umumin mutãne da yin rõwa. Kuma wanda ya jũya bãya, to, lalle Allah, Shĩ ne kaɗai Mawadãci, Gõdadde.",
            "numberInSurah": 24,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5100,
            "text": "Haƙĩƙa, lalle Mun aiko ManzanninMu da hujjõji bayyanannu, kuma Muka saukar da Littãfi da sikẽli tãre da su dõmin mutãne su tsayu da ãdalci, kuma Mun saukar da baƙin ƙarfe, a cikinsa akwai cutarwa mai tsanani da amfãni ga mutãne, kuma dõmin Allah Ya san mai taimakonSa da ManzanninSa a fake. Lalle ne Allah Mai ƙarfi ne, Mabuwãyi.",
            "numberInSurah": 25,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5101,
            "text": "Kuma haƙĩƙa, lalle, Mun aiki Nũhu da Ibrahĩm, kuma Muka sanya Annabci da Littĩfi a cikin zuriyarsu; daga cikinsu akwai mai nẽman shiryuwa, kuma mãsu yawa daga cikinsu fasiƙai ne.",
            "numberInSurah": 26,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5102,
            "text": "Sa'an nan Muka biyar a bãyansu da ManzanninMu; kuma Muka biyar da ĩsã ɗan Maryama, kuma Muka ba shi Linjila kuma Muka sanya tausayi da rahama a cikin zukãtan waɗanda suka bĩ shi, da ruhbãnanci wanda suka ƙãga, shi, ba Mu rubũta shi ba a kansu, fãce dai (Mun rubũta musu nẽman yardar Allah, sa'an nan ba su tsare shi haƙƙin tsarẽwarsa ba, sabõda haka Muka bai wa waɗanda suka yi ĩmãni daga cikinsu sakamakonsu kuma mãsu yawa daga cikinsu fãsiƙai ne.",
            "numberInSurah": 27,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5103,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Ku bi Allah da taƙawa, ku yi ĩmãni da ManzonSa Ya bã ku rabo biyu daga rahamarSa, kuma Ya sanya muku wani haske wanda kuke yin tafiya da shi, kuma Ya gãfarta muku. Kuma Allah Mai gãfara ne, Mai rahama.",
            "numberInSurah": 28,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5104,
            "text": "Dõmin kada mutãnen Littãfi su san cẽwa lalle bã su da ikon yi ga kõme na falalar Allah, kuma ita falalar ga hannun Allah kawai take, Yanã bãyar da ita ga wanda Ya so (wannan jãhilci yã hana su ĩmani). Kuma Al1ah Mai falala ne mai girma.",
            "numberInSurah": 29,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          }
        ]
      },
      {
        "number": 58,
        "name": "سُورَةُ المُجَادلَةِ",
        "englishName": "Al-Mujaadila",
        "englishNameTranslation": "The Pleading Woman",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5105,
            "text": "Lalle Alla Ya ji maganar wadda ke yi maka jãyayya game da mijinta, tanã kai ƙãra ga Allah, kuma Allah nã jin muhawararku. Lalle Allah Mai ji ne, Mai gani.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5106,
            "text": "Waɗanda ke yin zihãri daga cikinku game da mãtansu, sũmãtan nan bã uwãyensu ba ne, bãbu uwayensu fãce waɗanda suka haife su. Lalle sũ, sunã faɗar abin ƙyãmã na magana da ƙarya, kuma lalle Allah haƙĩƙa Mai yãfẽwa ne, Mai gãfara.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5107,
            "text": "Waɗanda ke yin zihãri game da mãtansu, sa'an nan su kõma wa abin da suka faɗa, to, akwai 'yanta wuya a gabãnin su shãfi jũna. Wannan anã yi muku wa'azi da shi. Kuma Allah Mai ƙididdigewa ne ga abin da kuke aikatãwa.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5108,
            "text": "To, wanda bai sãmu ba, sai azumin wata biyu jẽre a gabãnin su shãfi jũna, sa'an nan wanda bai sãmi ĩkon yi ba, to, sai ciyar da miskĩnai sittin. Wannan dõmin ku yĩ ĩmãni da Allah da Manzonsa. Kuma waɗannan hukunce-hukunce haddõdin Allah ne. Kuma kãfirai, sunã da azãba mai raɗadi.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5109,
            "text": "Lalle waɗanda ke sãɓã wa Allah da ManzonSa, an wulãkanta su kamar yadda aka wulãkantar da waɗanda ke a gabãninsu, kuma lalle Mun saukar da ãyõyi bayyanannu, kuma kãfirai nã da azãba mai wulãkantãwa.",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5110,
            "text": "Rãnar da Allah zai tãyar da su gabã ɗaya, sa'an nan Ya bã su lãbãri game da abin da suka aikata, Allah Yã lissafa shi, alhãli kuwa sũ, sun manta da shi, kuma a kan kõme Allah Halartacce ne.",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5111,
            "text": "Ashe, ba ka ga cẽwa lalle Allah Yanã sane da abin da yake a cikin sammai da abin da ke cikin ƙasa ba? Wata gãnãwa ta mutum uku bã zã ta kasance ba fãce Allah Shĩ ne na huɗu ɗinta, kuma bãbu ta mutum biyar fãce Shĩ ne na shida ɗinta, kuma babu abin da ya kãsa wannan kuma babu abin da yake mafi yawa fãce Shĩ Yanã tãre da su duk inda suka kasance, sa'an nan Ya bã su lãbãri game da abin da suka aikata a Rãnar ¡iyãma. Lalle Allah Masani ne ga dukkan kõme.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5112,
            "text": "Ashe, ba ka ga waɗanda aka hane su ba daga ganãwar, sa'an nan sunã kõmã wa abin da aka hane su daga gare shi, kuma sunã gãnãwa game da zunubi da zãlunci da sãɓã wa Manzon Allah, kuma idan sun zo maka sai su gasihe ka da abin da Allah bai gaishe ka da shi ba, kuma sunã cẽwa a cikin zukatansu: 'Don me Allah ba Ya yi mana azãba sabõda abin da muke faɗi?' Jahannama ita ce mai isarsu, zã su shigeta. Sabõda haka makõmarsu ta mũnana.",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5113,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Idan za ku yi gãnãwa, to, kada ku gãna game da zunubi da zãlunci da sãɓã wa Manzon Allah, kuma ku yi gãnãwa game da alhẽri da taƙawa. Ku bi Allah da taƙawa wanda zã a tattara ku zuwa gare, shi.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5114,
            "text": "Gãnãwar daga Shaiɗan take kawai dõmin ya mũnanã wa waɗanda suka yi ĩmãni, alhãli kuwa bã zai iya cũtar su da kõme ba fãce da iznin Allah. Kuma sai mũminai su dogara ga Allah.",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5115,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Idan an ce muka, 'Ku yalwatã a cikin majalisai,' to, ku yalwatã, sai Allah Ya yalwatã muku, kuma idan an ce muku, 'Ku tãshi' to, ku tãshi. Allah nã ɗaukaka waɗanda suka yi ĩmãni daga cikinku da waɗanda aka bai wa ilim wasu darajõji mãsu yawa, kuma, Allah Mai ƙididdigewa ne ga dukan kõme.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5116,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Idan zã ku gãnãwa da Manzon Allah, to, ku gabãtar da 'yar sadaka a gabãnin gãnãwarku, wannan ne mafi alhẽri a gare ku, kuma mafi tsarki. Sai idan ba ku sãmi (abin sadakar ba), to, lalle, Allah, Mai gãfara ne, Mai jin ƙai.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5117,
            "text": "Ashe, kun ji tsõron ku gabãtar da sadakõki a gabãnin gãnãwarku? To, idan ba ku aikata ba, kuma Allah Ya kõmo da ku zuwa ga sauƙi, sai ku tsai da salla, kuma ku bãyar da zakka, kuma ku yi ɗã'ã ga Allah da ManzonSa. Kuma Allah ne Mai ƙididdigewa ga abin da kuke aikatãwa.",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5118,
            "text": "Ashe, baka ga waɗanda suka jiɓinci waɗansu mutãne da Allah Ya yi hushi a kansu ba, bã su cikinku, kuma bã su a cikinsu kuma sunã rantsuwa a kan ƙarya, alhãli kuwa sunã sane?",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5119,
            "text": "Allah Ya yi musu tattalin azãba mai tsanani. Lalle sũ abin da suka kasance sunã aikatãwa ya mũnana.",
            "numberInSurah": 15,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5120,
            "text": "Sun riƙi rantsuwõwinsu garkuwa, sabõda haka suka kange (mũminai) daga jihãdin ɗaukaka tafarkin Allah. To, sunã da azãba mai wulãkantãwa.",
            "numberInSurah": 16,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5121,
            "text": "Dũkiyõyinsu ba su wadãtar musu kõme ba daga Allah, haka kuma ɗiyansu. Waɗannan 'yan wutã ne. Sũ, mãsu dawwama ne a cikinta.",
            "numberInSurah": 17,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5122,
            "text": "Rãnar da Allah ke tãyar da su gabã ɗaya, sai su yi Masa rantsuwa kamar yadda suke yi muku rantsuwa (a nan dũniya) kuma sunã zaton cẽwa sũ a kan wani abu suke! To, lalle sũ, sũ ne maƙaryata.",
            "numberInSurah": 18,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5123,
            "text": "Shaiɗan ya rinjaya a kansu, sai ya mantar da su ambaton Allah, waɗannan ƙungiyar Shaiɗan ne. To, lalle ƙungiyar Shaiɗan, sũ ne mãsu hasãra.",
            "numberInSurah": 19,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5124,
            "text": "Lalle, waɗanda ke sãɓã wa Allah da ManzonSa waɗannan sunã a cikin (mutãne) mafi ƙasƙanci.",
            "numberInSurah": 20,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5125,
            "text": "Allah Ya rubũta cẽwa,' Lalle zan rinjãya, Nĩ da ManzanniNa.' Lalle Allah Mai ƙarfi ne, Mabuwãyi.",
            "numberInSurah": 21,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5126,
            "text": "Bã zã ka sãmi mutãne mãsu yin ĩmãni da Allah da RãnarLãhira sunã sõyayya da wanda ya sãɓã wa Allah da ManzonSa ba, kõ dã sun kasance ubanninsu ne, kõ ɗiyansu kõ 'yan'uwansu, ko danginsu. waɗannan Allah Yã rubuta ĩmãni a cikin zukãtansu, kuma Yã ƙarfafa su da wani rũhi daga gare Shi, kuma zai shigar da su a gidãjen Aljanna, ƙoramu na gudãna ƙarƙashinsu sunã mãsu dawwama a cikinsu. Allah Yã yarda da su, kuma sun yarda da Shi. Waɗannan ƙungiyar Allah ne. To, lalle ƙungiyar Allah sũ ne mãsu babban rabo.",
            "numberInSurah": 22,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          }
        ]
      },
      {
        "number": 59,
        "name": "سُورَةُ الحَشۡرِ",
        "englishName": "Al-Hashr",
        "englishNameTranslation": "The Exile",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5127,
            "text": "Abin da ke cikin sammai da abin da ke cikin ƙasã ya yi tasbihi ga Allah, alhãli kuwa, Shĩ ne Mabuwãyi, Mai hikima.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5128,
            "text": "Shĩ ne wanda Ya fitar da waɗanda suka kãfirta daga Mazõwa Littãfi, daga gidãjẽnsu da kõra ta farko. Ba ku yi zaton sunã fita ba, kuma sun tabbata cẽwa gãnuwõwinsu mãsu tsare su ne daga Allah, sai Allah Ya jẽ musu daga wajen da ba su yi zato ba, kuma Ya jẽfa tsõro a cikin zukãtansu, sunã rushe gidãjensu da hannãyensu da kuma hannãyen mũminai. To, ku lũra fa, ya mãsu basĩrõri.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5129,
            "text": "Kuma ba dõmin Allah Ya rubuta musu kõrar ba, dã Ya azabta su a cikin dũniya, kuma a Lãhira sunã da azãbar wutã.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5130,
            "text": "Wannan dõmin lalle su, sun sãɓa wa Allah, da ManzonSa alhãli kuwa wanda ya sãɓa wa Allah, to, lalle Allah, Mai tsananin uƙũba ne.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5131,
            "text": "Abin da kuka sãre na dabĩniya, kõ kuka bar ta tsaye a kan asallanta, to da iznin Allah ne, kuma dõmin Ya wulãkanta fasiƙai,",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5132,
            "text": "Kuma abin da Allah Ya sanya ya zama ganĩma ga ManzonSa, daga gare su, to, ba ku yi hari a kansa da dawãki ko rãƙuma ba amma Allah ne Ya rinjãyar da ManzanninSa a kan wanda Yake so, kuma Allah Mai ĩkon yi ne a kan kõme.",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5133,
            "text": "Abin da Allah Ya sanya shi ganĩma ga ManzonSa dagamutãnen ƙauyukan nan, to, na Allah ne, kuma na ManzonSa ne kuma na mãsu dangantaka da mãrayu da miskĩnai da ɗan hanya (matafiyi) ne dõmin kada ya kasance abin shãwãgi a tsakãnin mawadãta daga cikinku kuma abin da Manzo ya bã ku, to, ku kama shi, kuma abin da ya hane ku, to, ku bar shi. Kuma ku bi Allah da taƙawa. Lalle, Allah, Mai tsananin uƙũba ne.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5134,
            "text": "(Ku yi mãmãki) Ga matalauta mãsu hijira waɗanda aka fitar daga gidãjẽnsu da dũkiyõyinsu, sunã nẽman falala daga Allah da kuma yarda, kuma sunã taimakon Allah da ManzonSa! Waɗannan sũ ne mãsu gaskiya.",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5135,
            "text": "Da waɗanda suka zaunar da gidãjensu (ga Musulunci) kuma (suka zãɓi) ĩmãni, a gabãnin zuwansu, sunã son wanda ya yi hijira zuwa gare su, kuma bã su tunãnin wata bukãta a cikin ƙirãzansu daga abin da aka bai wa muhãjirĩna, kuma sunã fĩfĩta waɗansu a kan kãwunansu, kuma ko dã sunã da wata larũra. Wanda ya sãɓã wa rõwar ransa, to, waɗannan sũ ne mãsu babban rabo.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5136,
            "text": "Kuma waɗanda suka zõ daga bãyansu, sunã cẽwa, 'Yã Ubangijinmu! Ka yi gãfara a gare mu, kuma ga 'yan'uwanmu, waɗanda da suka riga mu yin ĩmãni, kada Ka sanya wani ƙulli a cikin zukãtanmu ga waɗanda suka yi ĩmãni. Yã Ubangijinmu! Lalle Kai ne Mai tausayi, Mai jin ƙai.",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5137,
            "text": "Ashe, ba ka ga waɗanda suka yi munãfinci ba, sunã cẽwa ga 'yan'uwansu, waɗanda suka kãfirta daga Mazõwa Littafi, 'Lalle idan an fitar da ku, lalle zã mu fita tãre da ku, kuma bã zã mu yi ɗã'a ga kõwa ba game da ku, har abada, kuma lalle idan an yãƙe ku, lalle zã mu taimake ku haƙĩƙatan?' Alhãli kuwa Allah na shaidar cẽwa lalle sũ tabbas, maƙaryata ne.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5138,
            "text": "Lalle idan an fitar da su, bã zã su fita tãre da su ba kuma lalle idan an yãƙe su bã zã su taimake su ba, kuma lalle idan sun taimake su lalle ne, haƙĩƙatan, zã su jũyar da bãyansu dõmin gudu, sa'an nan kuma bã zã a taimake su ba.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5139,
            "text": "Lalle kũ ne kuka fi bãyar da firgita a cikin zukãtansu bisa ga Allah, wannan kuwa dõmin sũ lalle waɗansu irin mutãne ne da bã su gãnẽwa.",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5140,
            "text": "Bã su iya yãƙar ku gabã ɗaya, fãce a cikin garũruwa mãsu gãnuwa da gãruna, kõ kuma daga bãyan katangu. Yãkinsu a tsãkaninsu mai tsanani ne, kanã zaton su a haɗe, alhãli kuwa zukãtansu dabam- dabam suke. Wannan kuwa dõmin sũ, lalle wasu irin mutane ne da bã su hankalta.",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5141,
            "text": "Kamar misãlin waɗanda ke a gabãninsu, bã da daɗẽwa ba, sun ɗanɗani kũɗar al'amarinsu, kuma sunã da wata azãba mai raRaɗi.",
            "numberInSurah": 15,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5142,
            "text": "Kamar misãlin Shaiɗan a lõkacin da yake cẽ wa mutum, 'Ka kãfirta,' To, a lõkacin da ya kãfirta ɗin, ya ce (masa): 'Lalle bãbu ruwãna da kai. Lalle ni inã tsõron Allah Ubangijin halitta!'",
            "numberInSurah": 16,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5143,
            "text": "Sai ãƙibarsu ta kasance cẽwa su biyun duka sunã a cikin wutã, sunã mãsu dawwama a cikinta. 'Kuma wannan shĩ ne sakamakon mãsu zãlunci.'",
            "numberInSurah": 17,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5144,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Ku bi Allah da taƙawa kuma rai ya dũbi abin da ya gabãtar dõmin gõbe, kuma ku bi Allah da taƙawa. lalle Allah, Mai ƙididdigewa ne ga abin da kuke aikatãwa.",
            "numberInSurah": 18,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5145,
            "text": "Kuma kada ku kasance kamar waɗanda suka manta Allah shi kuma Ya mantar da su rãyu, kansu. Waɗannan sũ ne fãsiƙai.",
            "numberInSurah": 19,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5146,
            "text": "'Yan Wutã da 'yan Aljanna bã su daidaita. 'Yan Aljanna, sũ ne mãsu babban rabo.",
            "numberInSurah": 20,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5147,
            "text": "Dã Mun saukar da wannan Alƙur'ani a kan dũtse, dã lalle kã ga dũtsen yanã mai tawãli'u, mai tsattsãgẽwa sabõda tsõron Allah, kuma waɗancan misãlai Munã bayyana su ne ga mutãne, da fatan za su yi tunãni.",
            "numberInSurah": 21,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5148,
            "text": "(Wanda Ya saukar da Alƙur'ani) Shi ne Allah, wanda bãbu wani abin bautãwa fãce Shi Masanin fake da bayyane, Shĩ ne Mai rahama, Mai jin ƙai.",
            "numberInSurah": 22,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5149,
            "text": "Shĩ ne Allah, wanda bãbu abin bautãwa fãce shi, Mai mulki, Mai tsarki, Aminci, Mai amintarwa, Mai Tsarẽwa, Mabuwãyi, Mai t஛astãwa Mai nuna isa, tsarki ya tabbata a gare Shi daga abin da suke yi na shirki da Shi.",
            "numberInSurah": 23,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5150,
            "text": "Shĩ ne Allah, Mai halitta, Mai ginãwa, Mai sũrantãwa. Yanã da sũnãye mãsu kyau, abin da ke a cikin sammai da ƙasa sunã tsarkake Shi, kuma Shĩ ne Mabuwãyi, Mai hikima.",
            "numberInSurah": 24,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          }
        ]
      },
      {
        "number": 60,
        "name": "سُورَةُ المُمۡتَحنَةِ",
        "englishName": "Al-Mumtahana",
        "englishNameTranslation": "She that is to be examined",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5151,
            "text": "Ya kũ waɗanda suka yi ĩmani! Kada ku riƙi maƙiyĩNa kuma maƙiyinku masõyi, 845. Kuna jẽfa sõyayya zuwa gare su, alhãli kuwa haƙĩƙa sun kãfirta da abin da ya zo muku, na gaskiya, sunã fitar da Manzon Allah tãre da ku (daga gidãjenku) dõmin kun yi ĩmãni da Allah, Ubangijinku, idan kun fito dõmin jihãdi sabõda ɗaukaka kalmaTa da nẽman yardaTa, kunã asirta sõyayya zuwa gare su alhãli kuwa Nĩ ne Mafi sani ga abin da kuka ɓõye da abin da kuka bayyana, kuma duk wanda ya aikata shi daga cikinku, to, lalle ya ɓace daga tsakar hanya.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5152,
            "text": "Idan sun kãma ku, zã su kasance maƙiya a gare ku, kuma su shimfiɗa hannuwansu da harsunan gũrin ku kãfirta.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5153,
            "text": "Zumuntarku bã zã ta amfãne ku ba, haka kuma ɗiyanku, a Rãnar Kiyãma. (Allah) zai rarrabe tsakãninku, kuma Allah Mai gani ne ga abin da kuke aikatãwa.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5154,
            "text": "Lalle abin kõyi mai kyau ya kasance a gare ku game da Ibrãhĩm da waɗanda ke tãre da shi a lõkacin da suka ce wa mutnensu, 'Lalle mũ bãbu ruwanmu daku, kuma da abin da kuke bautãwa, baicin Allah, mun fita batunku, kuma ƙiyayya da jiyẽwa jũna sun bayyana a tsakãninmu, sai kun yi ĩmãni da Allah Shi kaɗai.' Fãce maganar Ibrãhĩm ga ubansa (da yace), 'Lalle za ni nẽma maka gãfara kuma ban mallaki kõme ba daga Allah sabõda kai.' 'Yã Ubangijinmu! A gare Ka muka dõgara, kuma gare Ka muka mayar da al' amuranmu, kuma zuwa gare Ka makõma take.'",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5155,
            "text": "'Yã Ubangijinmu! Kada Ka sanya mu fitina ga waɗanda suka kãfirta, kuma Ka yi gãfara gare Mu. Ya Ubangijinmu! Kai ne Mabuwãyi, Mai hikima!",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5156,
            "text": "Lalle, haƙĩƙa, abin kõyi mai kyau ya kasance a gare ku daga cikinsu, ga wanda ya kasance yanã fãtan (rahamar) Allah da Rãnar Lãhira, kuma wanda ya jũya bãya, to, lalle Allah, Shĩ ne wadãtacce, Gõdadde.",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5157,
            "text": "Anã tsammãnin Allah Ya sanya, a tsakãninku da tsakãnin waɗanda kuka yi ƙiyayya da su, wata sõyaya daga gare su, kuma Allah Mai ĩkon yi ne, kuma Allah Mai gãfara ne, Mai jin ƙai.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5158,
            "text": "Allah bã Ya hana ku, daga waɗanda ba su yãƙe ku ba sabõda addini kuma ba su fitar da ku ba daga gidãjenku, ga ku kyautata musu kuma ku yi musu ãdalci. Lalle Allah Yanã son mãsu ãdalci.",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5159,
            "text": "Allah Yanã hana ku kawai daga waɗanda suka yãke ku sabõda addini kuma suka fitar da ku daga gidãjenku, kuma suka taimaki jũna ga fitar da ku, karku jiɓince su, kuma wanda ya jiɓince su, to waɗannan sũ ne azzãlumai.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5160,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Idan mãtã mũminai suka zo muku, sunã mãsu hijira, to, ku jarraba su. Allah Shĩ ne mafi sani ga ĩmãninsu. To, idan kun san sũ mũminai ne, kada ku mayar da su zuwa ga kãfirai. Sũ mãtan bã su halatta ga aurensu, sũ kuma kãfiran bã su halatta ga auren mãtan. Ku bã su abin da suka ɓatar na dũkiya. Kuma bãbu laifi a kanku ga ku aure su idan kun bã su sadãkõkinsu. Kuma kada ku riƙe auren mãtã kãfirai, kuma ku tambayi abin da kuka ɓatar daga dũkiya, su kuma kãfirai su tambayi abin da suka ɓatar na dũkiya. Wannan hukuncin Allah ne Yanã hukunci a tsakãninku. kuma Allah, Masani ne, Mai hikima",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5161,
            "text": "Kuma idan wani abu ya kuɓuce muku daga mãtanku zuwa ga kãfirai sa'an nan kuma kuka ɗauki fansa, to, sai ku bai wa waɗanda matansu suka tafi kwatankwacin abin da suka ɓatar na dũkiya. Kuma ku yi biyayya da taƙawa ga Allah wanda kuke mãsu ĩmãni da Shi, Shi Kaɗai.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5162,
            "text": "Yã kai Annabi! Idan mãtã mũminai suka zo maka sunã yi maka mubãya'a a kan bã zã su yi shirki da Allah ba ga kõme, kuma bã su yin sãtã, kuma bã su yin zina, kuma bã su kashe, ya'yansu, kuma bã su zuwa da ƙarya da suke ƙirƙirãwa a tsãkanin hannuwansu da ƙafãfunsu kuma bã su sãɓa maka ga wani abu da aka sani na sharĩ'a, to, ka karɓi mubãya'arsu, kuma ka nẽmi Allah Ya gãfarta musu. Lalle Allah Mai gãfara ne, Mai jin ƙai.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5163,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Kada ku jiɓinci waɗansu mutãne da Allah Ya yi hushi a kansu. Lalle sun yanke tsammãni daga (rahamar) Lãhira, kamar yadda kãfirai suka yanke tsammãni daga mazõwa kaburbura.",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          }
        ]
      },
      {
        "number": 61,
        "name": "سُورَةُ الصَّفِّ",
        "englishName": "As-Saff",
        "englishNameTranslation": "The Ranks",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5164,
            "text": "Abin da ke cikin sammai da abin da ke cikin ƙasã ya yi tasbihi ga Allah alhãli kuwa, Shi ne Mabuwayi, Mai hikima.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5165,
            "text": "Ya kũ waɗanda suka yi ĩmani! Don me kuke faɗin abin dã ba ku aikatãwa?",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5166,
            "text": "Ya girma ga zama abin ƙyãma awurin Allah, ku faɗi abin da bã ku aikatãwa.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5167,
            "text": "Lalle Allah Yanã son waɗanda ke yin yãƙi dõmin ɗaukaka KalmarSa, a cikin safu kamar sũ gini ne mai ɗamfarar jũna.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5168,
            "text": "Kuma a lõkacin da Mũsã ya ce wa mutãnensa: 'Yã mutãnena! sabõda me kuke cũtar da ni, alhãli kuwa lalle kun sani cẽwa lalle nĩ Manzon Al1ah ne zuwa gare ku?' To, a lõkacin da suka karkace, Allah Ya karkatar da zukãtansu. Kuma Allah bã Ya shiryar da mutãne fasiƙai.",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5169,
            "text": "Kuma a lõkacin da Ĩsã ɗan Maryayama ya ce: 'Yã BanĩIsrã'ĩla! Lalle nĩ Manzon Allah ne zuwa gare ku, mai gaskata abin da ke gaba gare ni na Attaura, kuma mai bãyar da bushãra da wani Manzo da ke zuwa a bãyãna, SũnasaAhmad (Mashãyabo).' To, a lõkacin da ya jẽ musu da hujjõji, suka ce: 'Wannan sihiri ne, bayyananne.'",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5170,
            "text": "Kuma wãne ne mafi zãlunci daga wanda ya ƙirƙira ƙarya ya Jingina ta ga Allah, alhãli kuwa shĩ, anã kiran sa zuwa ga Musulunci? Kuma Allah bã Ya shiryar da mutãne azzãlumai.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5171,
            "text": "Sunã nufin su bice hasken Allah da bãkunansu alhãli kuwa Allah Mai kammala haskenSa ne, kuma kõ da kãfirai sun ƙi.",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5172,
            "text": "Shi ne wanda Ya aiko ManzonSa da shiriya da addĩnin gakiya, dõmin Ya ɗaukaka shi a kan wani addĩni dukansa kuma kõ dã mushirikai sun ƙi.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5173,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Shin, in nũna muku wani fatauci wanda zai tsarshe ku daga wata azãba mai raɗaɗi?",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5174,
            "text": "Ku yi ĩmani da Allah da ManzonSa, kuma ku yi jihãdi ga ɗaukaka kalmar Allah game da dũkiyõyinku da rãyukanku. Wannan shĩ ne alhẽri a gare ku idan kun kasance kuna da sani.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5175,
            "text": "Sai Allah Ya gãfarta muku zunubbanku, kuma Ya shigar da ku gidãjen Aljanna, ƙoramu na gudãna daga ƙarƙashinsu, da waɗansu ɗãkuna mãsu dãɗi a cikin gidãjen Aljannar zama. Wannan shĩ ne babban rabo, mai girma.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5176,
            "text": "Da wata (falala) da yake kunã son ta; taimako daga Allah da cin nasara wanda yake kusa. Kuma ka yi bushãra ga mũminai.",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5177,
            "text": "Yã kũ waɗanda suka yi ĩmani! Ku kasance mataimakan Allah kamar abin da Ĩsã ɗanMaryama ya ce ga Hawãriyãwa, 'Waɗanne ne mataimakãna zuwa ga (aikin) Allah?' Sai wata ƙungiya daga Banĩ Isrã'ĩla ta yi ĩmãni, kuma wata ƙungiya ta kãfirta. Sai muka ƙarfafa waɗanda suka yi ĩmãni a kan maƙiyansu, sabõda haka suka wãyi gari marinjãya.",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          }
        ]
      },
      {
        "number": 62,
        "name": "سُورَةُ الجُمُعَةِ",
        "englishName": "Al-Jumu'a",
        "englishNameTranslation": "Friday",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5178,
            "text": "Abin da yake a cikin sammai da abin da yake a cikin kasã sunã tasbĩhi ga Allah, Mamallaki, Mai tsarki' Mabuwãyi, Mai hikima.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5179,
            "text": "Shĩ ne wanda Ya aika, a cikin mabiya al'ãdu, wani Manzo daga gare su yanã karanta ãyõyinSa a kansu, kuma yanã tsarkake su, kuma yanã sanar da su littafin da hikimarsa kõ da yake sun kasance daga gabãninsa lalle sunã a cikin ɓata bayyanãnna.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5180,
            "text": "Da waɗansu mutãne daga gare su, ba su i da riskuwa da sũ ba, alhãli kuwa shĩ ne Mabuwãyi, Mai hikima.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5181,
            "text": "Waccan wata falalar Allah ce Yanã bãyar da ita ga wanda Yake so. Kuma Allah ne Ma'abũcin dukan falala mai girma.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5182,
            "text": "Misãlin waɗanda aka ɗõra wa ɗaukar Attaura sa'an nan ba su ɗauke ta ba, kamar misãlin jãki ne Yanã ɗaukar littattafai. Tir da misãlin mutãnen nan da suka ƙaryatã game da ãyõyin Allah! Kuma Allah bã Yã shiryar da mutãnẽ azzãlumai.",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5183,
            "text": "Ka ce: 'Yã kũ waɗandasuka tũba (Yahũdu)! Idan kun riya cẽwa kũ ne zãɓaɓɓun Allah bã sauran mutanẽ ba, sai ku yi gũrin mutuwa idan kun kasance mãsu gaskiya.'",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5184,
            "text": "Kuma bã zã su yi gũrinta ba har abadã sabõda abin da hannãyen su suka gabãtar. Kuma Allah ne masani ga azzãlumai",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5185,
            "text": "Ka ce 'Lalle mutuwar nan da kuke gudũ daga gareta, to, lalle ita mai haɗuwa da ku ce sa'an nan kuma anã mayar da kũ zuwa ga masanin fake da bayyane, dõmin Ya bãku lãbãri game da abin da kuka kasance kunã aikatãwa.'",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5186,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Idan anyi kirã zuwã ga salla a rãnar Jumu'a, sai ku yi aiki zuwa ga ambaton Allah, kuma ku bar ciniki. Wancan ɗinku ne mafi alhẽri a gare ku idan kun kasance kunã sani.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 487,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5187,
            "text": "Sa'an nan idan an ƙarẽ salla, Sai ku wãtsu a cikin ƙasã kuma ku nẽma daga falalar Allah, kuma ku ambaci sũnan Allah da yawa ɗammãninku, ku sãmi babban rabo.",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 487,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5188,
            "text": "Kuma idan suka ga wani fatauci kõ kuma wani wasan shagala, sai su yi rũgũguwar fita zuwa gare su, kuma su bar ka kanã tsaye. Ka ce: 'Abin da yake a wurin Allah ne mafi alhẽri daga fataucin, alhãli kuwa Allah ne Mafi alhẽrinmãsu arzũtãwa.'",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 487,
            "hizbQuarter": 221,
            "sajda": false
          }
        ]
      },
      {
        "number": 63,
        "name": "سُورَةُ المُنَافِقُونَ",
        "englishName": "Al-Munaafiqoon",
        "englishNameTranslation": "The Hypocrites",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5189,
            "text": "Idan munãfukai suka jẽ maka suka ce: 'Munẽ shaidar lalle kai haƙĩƙa Manzon Allah ne,' Kuma Allah Yanã sane da lalle Kai, haƙĩƙa ManzonSa ne, kuma Allah Yanã shaida lalle munafukan haƙĩƙa, maƙaryata ne.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5190,
            "text": "Sun riƙi rantsuwõwinsu garkuwa, sai suka taushe daga tafarkin Allah. Lalle sũ abin da suka kasance sunã aikatãwa ya mũnana.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5191,
            "text": "Wancan, dõmin lalle sũ, sun yi ĩmãni, sa'an nan kuma suka kãfirta, sai aka yunƙe a kan zukãtansu. Sabõda sũ, bã su fahimta.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5192,
            "text": "Kuma idan kã gan su, sai jikunansu su bã ka sha'awa kuma idan sun faɗa, zã ka saurãra ga maganarsu. Kamar dai sũ ƙyami ne wanda aka jingine. Sunã zaton kõwace tsãwa a kansu take. Sũ ne maƙiyan, sai ka yi saunarsu. Allah Yã la'ane su. Yãya ake karkatar da su?",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5193,
            "text": "Kuma idan aka ce musu 'Ku zo Manzon Allah ya nẽma maku gãfara,' Sai su gyãɗa kãwunansu, kuma ka gan su sunã kangẽwa, alhãli kuwa sunã mãsu girman kai.",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5194,
            "text": "Daidai ne a kansu, kã nẽma musu gãfara kõ ba ka nẽmamusu ba. faufau Allah bã zai gãfarta musu ba. Lalle Allah, bã zai shiryar da mutãne fãsiƙai ba.",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5195,
            "text": "Sũ ne waɗanda ke cẽwa 'Kada ku ciyar a kan wanda ke wurin Manzon Allah har su wãtse,' alhali kuwa taskõkin sammai da ƙasa ga Allah suke kuma amma munãfukai bã su fahimta.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5196,
            "text": "Sunã cẽwa 'Lalle ne idan mun kõma zuwa Madĩnar, haƙĩƙa mafi rinjaya zai fitar da mafi ƙasƙanta daga gare ta, alhãli kuwa rinjãyar ga Allah take kuma da ManzonSa, kuma da mũminai, kuma amma munãfikai ba su sani ba.'",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5197,
            "text": "Ya ku waɗanda suka yi ĩmãni! Kada dũkiyõyinku da ɗiyanku su shagaltar da ku daga ambaton Allah. Kuma wanda ya yi haka, to, waɗannan sũ ne mãsu hasãra.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 489,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5198,
            "text": "Kuma ku ciyar daga abin da Muka azurta ku daga gãbanin mutuwa ta je wa ɗayanku har ya ce: 'Yã Ubangijina! Don me ba Ka yi mini jinkiri ba zuwa ga wani ajali makusanci dõmin in gaskata kuma in kasance daga sãlihai?'",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 489,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5199,
            "text": "Kuma Allah bã zai jinkirta wa wani rai ba idan ajalinsa ya je. Kuma Allah Masani ne ga abin da suke aikatãwa.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 489,
            "hizbQuarter": 222,
            "sajda": false
          }
        ]
      },
      {
        "number": 64,
        "name": "سُورَةُ التَّغَابُنِ",
        "englishName": "At-Taghaabun",
        "englishNameTranslation": "Mutual Disillusion",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5200,
            "text": "Abin da yake a cikin sammai da wanda yake a cikin ƙasã sunã tasbĩhi ga Allah. Gare Shi mulki yake, kuma gare Shi gõdiya take. Kuma Shi, a kan kõme, Mai ikon yi ne.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5201,
            "text": "Shĩ ne wanda Ya halitta ku. Sa'an nan daga gare ku akwai kãfiri kuma daga gare ku akwai mũmini. Kuma Allah Mai gani ne ga abin da kuke aikatãwa.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5202,
            "text": "Ya halitta sammai da ƙasã da abin da yake hakkinSa. Kuma Ya sũranta ku, sa'an nan Ya kyautata sũrõrinku. Kuma zuwa gare Shi makõma take.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5203,
            "text": "Yanã sanin abin da ke a cikin sammai da ƙasã. Kuma Yanã sanin abin da kuke bõyẽwa da abin da kuke bayyanãwa. Kuma Allah Masani ne ga abin da ke cikin ƙirãza.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5204,
            "text": "Shin, babban lãbãri bai je muku ba na waɗanda suka kãfirta daga gabãni, sai suka ɗanɗani uƙũbar al'amarinsu kuma sunã da wata azãba mai raɗaɗi?",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5205,
            "text": "Wancan sabõda lalle su Manzanninsu sun kasance sunã jẽ musu da hujjõji bayyanannu, sai suka ce: 'Ashe, wasu mutane za su shiryar da mũ?' Sai suka kãfirta kuma suka jũya bãya, kuma Allah Ya wadãtu (daga gare su). Kuma Allah wadãtacce ne, Gõdadde.",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5206,
            "text": "Waɗanda suka kãfirta sun riya cẽwa bã zã a tãyar da su ba. Ka ce: 'Ni, inã rantsuwa da Ubangijĩna. Lalle zã a tãyar da ku haƙĩƙatan, sa'an nan kuma lalle anã bã ku lãbãri game da abin da kuka aikata. Kuma wannan ga Allah mai sauƙi ne.'",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5207,
            "text": "Sabõda haka ku yi ĩmani da Allah da ManzonSa da hasken nan da Muka saukar. Kuma Allah, ga abin da kuke aikatãwa, Mai labartawa ne.",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5208,
            "text": "A rãnar da Yake tattara ku dõmin rãnar tãruwa. Wancan ne rãnar kãmunga. Kuma wanda ya yi ĩmãni da Allah, kuma ya aikata aikin ƙwarai, zai kankare masa mũnãnan ayyukansa, kuma Yashigar da shi gidãjen Aljanna, ƙoramu na gudãna daga ƙarƙashinsu, sunã madawwama a cikinsu har abada. Wannan ne babban rabo mai girma.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5209,
            "text": "Kuma waɗanda suka kãfirta, kuma suka ƙaryata game da ayõyinMu, waɗannan sũne'yan wuta, sunã madawwama a cikinta. Kuma tir da makõma, ita.",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5210,
            "text": "Wata masĩfa bã zã ta sãmu ba fãce da iznin Allah. Kuma wanda ya yi ĩmãni da Allah, Allah zai shiryar da zuciyarsa. Kuma Allah, ga dukan kõme, Masani ne.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5211,
            "text": "Kuma ku yi ɗã'a ga Allah, kuma ku yi ɗã'a ga Manzo. Sa'an nan idan kun jũya bãya, to, abin sani kawai, akwai iyarwa bayyananna a kan ManzonMu.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5212,
            "text": "Allah bãbu wani abin bauta wa fãce Shi. Kuma ga Allah, sai mũminai su dõgara.",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5213,
            "text": "Yã kũ waɗanda suka yi ĩmani! Lalle ne daga mãtanku da ɗiyanku akwai wani maƙiyi a gare ku, sai ku yi saunarsu. Kuma idan kuka yãfe, kuma kuka kau da kai, kuma kuka gãfarta, to, lalle Allah Mai gãfara ne, Mai jin ƙai.",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5214,
            "text": "Dũkiyõyinku da ɗiyanku fitina ɗai ne. Kuma Allah, a wurinSa akwai wani sakamako mai girma.",
            "numberInSurah": 15,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5215,
            "text": "Sai ku bi Allah da taƙawa gwargwadon abin da kuka sãmi ĩko. Kuma ku saurãra kuma ku yi ɗã'ã, kuma ku ciyar, ya fi zama alhẽri gare ku. Kuma wandaya sãɓã wa rõwar ransa, to, waɗannan sũ ne mãsu babban rabo.",
            "numberInSurah": 16,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5216,
            "text": "Idan kun bai wa Allah rance, rance mai kyau, (Allah) zai ninka shi a gare ku kuma Ya gãfarta muku. Kuma Allah Mai yawan gõdiya ne, Mai haƙuri.",
            "numberInSurah": 17,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5217,
            "text": "Shĩ ne Masanin fake da bayyane, Mabuwayi, Mai hikima.",
            "numberInSurah": 18,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          }
        ]
      },
      {
        "number": 65,
        "name": "سُورَةُ الطَّلَاقِ",
        "englishName": "At-Talaaq",
        "englishNameTranslation": "Divorce",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5218,
            "text": "Ya kai Annabi! Idan kun saki mãtã, sai ku sake su ga iddarsu, kuma ku ƙididdige iddar. Kuma ku bi Allah Ubangijinku da taƙawa. Kada ku fitar da su daga gidãjensu, kuma kada su fita fãce idan sunã zuwa da wata alfãshã bayyananna. Kuma waɗancan iyakõkin Allah ne. Kuma wanda ya ƙẽtare iyãkõkin Allah, to, lalle ya zãlunci kansa. Ba ka sani ba ɗammãnin Allah zai fitar da wani al'amari a bãyan haka.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5219,
            "text": "Sa'an nan idan sun isa ga ajalinsu (na idda) sai ku riƙe su da abin da aka sani kõ ku rabu da su da abin da aka sani kuma ku shaidar da mãsu adalci biyu daga gare ku. Kuma ku tsayar da shaidar dõmin Allah. Wancan ɗinku anã yin wa'azi da shi ga wanda ya kasance yanã yin ĩmãni da Allah da Rãnar Lãhira. Kuma wanda ya bi Allah da taƙawa, Allah zai sanya masa mafita.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5220,
            "text": "Kuma Ya arzũta shi daga inda bã ya zato. Kuma wanda ya dõgara ga Allah, to, Allah ne Ma'ishinsa. Lalle Allah Mai iyar da umurninSa ne. Haƙĩƙa Allah Ya sanyama'auni ga dukan kõme.",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5221,
            "text": "Kuma waɗanda suka yanke ɗammãni daga haila daga mãtanku, idan kun yi shakka, to, iddarsu watã uku ce da waɗanda ba su yi haila ba. Kuma ma'abũta cikinna ajalinsu, (shi ne) cewa su haifi cikinnansu. Kuma wanda ya bi Allah da taƙawa, (Allah) zai sanya masa wani sauƙi daga al'amarinsa.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5222,
            "text": "Wancan umurnin Allah ne, Ya saukar da shi zuwa gare ku. Kuma wanda ya bi Allah da taƙawa, Allah zai kankare masa mũnãnan ayyukansa, kuma Ya girmama masa sãkamako.",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5223,
            "text": "Ku zaunar da su daga inda kuka zaunar daga gwargwadon sãmunku. Kuma kada ku cũce su dõmin ku ƙuntata a kansu. Kuma idan sun kasance ma'abũta ciki, sai ku ciyar da su har su haifi cikinsu. Sa'an nan idan sun shãyar da mãma sabõda ku, sai ku bã su tsãdõdinsu. Kuma ku yi shãwara a tsakãninku bisa abin da aka sani. Kuma idan kun nũna talauci to wata mace zã ta shãyar da mãma sabõda shi (mijin).",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5224,
            "text": "Sai mawadãci ya ciyar daga wadãtarsa kuma wanda aka ƙuntata masa arzikinsa, to, sai ya ciyar daga abin da Allah Ya bã shi. Allah bã Ya kallafa wa wani rai fãce abin da Ya bã shi. Allah zai sanya sauƙi a bãyan tsanani.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5225,
            "text": "Kuma da yawa daga alƙarya wadda ta yi tsaurin kai daga barin umurnin Ubangijinta da ManzanninSa, sai Muka yi mata hisãbi, hisãbi mai tsanani, kuma Muka, azabtar da ita azãba abar ƙyãma.",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5226,
            "text": "Sa'an nan ta ɗanɗana masĩfar al'amarinta. kuma ƙarshen al'amarinta ya kasance hasãra,",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5227,
            "text": "Allah Ya yi musu tattalin wata azãba mai tsanani. Sabõda haka ku bi Allah da taƙawa, yã ma'abũta hankula, waɗanda suka yi ĩmãni! Haƙĩƙa Allah Ya saukar da wata tunãtarwa zuwa gare ku.",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5228,
            "text": "Manzo, yanã karãtun ãyõyin Allah bayyananu a kanku dõmin Ya fitar da waɗanda suka yi ĩmãni kuma suka aikata ayyukan ƙwarai daga duffai zuwa ga haske. Kuma wanda ya yi ĩmãni da Allah ya aikata aikin ƙwarai Allah zai shigar da shi gidãjen Aljanna ƙoramu nã gudãna daga ƙarƙashnsu sunã mãsu dawwama a cikinsu har abada. Haƙĩƙa Allah Yã kyautata masa arziki.",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5229,
            "text": "Allah, wanda Ya halitta bakwai ɗin sammai kuma daga ƙasã kwatankwacinsu, umuruinSa yanã ta sauka a tsakaninsu dõmin ku san lalle Allah Mai ĩkon yi ne akan dukan kõme, kuma lalle Allah, haƙĩƙa Ya kẽwaye ga dukan kõme da sani.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          }
        ]
      },
      {
        "number": 66,
        "name": "سُورَةُ التَّحۡرِيمِ",
        "englishName": "At-Tahrim",
        "englishNameTranslation": "The Prohibition",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5230,
            "text": "Yã kai Annabi! Don me kake haramta abin da Allah Ya halatta maka, kanã nẽman yardar mãtanka, alhãli kuwa Allah Mai gãfara ne, Mai jin ƙai.",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5231,
            "text": "Haƙĩƙa Allah ya faralta muku warware rantsuwõyinku, kuma Allah ne Mataimakinku kuma shĩ ne Masani, Mai hikima.",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5232,
            "text": "Kuma a sã'ilin da Annabi ya asirta wani lãbãri zuwa ga sãshen mãtansa, to, a lõkacin da ta bã da lãbari da shi. Kuma Allah Ya bayyana shi a gare shi, ya sanar da sãshensa kuma ya kau da kai daga wani sãshe. To, a lõkacin da ya bã ta lãbãri da shi, ta ce: 'Wãne ne ya gaya maka wannan?' Ya ce: 'Masani, Mai labartãwa, Ya gaya mini.'",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5233,
            "text": "Idan kũ biyu kuka tũba zuwa ga Allah, to, haƙĩƙa zukãtanku sun karkata. Kuma idan kun taimaki jũna a kansa to, lalle Allah Shĩ ne Mataimakinsa, da Jibrĩlu da sãlihan mũminai. Kuma malã'iku a bãyan wancan, mataimaka ne.",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5234,
            "text": "Mai yiwuwa ne Ubangijinsa, idan ya sake ku, Ya musanya masa waɗansu mãtan aure mafiya alhẽri daga gare ku, Musulmai mũminai mãsu tawãli'u, mãsu tũba, mãsu ibãda, mãsu, azumi, zawarõri da 'yammãta.",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5235,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Ku kãre wa kanku (da iyãlinku wata wuta makãmashinta mutãne da duwãtsu ne. A kanta akwai waɗansu malã'iku mãsu kauri, mãsu ƙarfi. Bã su sãɓã wa Allah ga abin da Ya umurce su, kuma sunã aikata abin da ake umunin su.",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5236,
            "text": "Yã kũ waɗanda suka kãfirta! Kada ku kãwo wani uzuri a yau. Anã yi muku sakamakon abin da kuka kasance kunã aikatãwa kawai ne.",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5237,
            "text": "Yã kũ waɗanda suka yi ĩmãni! Ku kõma zuwa ga Allah kõmawar gaskiya. Mai yiwuwa Ubangjinku Ya kankare muku miyãgun ayyukanku kuma Ya shigar da ku a gidãjen Aljanna, ƙoramu na gudãna daga ƙarƙashinsu a rãnar da Allah bã Ya kunyatar da Annabi da wadanda suka yi ĩmãni tãre da shi. Haskensu yanã tafiya a gaba gare su da jihõhin dãmansu, sunã cẽwa, 'Yã Ubangijiumu! Ka cika mana haskenmu, kuma Ka yi mana gãfara. Lalle Kai, a kan dukkan kõme, Yã kai Mai ikon yi ne.'",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5238,
            "text": "Yã kai Annabi! Ka yãki kãfirai da munãfwkai. Kuma ka tsananta a kansu. Kuma matattararsu Jahannama ce, kuma tir da makoma, ita.",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5239,
            "text": "Allah Ya buga wani misãli dõmin waɗanda suka kãfirta: mãtar Nũhu da mãtar Lũɗu, sun kasance a ƙarƙashin wasu bãyi biyu daga bãyinMu sãlihai, saisuka yaudare su, sabõda haka ba su wadãtar musu da kõme daga Allah ba. Kuma aka ce: 'Ku shiga, kũ biyu, wutã tãre da mãsu shiga.'",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5240,
            "text": "Kuma Allah Ya buga wani misãli dõmin waɗanda suka yi ĩmãni; matar Fir'auna, sa'ad da ta ce 'Ya Ubangiji! Ka gina mini wani gida a wurinKa a cikin Aljanna. Kuma Ka tsĩrar da ni daga Fir'auna da aikinsa. Kuma Ka tsĩrar da ni daga mutãnen nan azzãlumai.'",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5241,
            "text": "Da Maryama ɗiyar Imrãna wadda ta tsare farjinta, sai Muka hũra a cikinsa daga rũhinMu. Kuma ta gaskata game da ãyõyin Ubangijinta da LittattafanSa alhãli kuwa ta kasance daga mãsu tawãli'u.",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          }
        ]
      },
      {
        "number": 67,
        "name": "سُورَةُ المُلۡكِ",
        "englishName": "Al-Mulk",
        "englishNameTranslation": "The Sovereignty",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5242,
            "text": "(Allah), Wanda gudãnar da mulki yake ga hannunSa, Ya tsarkaka, kuma Shi Mai ĩko ne a kan kome.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5243,
            "text": "Shi ne Wanda Ya halitta mutuwa da rãyuwa domin Ya jarraba ku, Ya nũna wãye daga cikinku ya fi kyãwon aiki, Shi ne Mabuwãyi, Mai gafara.",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5244,
            "text": "Shi ne wanda Ya halitta sammai bakawi, ɗabaƙõƙi a kan jũna, bã za ka ga goggociya ba a cikin halittar (Allah) Mai rahama. Ka sãke dũbawa, ko za ka ga wata ɓaraka?",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5245,
            "text": "Sa'an nan ka sake maimaita, dũbãwa, ganinka zai komo maka, gajiyayye, ba da ganin wata naƙasa bã.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5246,
            "text": "Kuma lalle Mun ƙawãta samar farko da fitillu, kuma Muka sanya su abin jifa ga shaiɗanu, kuma Muka yi musu tattalin azãbar Sa'ĩr.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5247,
            "text": "Kuma waɗanda suka kafirce wa Ubangjinsu na da azãbar Jahannama, tã munana ga zamanta makõmarsu.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5248,
            "text": "Idan an jẽfa su a cikinta, sai su ji daga gare ta wata ƙãra, tana tafasa.",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5249,
            "text": "Tana kusa ta tsãge domin hushi, ko da yaushe aka jẽfa wani ɓangaren jama'a a cikinta, matsaranta na tambayar su da cewa, 'Wani mai gargaɗi bai je muku ba?'",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5250,
            "text": "Sai su ce: 'I, lalle wani mai gargaɗi ya je mana, sai muka ƙaryata shi, muka ce, 'Allah bai saukar da kome ba, bã ku cikin kõme sai ɓata babba.''",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5251,
            "text": "Kuma suka ce: 'Dã mun zamo muna saurãre, kõ muna da hankali, dã ba mu kasance a cikin 'yan sa'ĩr ba.'",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5252,
            "text": "Wato su yi iƙrãri da laifinsu. Allah Ya la'ani'yan sa'ĩr!",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5253,
            "text": "Lalle waɗanda ke tsõron Ubangjinsu, a ɓoye, suna da wata gãfara da wani sakamako mai girma.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5254,
            "text": "Kuma ku asirta maganarku ko ku bayyana ta, lalle shi, (Allah), Masani ne ga abin da ke cikin ƙirãza.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5255,
            "text": "Ashe, wanda Ya yi halitta bã zai iya saninta ba, alhãli kuwa shi Mai tausasãwa ne, kuma Mai labartawa?",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5256,
            "text": "Shi, (Allah), Yã sanya muku ƙasa hõrarriya, sai ku tafi cikin sãsanninta, kuma ku ci daga arzikinSa, kuma zuwa gare shi ne tãshin yake.",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5257,
            "text": "Shin kõ kun amince cewa wanda ke cikin sama, bã zai iya shãfe ƙasa tãre da ku ba, sai ga ta tana mai girgiza?",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5258,
            "text": "Kõ kun amincẽ cewa wanda ke cikin sama bã zai iya sako muku iskar gũguwa ba? To, zã ku san yadda (ãƙibar) gargaɗiNa take.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5259,
            "text": "Lalle, waɗanda ke gabansu sun ƙaryata (manzanni). To, yãya (ãƙibar) gargaɗiNa ta kasance?",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5260,
            "text": "Bã zã su yi dũbi ba zuwa ga tsuntsãye a kansu, mãsu sanwã, kuma suna fiffikãwa, bãbu mai riƙe da su sai (Allah), Mairahama? Lalle shi Mai gani ne ga dukan kõme.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5261,
            "text": "Wãne ne wanda zai zame muku mayãƙin da zai taimake ku, wanda bã (Allah) ba, Mai rahama? Kãfirai ba su a cikin kõme fãce rũɗu.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5262,
            "text": "Wãne ne wanda zai ciyar da ku, idan (Allah) Ya riƙe arzikinSa? A'aha, sun yi zurfi cikin girman kai da tãshin hankali.",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5263,
            "text": "Shin, wanda ke tafiya a kife a kan fuskarsa yã fi zama a kan shiryuwa, ko kuwa wanda ke tafiya miƙe a kan hanya madaidaiciya?",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5264,
            "text": "Ka ce: '(Allah) Shine Wanda Ya ƙaga halittarku, Ya sanya muku ji da gani da tunãni, amman kaɗan ce ƙwarai godiyarku!'",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5265,
            "text": "Kuma kace 'Shi ne Ya halitta ku daga ƙasã, kuma zuwa gareShi ne ake tãshin ku.'",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5266,
            "text": "Kuma sunã cewa, 'Yaushe ne wannan alkawarin zai tabbata in dai kun kasance mãsu gaskiya ne kũ?'",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5267,
            "text": "Ka ce: 'Ilmin a wurin Allah kawai yake, kuma ni mai gargaɗi kawai ne, mai bayyana (gargaɗin).'",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5268,
            "text": "To, lokacin da suka gan ta (azãbar) a kusa, fuskokin waɗanda suka kãfirta suka mũnana, kuma aka ce (musu) wannan shi ne abin da kuka zamo kuna ƙaryatãwa.",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5269,
            "text": "Ka ce musu 'Idan Allah Ya halaka ni nĩ da wanda ke tãre da ni, ko kuma, Yã yi mana rahama, to, wãne ne zai tserar da kafirai daga wata azãba mai raɗaɗi?'",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5270,
            "text": "Ka ce: 'Shĩ ne Mai rahama mun yi ĩmãni da Shi, gare Shi muka dogara, saboda haka zã ku san wanda yake a cikin ɓata bayyananniya.'",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5271,
            "text": "Ka ce: 'Ko kun gani, idan ruwanku ya wãyi gari faƙaƙƙe, to, wãne ne zai zo muku da ruwa wani mai ɓuɓɓuga?'",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          }
        ]
      },
      {
        "number": 68,
        "name": "سُورَةُ القَلَمِ",
        "englishName": "Al-Qalam",
        "englishNameTranslation": "The Pen",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5272,
            "text": "Ñ. Na rantse da alƙalami da abin da (marubũta) suke rubũtãwa.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5273,
            "text": "Kai, sabõda ni'imar Ubangijinka, bã mahaukaci kake ba.",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5274,
            "text": "Kuma, lalle, haƙĩƙa kanã da lãdar da bã ta yankewa.",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5275,
            "text": "Kuma, lalle haƙiƙa kana a kan hãlãyen kirki, manya.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5276,
            "text": "Don haka, zã ka gani, sũ kuma zã su gani.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5277,
            "text": "Ga wanenku haukã take.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5278,
            "text": "Lalle Ubangijinka Shi ne Mafi sani ga wanda ya ɓace daga tafarkinSa. Kuma Shi ne Mafi sani ga mãsu shiryuwa.",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5279,
            "text": "Saboda haka kada ka bi mãsu ƙaryatãwa.",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5280,
            "text": "Sunã fatar ka sassauta, su kuma sũ sassauta.",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5281,
            "text": "Kada ka bi dukkan mai yawan rantsuwa, walãkantacce.",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5282,
            "text": "Mai zunɗe, mai yãwo da gulma.",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5283,
            "text": "Mai hana alhẽri mai zãlunci, mai zunubi.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5284,
            "text": "Mai girman kai, bãyan haka kuma la'ĩmi (bã ya son alhẽri).",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5285,
            "text": "Sabõda yã kasance mai dũkiya da ɗiya.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5286,
            "text": "Idan ana karanta masa ãyõyinMu, sai ya ce: 'Tatsũniyõyin mutãnen farko ne.'",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5287,
            "text": "Zã Mu yi masa alãma a kan hanci.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5288,
            "text": "Lalle Mun jarrabe su kamar yadda Muka jarrabi mãsu gonar lambu, a lokacin da suka yi rantsuwa, wai lalle zã su girbe amfãninta, suna mãsu asubanci.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5289,
            "text": "Kuma (suka yi niyya), bã su kõ tõgẽwa.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5290,
            "text": "Wani mai kẽwayãwa daga Ubangijinka ya kẽwayã a kanta, (ya ƙone ta,) alhãli sunã barci.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5291,
            "text": "Sai ta wãyi gari (baƙa ƙirin) kamar dare.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5292,
            "text": "Sai suka kirãyi jũna, sunã mãsu yin asubanci.",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5293,
            "text": "Cẽwa ku yi sammakõ ga amfãnin gõnarku, idan kun kasance mãsu girbewa.",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5294,
            "text": "Sai suka tafi sunã shãwara a ɓõye (sunã cẽwa).",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5295,
            "text": "'Lalle kada wani miskĩni ya shigar muku ita a yau!'",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5296,
            "text": "Kuma suka yi sammako akan (niyyar) rõwa, matuƙar iyãwarsu.",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5297,
            "text": "Lõkacin da suka gan ta, sai suka ce: 'Lalle, mun saki hanya (mun ɓace)!'",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5298,
            "text": "'Ba haka ba, mũ dai, an hana mu ne.'",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5299,
            "text": "Mafificinsu (hankali) ya ce: 'Ban gaya muku ba, yã kamata ku tsarkake Allah?'",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5300,
            "text": "Suka ce: 'Tsarki ya tabbata ga Ubangijinmu! Lalle, mu mun kasance azzãlumai.'",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5301,
            "text": "Sai suka fuskanci jũna, sunã zargin kansu.",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5302,
            "text": "Suka ce: 'Kaitonmu! Lalle, mũ ne muka kasance mãsu ƙẽtare iyãka.'",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5303,
            "text": "'Mai yiwuwa ne Ubangijinmu Ya musanya mana da wani abu da yã fi ta. Lalle, mũ (ba mu yanke ƙauna ba) zuwa ga Ubangijinmu, mãsu kwaɗayi ne.'",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5304,
            "text": "Haka dai azãbar take, kuma lalle ne azãbar Lãhira ta fi girma, in da sun kasance zã su iya gãnẽwã.",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5305,
            "text": "Lalle ne mãsu taƙawa nã da a wurin Ubangijinsu, gidãjen Aljanna na ni'ima.",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5306,
            "text": "Shin ko zã Mu sanya waɗanda ke bi da ɗa'a kamar mãsu laifi?",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5307,
            "text": "Me ya sãme ku, yãya kuke yin hukunci (da haka)?",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5308,
            "text": "Ko kuna da wani littafi, wanda a cikinsa, kuke karantãwa?",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5309,
            "text": "Cẽwã kunã da, a cikinsa lalle (duk) abin da rãyukanku suka zãɓa?",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5310,
            "text": "Kõ kunã (riƙe) da wasu rantsuwõyin alkawari a kanMu, har ya zuwa Rãnar ¡iyãma, cẽwa lalle ne kunã da abin da kuke hukuntã wa kanku?",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5311,
            "text": "Ka tambaye su, wãne, a cikinsu, ke lãmuncewã ga sãmun wannan (hukuncin)?",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5312,
            "text": "Kõ sunã da abõkan tãrẽwa (ga wannan ra'ayi)? To, sai su kãwo abõkan tãrayyarsu, idan sun kasance mãsu gaskiya.",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5313,
            "text": "Ranar da zã a kuranye ƙwabri, kuma a kira su zuwa ga yin sujũda, sai bã zã su iyãwa ba.",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5314,
            "text": "Su fito da idãnu ƙasƙantattu, wulãkanci yana rufe su. Lalle ne a dã sun kasance anã kiran su zuwa, yin sujũdar, alhãli kuwa sunã lãfiya lau, (suka ƙi yi).",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5315,
            "text": "Sabõda haka ka bar Ni da mai ƙaryata wanna lãbãri (Alƙur'ãni). Zã Mu yi musu istidrãji daga inda ba su sani ba.",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5316,
            "text": "Ina jinkirtã musu, lalle kaidiNa mai ƙarfi ne.",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5317,
            "text": "Kõ kana tambayar su wata la'ada ne, sabõda haka sũ, daga wannan tãrar, suke jin an nauyaya musu?",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5318,
            "text": "Ko kuma sunã da ilmin gaibu ne, wãto sunã yin rubũtun (abin da suke faɗã daga gare shi) ne?",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5319,
            "text": "Sabõda haka, ka yi haƙuri da hukuncin Ubangijinka, kada ka zama kamar Ma'abũcin kĩfi, alõkacin da ya yi kira, alhãli yana cike da hushi.",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5320,
            "text": "Ba dõmin ni'ima daga wajen Ubangijinsa tã riske shi ba, lalle ne da an jẽfa shi a cikin wõfintacciyar ƙasa, alhãli yanã abin zargi.",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5321,
            "text": "Sai Ubangijinsa Ya zãɓe shi, sa'an nan Ya sanya shi a cikin mutãnen kirki.",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5322,
            "text": "Kuma lalle, ne waɗanda suka kãfirta sunã yin kamar su kãyar da kai sabõ da kallonsu (kallon mãmãki), a lokacin da suke jin karãtun Alƙur'ãni, kuma sunã cẽwa, 'Lalle ne shi mahaukaci ne!'",
            "numberInSurah": 51,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5323,
            "text": "Shi (Alƙur'ãni) bai kasance ba, fãce abin tunãtarwa ne ga duka dũniya.",
            "numberInSurah": 52,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          }
        ]
      },
      {
        "number": 69,
        "name": "سُورَةُ الحَاقَّةِ",
        "englishName": "Al-Haaqqa",
        "englishNameTranslation": "The Reality",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5324,
            "text": "Kiran gaskiya!",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5325,
            "text": "Mẽne ne kiran gaskiya?",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5326,
            "text": "Kuma mẽ ya sanar da kai abin da ake cẽwa kiran gaskiya?",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5327,
            "text": "Samũdãwa da Ãdãwa sun ƙaryatar da kiran gaskiya mai dũkar zũciya!",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5328,
            "text": "To, amma Samũdãwa to, an halakãsu da tsãwa mai tsanani.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5329,
            "text": "Kuma amma Ãdãwa to, an halaka su da wata iska mai tsananin sauti wadda ta ƙẽtare haddi.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5330,
            "text": "(Allah) Ya hõre ta a kansu a cikin dare bakwai da yini takwas, biye da jũna, sabõda haka, kana ganin mutãne a cikinta kwance. Kamar sũ ƙirãruwan dabĩno ne, waɗanda suka fãɗi.",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5331,
            "text": "To, kõ kanã ganin abin da ya yi saurã daga cikinsu?",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5332,
            "text": "Kuma Fir'auna yã zo da waɗanda ke gabãninsa, da waɗannan da aka kife ƙasarsu, sabõda laifi.",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5333,
            "text": "Dõmin sun sãɓã wa manzon Ubangijinsu, sabõda haka ya kãmã su da wani irin kãmu mai ƙãruwar (tsanani).",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5334,
            "text": "Lalle ne, Mũ, a lõkacin da ruwa ya ƙẽtare haddi, Munɗauke aaku a cikin jirgin ruwan nan.",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5335,
            "text": "Dõmin Mu sanya shi, gare ku abin tunãwa kuma wani kunne mai kiyayewa ya kiyaye (shi).",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5336,
            "text": "To, idan an yi bũsa a cikin ƙaho, bũsa ɗaya.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5337,
            "text": "Kuma aka ɗauki ƙasa da duwãtsu, kuma aka niƙa su niƙãwa ɗaya.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5338,
            "text": "A ran nan, mai aukuwa zã ta auku.",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5339,
            "text": "Kuma sama zã ta tsãge, dõmin ita a ran nan, mai rauni ce.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5340,
            "text": "Kuma malã'iku (su bayyana) a kan sãsanninta, kuma wasu (malã'iku) takwas na ɗauke da Al'arshin Ubangijinka, a sama da su, a wannan rãnar.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5341,
            "text": "A rãnar nan zã a bijirã ku (dõmin hisãbi), bãbu wani rai, mai ɓoyewa, daga cikinku, wanda zai iya ɓõyẽwa.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5342,
            "text": "To, amma wanda aka bai wa littãfinsa a dãmansa, sai ya ce wa (makusantansa), 'Ku karɓa, ku karanta littafina.'",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5343,
            "text": "'Lalle ne ni, nã tabbata cewa ni mai haɗuwa da hisãbina ne.'",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5344,
            "text": "Sabõda haka, shi yana cikin wata rãyuwa yardadda.",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5345,
            "text": "A cikin Aljanna maɗaukakiya.",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5346,
            "text": "Nunannun 'yã'yan itãcenta makusantã ne (ga mai son ɗĩba),",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5347,
            "text": "(Ana ce musu) 'Ku ci, kuma ku sha a cikin ni'ima, sabõda abin da kuka gabãtar a cikin kwãnukan da suka shige.'",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5348,
            "text": "Kuma wanda aka bai wa littãfinsa ga hagunsa, sai ya ce: 'Kaitona, ba a kãwo mini littãfina ba!'",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5349,
            "text": "'Kuma ban san abin da (ke sakamakon) hisãbina ba!'",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5350,
            "text": "'In dã dai ita, tã kasance mai halakã ni gabã ɗaya ce!",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5351,
            "text": "'Dukiyãta ba ta wadatar da ni ba!'",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5352,
            "text": "'Ĩkona ya ɓace mini!'",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5353,
            "text": "(Sai a ce wa malã'iku) 'Ku kãmã shi, sa'an nan ku sanyã shi a cikin ƙuƙumi.'",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5354,
            "text": "'Sa'an nan, a cikin Jahĩm, ku ƙõna shi.'",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5355,
            "text": "'Sa'an nan, acikin sarƙa, tsawonta zirã'i saba'in, sai ku sanya shi.'",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5356,
            "text": "'Lalle ne, shi ya kasance ba ya yin ĩmãni da Allah, Mai girma!'",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5357,
            "text": "'Kuma ba ya kwaɗaitarwa ga (bãyar da) abincin matalauci!'",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5358,
            "text": "'Sabõda haka, a yau, a nan, bã ya da masõyi.'",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5359,
            "text": "'Kuma bãbu wani abinci, sai daga (itãcen) gislĩn.'",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5360,
            "text": "'Bãbu mai cin sa sai mãsu ganganci.'",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5361,
            "text": "To, ba sai Nã yi rantsuwa da abin da kuke iya gani ba,",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5362,
            "text": "Da abin da bã ku iya gani.",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5363,
            "text": "Lalle ne, shi (Alƙur'ani) tabbas maganar wani manzo (Jibirilu) mai daraja ne.",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5364,
            "text": "Kuma shi ba maganar wani mawãƙi ba ne. Kaɗan ƙwarai zã ku gaskata.",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5365,
            "text": "Kuma bã maganar bõka ba ne. Kaɗan ƙwarai zã ku iya tunãwa.",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5366,
            "text": "Abin saukarwã ne daga Ubangijin halitta duka.",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5367,
            "text": "Kuma dã (Muhammadu) yã faɗi wata maganã, yã jingina ta garẽ Mu.",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5368,
            "text": "Dã Mun kãma shi da dãma.",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5369,
            "text": "sa'an nan, lalle ne, dã Mun kãtse masa lakã.",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5370,
            "text": "Kuma daga cikinku bãbu wasu mãsu iya kãre (azãbarMu) daga gare shi.",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5371,
            "text": "Kuma lalle ne shi (Alƙur'ãni) tanãtarwa ce ga mãsu taƙawa.",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5372,
            "text": "Kuma lalle, ne Mũ, wallahi Munã sane da cẽwa daga cikinku alwwai mãsu ƙaryatãwa.",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5373,
            "text": "Kuma lalle ne shi (Alƙarãni) wallahi baƙin ciki ne ga kãfirai.",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5374,
            "text": "Kuma lalle, ne shi gaskiya ce ta yaƙshẽni.",
            "numberInSurah": 51,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5375,
            "text": "Sabõda haka, ka tsarkake sũnan Ubangjinka, mai girma.",
            "numberInSurah": 52,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          }
        ]
      },
      {
        "number": 70,
        "name": "سُورَةُ المَعَارِجِ",
        "englishName": "Al-Ma'aarij",
        "englishNameTranslation": "The Ascending Stairways",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5376,
            "text": "Wani mai tambaya yã yi tambaya game da azãba, mai aukuwa.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5377,
            "text": "Ga kãfirai, bã ta da mai tunkuɗẽwa.",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5378,
            "text": "Daga Allah Mai matãkala.",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5379,
            "text": "Malã'iku da Ruhi (Jibrila) sunã tãkãwa zuwa gare Shi, a cikin yini wanda gwargwadonsa, shẽkara dubu hamsin ne.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5380,
            "text": "Sabõda haka, ka yi haƙuri, haƙuri mai kyãwo.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5381,
            "text": "Lalle ne su, sunã ganin ta mai nĩsa.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5382,
            "text": "Kuma Mu, Muna ganin ta a kusa.",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5383,
            "text": "Rãnar da sama zã ta kasance kamar narkakkar azurfa.",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5384,
            "text": "Kuma duwãtsu su zama kamar saɓin sũfi.",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5385,
            "text": "Kuma abõki bã ya tambayar inda wani abõki yake.",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5386,
            "text": "Ana sanya su, su gajũna, mai laifi na gũrin dã zai iya yin fansa, daga azãbar rãnar nan, da ɗiyansa,",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5387,
            "text": "Da matarsa da ɗan'uwansa.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5388,
            "text": "Da danginsa, mãsu tattarã shi.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5389,
            "text": "Da wanda ke a cikin dũniya duka gabã ɗaya, sa'an nan fansar, ta tsĩrar da shi.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5390,
            "text": "A'aha! Lalle ne fa, ita ce Lazã,",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5391,
            "text": "Mai twãle fãtar goshi.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5392,
            "text": "Tanã kiran wanda ya jũya bãya (daga addini) kuma ya kau da kai.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5393,
            "text": "Ya tãra (dũkiya), kuma ya sanya ta a cikin jaka.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5394,
            "text": "Lalle ne mutum an halitta shi mai ciwon kwaɗayi.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5395,
            "text": "Idan sharri ya shãfe shi, ya kãsa haƙuri.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5396,
            "text": "Kuma idan alhẽri ya shãfe shi, ya yi rõwa.",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5397,
            "text": "Sai mãsu yin salla,",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5398,
            "text": "Waɗanda suke, a kan sallarsu, su, mãsu dawwama ne.",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5399,
            "text": "Kuma waɗanda a cikin dũkiyarsu, akwai wani haƙƙi sananne.",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5400,
            "text": "Ga (matalauci) mai rõƙo da wanda aka hanã wa roƙon.",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5401,
            "text": "Da waɗannan da ke gaskata rãnar sakamako.",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5402,
            "text": "Da waɗannan sabõda azãbar Ubangijinsu, suna jin tsõro.",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5403,
            "text": "Lalle ne, azãbar Ubangijinsu bã wadda ake iya amincẽwaba ce.",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5404,
            "text": "Da waɗanda suke, ga farjojinsu, mãsu tsarewa ne.",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5405,
            "text": "Sai fa a kan matan aurensu da abin da hannayensu na dãma suka mallaka. To lalle ne sũkam ba waɗanda ake zargi ba ne.",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5406,
            "text": "To, duk wanda ya nẽmi abin da yake a bayan wannan, to, waɗancan sũ ne mãsu ƙetare iyãka.",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5407,
            "text": "Kuma da waɗannan da suke ga amãnõninsu da alkawarinsu mãsu tsarẽwa ne.",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5408,
            "text": "Kuma da waɗanda suke, ga shaidarsu, mãsu dãgẽwa ne.",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5409,
            "text": "Kuma waɗanda suke, a kan sallarsu, mãsu tsarẽwa ne.",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5410,
            "text": "Waɗannan, a cikin gidãjen Aljanna, waɗanda ake girmamãwa ne",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5411,
            "text": "Mẽ yã sãmi waɗanda suka kãfirta, a wajenka, sunã gaugãwar (gudu).",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5412,
            "text": "Daga jihar dãma, kuma daga jihar hagu, jama'a-jama'a!",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5413,
            "text": "Shin kõwane mutum daga cikinsu yana nẽman a shigar da shi a Aljannar ni'ima ne (ba da wani aiki ba)?",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5414,
            "text": "A'aha! Lalle Mũ, Mun halitta su, daga abin da suka sani.",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5415,
            "text": "Sabõda haka ba sai Na yi rantsuwa da Ubangijin wurãren ɓullõwar rãna da wurãren fãɗuwarta ba, lalle Mũ, Mãsu iyãwa ne.",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5416,
            "text": "Ga Mu musanya waɗanda suke mafi alhẽri, daga gare su. Kuma ba Mu kasance waɗanda ake rinjãya ba.",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5417,
            "text": "Sabõda haka ka bar su, su dulmuya, kuma su yi wãsã, har su haɗu da rãnarsu wadda ake yi musu alkawari (da ita).",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5418,
            "text": "Rãnar da suke fitowa daga kaburbura da gaugãwa, kamar sũ, zuwa ga wata kafaffiyar (tuta), suke yin gaugãwa.",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5419,
            "text": "Maƙaskanta ga idanunsu, wani walakanci yana rufe su. wannan shi ne yinin da suka kasance ana yi musa alkawari (a kansa.)",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          }
        ]
      },
      {
        "number": 71,
        "name": "سُورَةُ نُوحٍ",
        "englishName": "Nooh",
        "englishNameTranslation": "Noah",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5420,
            "text": "Lalle ne Mun aiki Nũhu zuwa ga mutãnensa, cẽwa ka yi gargaɗi ga mutãnenka gabãnin wata azãba mai raɗaɗi ta zo musu.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5421,
            "text": "Ya ce: 'Ya mutãnena ni, a gare ku, mai gargaɗi ne, mai bayyanãwa.'",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5422,
            "text": "'Cewa ku bauta wa Allah, ku ji tsõronSa, kuma ku bĩ ni.'",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5423,
            "text": "'Allah zai gãfarta muku daga zunubanku kuma Ya jinkirta muku zuwa ga ajalin da aka ambata. Lalle ne ajalin Allah idan ya zo, ba za a jinkirta Shi ba, dã kun kasance masana (ga abin da nake faɗã dã, kun bar kãfirci).'",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5424,
            "text": "Nũhu) ya ce: 'Ya Ubangijina! Lalle ne, na kirãyi mutãnena, a cikin dare da yini.'",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5425,
            "text": "'To, amma kirana bai ƙãre su ba sai da, gudu (daga gare ni).'",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5426,
            "text": "'Kuma lalle ne ni, kõ da yaushe na kirãye su dõmin Ka gãfarta musu, sai su sanya yatsunsu a cikin kunnuwansu, su lulluɓe da tufãfinsu, su dõge ga yin laifi. Sun yi girman kai iyãkar girman kai.'",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5427,
            "text": "'Sa'an nan lalle ne ni, na kira su, a bayyane.'",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5428,
            "text": "'Sa'an nan lalle ne, na yi yẽkuwa sabõda su, kuma na gãna da su a cikin asĩri.'",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5429,
            "text": "'Shi na ce, 'Ku nẽmi gãfara daga Ubangijinku, lalle ne shi Ya kasance Mai gãfara ne.'",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5430,
            "text": "'Ya sako (girgijen) sama a kanku da ruwa mai ɓuɓɓuga.'",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5431,
            "text": "'Kuma ya yalwata muku game da dũkiya da ɗiya, Ya sanya muku (albarka) ga gõnaki, kuma Ya sanya muku koguna.'",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5432,
            "text": "'Me ya sãme ku, bã ku fãtar sãmun natsuwa daga Allah,'",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5433,
            "text": "'Alhãli kuwa, lalle ne, Ya halitta. ku, a cikin hãlãye?'",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5434,
            "text": "'Ba ku ga yadda Allah Ya halitta wasu sammai bakwai ɗabaƙõƙĩ a kan jũna ba?'",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5435,
            "text": "'Ya sanya watã a cikinsu, yana mai haske, Ya sanya rãnã babbar fitila?'",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5436,
            "text": "'Kuma Allah Ya tsirar da ku daga ƙasa (kamar ganye) tsirarwa.'",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5437,
            "text": "'Sa'an nan Ya mayar da ku a cikinta, kuma Ya fitar da ku fitarwa.'",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5438,
            "text": "'Kuma Allah Ya sanya muku ƙasa shimfidaɗɗiya.'",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5439,
            "text": "'Dõmin ku shiga a cikinta, hanyõyi mãsu fãɗi.'",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5440,
            "text": "Nũhu ya ce: 'Ya Ubangijina! Lalle ne sũ sun saɓa mini sun bi wanda dukiyarsa da ɗiyansa ba su ƙãre shi da kõme ba sai da hasãra.'",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5441,
            "text": "'Kuma sun yi wani mãkirci, mãkirci babba.'",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5442,
            "text": "'Kuma suka ce: Faufau kada ku bar gumãkanku, kuma faufau, kada ku bar wadda, kuma kada ku bar suwã kuma kada ku bar Yagũsa da Ya'ũƙa da Nasra.'",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5443,
            "text": "'Kuma lalle ne sun ɓatar da (mutãne) mãsu yawa, kuma kada Ka ƙãrã wa azzãlumai (kõme) sai ɓata.'",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5444,
            "text": "Sabõda laifukansu na ganganci aka nutsar da su, sa'an nan aka sanya su a wuta. Saboda haka ba su sãmã wa kansu wani mataimaki wanin Allah ba.",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5445,
            "text": "Kuma Nũhu ya ce: 'Ya Ublngijina! Kada Ka bari, a kan ƙasa, daga cikin kãfirai, wanda ke zama a cikin gida.'",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5446,
            "text": "'Lalle ne, Kai idan Ka bar su, zã su ɓatar da bãyinKa, kuma bã zã su haifi kowa ba fãce fãjiri mai yawan kãfirci'",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5447,
            "text": "'Ya Ubanginjina! Ka yi mini gãfara (ni) da mahaifana biyu, da wanda ya shiga gidana, yana mai ĩmãni, da mũminai maza da mũminai mãtã, kuma kada Ka ƙãrã wa azzãlumai kõme sai halaka.'",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          }
        ]
      },
      {
        "number": 72,
        "name": "سُورَةُ الجِنِّ",
        "englishName": "Al-Jinn",
        "englishNameTranslation": "The Jinn",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5448,
            "text": "Ka ce: 'An yi wahayi zuwa gare ni cewa wasu jama'a na aljannu sun saurũri (karatuna), sai suka ce: Lalle ne mũ mun ji wani abin karantãwa (Alƙui'ãni), mai ban mãmãki.'",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5449,
            "text": "'Yana nũni zuwa ga hanyar ƙwarai, sabõda haka mun yi ĩmãni da shi bã zã mu kõma bautã wa Ubangijinmu tãre da kõwa ba.'",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5450,
            "text": "'Kuma lalle ne shĩ girman Ubangijinmu, Ya ɗaukaka, bai riƙi mãta ba, kuma bai riƙi ɗã ba.'",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5451,
            "text": "'Kuma lalle ne shi, wãwanmu ya kasance yana faɗar abin da ya ƙetare haddi ga Allah.'",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5452,
            "text": "'Kuma lalle ne mũ, mun yi zaton mutum da aljani bã zã su iya faɗar ƙarya ba ga Allah.'",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5453,
            "text": "'Kuma lalle ne shi wasu maza daga cikin mutãne, sun kasance sunã nẽman tsari da wasu maza daga cikin aljannu, sabõda haka suka ƙara musu girman kai.'",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5454,
            "text": "'Kuma lalle ne sũ, sun yi zato, kamar yadda kuka yi zato, cewa Allah bã zai aiko kowa ba.'",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5455,
            "text": "'Kuma lalle ne mũ mun nẽmi (hawan) sama, sai muka, sãme ta an cika ta da tsaro mai tsanani da kuma yũlãye.'",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5456,
            "text": "'Kuma lalle ne mũ, mun kasance muna zama daga garẽta, a wurãren zama dõmin saurare. To wanda ya yi saurãre a yanzu, zai sãmi yũla, mai dãko dõminsa.'",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5457,
            "text": "'Kuma lalle ne mũ ba mu sani ba, shin, sharri ne aka yi nufi ga waɗanda ke cikin ƙasa, ko Ubangijinsu Yã yi nufin shiriya a gare su ne?'",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5458,
            "text": "'Kuma lalle ne mũ, akwai sãlaihai a cikinmu, kuma akwai a cikinmu waɗanda ba haka bã mun kasance ƙungiyõyi dabam-dabam.'",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5459,
            "text": "'Kuma lalle ne mũ mun tabbatã bã zã mu buwãyi Allah ba, a cikin ƙasa, kuma bã zã mu buwãye Shi da gudu ba.'",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5460,
            "text": "'Kuma lalle ne mũ, a lõkacin da muka ji shiriya, mun yi ĩmãni da ita. To wanda ya yi ĩimãni da Uhangijinsa, bã zai ji tsõron nakkasa ba, kuma bã zai ji tsõron zãlunci ba.'",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5461,
            "text": "'Kuma lalle ne mũ akwai a cikinmu, waɗanda suka mĩƙa wuya, kuma akwai a cikinmu karkatattu. to, wanda ya mĩƙa wuya waɗancan kam sun nufi shiryuwa.'",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5462,
            "text": "'Kuma amma karkatattu sai suka kasance makãmashi ga Jahannama.'",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5463,
            "text": "'Kuma dã sun tsayu sõsai a kan hanya, dã lalle Mun shãyar da su ruwa mai yawa.'",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5464,
            "text": "'Domin Mu jarraba su a ciki, kuma wanda ya kau da kai daga tuna Ubangijinsa, Ubangijinsa zai shigar da shi azãba mai hauhawa.'",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5465,
            "text": "'Kuma lalle ne wurãren sujada na Allah ne, saboda haka kada ku kira kõwa tãre da Allah (da su, a cikinsu).'",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5466,
            "text": "'Kuma, lalle ne shi, a lõkacin da bãwan Allah ke kiran Sa sun yi kusa su zama kamar shirgi a kansa.'",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5467,
            "text": "Ka ce: 'lna kiran Ubangijina ne kawai, kuma bã zan tãra kowa da Shi ba.'",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5468,
            "text": "Ka ce: 'Lalle ne, nĩ bã ni mallakar wata cũta gare ku, kuma bã ni mallakar wani alhẽri.'",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5469,
            "text": "Ka ce: 'Lalle ne, nĩ kõwa bã ya iya cẽtona daga Allah, kuma bã zan iya sãmun mafaka ba daga gare Shi.'",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5470,
            "text": "'Fãce iyarwa, daga Allah, da manzancinsa. To, wanda ya sãɓa wa Allah da ManzonSa, to, lalle yana da wutar Jahannama suna mãsu dawwama a cikinta, har abada.'",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5471,
            "text": "Har idan sun ga abin da ake yi musu gargaɗi da shi, to, zã su san wanda ya zama mafi raunin mataimaki da mafi ƙarancin adadi.",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5472,
            "text": "Ka ce: 'Ban sani ba a kusa ne abin da ake yi muku gargaɗi da shi, ko Ubangijina Ya sanya maSa dõgon ajali!'",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5473,
            "text": "'Shi ɗai ne Masanin fake sabõda haka, bã Ya bayyana gaibinSa ga kowa.'",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5474,
            "text": "'Fãce ga wanda Yã yarda da shi, wato wani manzo sa'an nan lalle ne, zai sanya gãdi a gaba gare shi da bãya gare shi.'",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5475,
            "text": "'Dõmin Ya san lalle, sun iyar da sãƙonnin Ubangijinsu, kuma (Shi Ubangijin) Yã kẽwaye su da sani, kuma Yã lissafe dukan kõme da ƙididdiga.'",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          }
        ]
      },
      {
        "number": 73,
        "name": "سُورَةُ المُزَّمِّلِ",
        "englishName": "Al-Muzzammil",
        "englishNameTranslation": "The Enshrouded One",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5476,
            "text": "Yã wanda ya lulluɓe!",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5477,
            "text": "Ka tsayu domin yin salla a cikin dare (duka) fãce kaɗan.",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5478,
            "text": "Rabinsa, ko ka rage abu kaɗan daga gare shi.",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5479,
            "text": "Ko ka ƙara kansa kuma ka kyautata karanta Alƙu'ãni, daki daki.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5480,
            "text": "Lalle ne, Mũ, zã Mu jẽfa maka magana mai nauyi.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5481,
            "text": "Lalle ne, tashin dare shi ne mafi tsananin natsuwa, kuma mafi daidaituwa ga magana.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5482,
            "text": "Lalle ne, kanã da, a cikin yini, wani tasĩhi mai tsawo.",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5483,
            "text": "Kuma ka ambaci sũnan Ubangijinka, kuma ka yanke (ƙauna daga kowa ka koma) zuwa gare Shi, yankewa.",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5484,
            "text": "Shi ne Ubangijin mafitar rãnã da mafãɗarta, bãbu abin bautawa fãce Shi. Sabõda haka ka riƙe Shi wakili.",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5485,
            "text": "Kuma ka yi haƙuri ga abin da suke faɗã, kuma ka ƙaurace musu, ƙauracẽwa mai kyãwo.",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5486,
            "text": "Kuma ka bar Ni da mãsu ƙaryatãwa, mawadãta, kuma ka jinkirta musu kaɗan.",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5487,
            "text": "Lalle ne, a wurinMu, akwai wasu marũruwa mãsu nauyi da Jahĩm.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5488,
            "text": "Da wani abinci mai mãƙara da azãba mai raɗaɗi.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5489,
            "text": "Rãnar da ƙasa ke raurawa, da duwãtsu kuma duwãtsu su kasance tudun rairayi mai malãlã.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5490,
            "text": "Lalle ne, Mũ. Mun aika zuwa gare ku, wani Manzo mai shaida a kanku, kamar yadda Muka aika wani Manzo zuwaga Fir'auna,",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5491,
            "text": "Sai Fir'auna ya saɓa wa Manzon, saboda haka Muka kãma shi kãmu mai tsanani.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5492,
            "text": "To, yãya, idan kun kãfirta, zã ku kãre (azabar) wani yini mai mayar da yãra (tsofaffi) mãsu hurhura.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5493,
            "text": "Sama za ta tsãge a cikinsa, wa'adinsa yã kasance mai aukuwa.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5494,
            "text": "Lalle ne, wannan wata tunãtarwa ce, sai dai wanda ya so, ya kãma hanyar ƙwarai zuwa ga Ubangijinsa.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5495,
            "text": "Lalle ne, Ubangijinka Yã sani cẽwa, kai kana tsayuwa a kusan kashi biyu daga uku na dare, da rabinsa, da sulusinsa, tãre da wani ɓangare na waɗanda ke tãre da kai. Allah Yake iya ƙaddara dare da yini, Ya san bã zã ku iya lissafa shi ba, sabõda haka Ya karɓi tubarku sai, ku karanta abin da ya sauƙaƙa na Alƙur'ãni. Ya san wasu daga cikinku, zã su yi cĩwo, kuma wasu zã su yi tafiya cikin ƙasa suna nẽman falalar Allah da fatauci, kuma wasu zã su yi yãƙi a cikin banyar Allah. Sabõda haka ku karanta abin da ya sauƙaƙa daga gare shi, kuma ku tsai da salla kuma ku bãyar da zakka, kumaa ku bai wa Allah rance, rance mai kyãwo. Kuma abin da kuka gabatar dõmin kanku, na alhẽri, zã ku sãme shi a wurin Allah zai kasance mafifici (daga wanda kuka ajiye) kuma zai fi girma ga sakamako, kuma ku rõƙi Allah gãfara; lalle ne Allah Mai gãfara ne, Mai jin ƙai.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 509,
            "hizbQuarter": 230,
            "sajda": false
          }
        ]
      },
      {
        "number": 74,
        "name": "سُورَةُ المُدَّثِّرِ",
        "englishName": "Al-Muddaththir",
        "englishNameTranslation": "The Cloaked One",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5496,
            "text": "Yã wanda ya lulluɓa da mayãfi.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5497,
            "text": "Ka tãshi dõmin ka yi gargaɗi",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5498,
            "text": "Kuma Ubangijinka, sai ka girmama Shi,",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5499,
            "text": "Kuma tutãfinka, sai ka tsarkake su,",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5500,
            "text": "Kuma gumãka, sai ka ƙaurace musu.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5501,
            "text": "Kada ka yi kyauta kana nẽman ƙãri",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5502,
            "text": "Kuma sabõda Ubangijinka? Sai ka yi haƙure",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5503,
            "text": "To, idan aka yi bũsa a cikin ƙaho.",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5504,
            "text": "To, wannan, a rãnar nan, yini ne mai wuya",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5505,
            "text": "A kan kãfirai, bã mai sauƙi ba ne.",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5506,
            "text": "Ka bar Ni da wanda Na halitta, yana shi kaɗai,",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5507,
            "text": "Kuma Na sanya masa dũkiya shimfiɗaɗɗiya",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5508,
            "text": "Da ɗiyã halartattu,",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5509,
            "text": "Kuma Na shimfiɗa? masa kõme shimfiɗãwa.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5510,
            "text": "Sa'an nan, yanã kwaɗayin in yi masa ƙãri!",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5511,
            "text": "Faufau! Lalle ne, shĩ yã kasance, ga ãyõyinMu, mai tsaurin kai.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5512,
            "text": "Zã Ni kallafa masa wahala mai hauhawa.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5513,
            "text": "Lalle ne, Shi, yã yi tunãni, kuma yã ƙaddara (abin da zai faɗã game da Alƙur'ãni)",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5514,
            "text": "Sabõda haka, aka la'ane shi, kamar yadda ya ƙaddara.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5515,
            "text": "Sa'an nan, aka la'ane shi kamar yadda ya ƙaddara.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5516,
            "text": "Sa'an nan, ya yi tunãni",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5517,
            "text": "Sa'an nan, ya gintse huska? kuma ya yi murtuk.",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5518,
            "text": "Sa'an nan, ya jũya bãya, kuma ya bunƙãsa,",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5519,
            "text": "Sai ya ce: 'Wannan abu dai bã kõme ba ne fãce wani sihiri, wanda aka ruwaito.'",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5520,
            "text": "'Wannan maganar mutum dai ce.'",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5521,
            "text": "Zã Ni ƙõna shi da Saƙar.",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5522,
            "text": "Kuma mẽ ya sanar da kai abin da akẽ cẽwa Saƙar!",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5523,
            "text": "Bã ta ragẽwa, kuma bã ta bari.",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5524,
            "text": "Mai nãcẽwa ga jiki ce (da (ƙũna).",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5525,
            "text": "A kanta akwai (matsara) gõma shã tara.",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5526,
            "text": "Kuma ba Mu sanya ma'abũta wutã (wãto matsaranta) ba, fãce malã'iku, kuma ba Mu sanya adadinsu (gõma sha tara) ba, fãce dõmin fitina ga waɗanda suka kãfirta domin waɗanda aka bai wa littãfi su sãmi yaƙĩni kuma waɗanda suka yi ĩmãni su ƙãra ĩmãni, kuma waɗanda aka bai wa littãfi da mũminai bã zã su yi shakka ba, kuma domin wa ɗanda a cikin zukatansu akwai wata cuta da kãfirai su ce: 'Mẽ Allah Yake nufi da wannan, ya kasance misãli?' Haka dai Allah ke ɓatar da wanda Ya so, kuma Ya shiryar da wanda ya so. Kuma bãbu wanda ya san mayãƙan Ubangijinka fãce Shi, kuma ita (wutar) ba ta kasance ba fãce wata tunãtarwa ce ga mutum.",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5527,
            "text": "A'aha! Ina rantsuwa da watã.",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5528,
            "text": "Da dare a lõkacin da ya jũyar da baya.",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5529,
            "text": "Da sãfiya idan ta wãye.",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5530,
            "text": "Ita (wutar) ɗayan manyan masĩfũ? ce.",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5531,
            "text": "Mai gargaɗĩ ce ga mutum.",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5532,
            "text": "Ga wanda ya so, daga cikinku, ya gabãta ko ya jinkirta.",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5533,
            "text": "Kõwane rai ga abin da ya aikata jingina ce.",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5534,
            "text": "Fãce mutãnen dãma.",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5535,
            "text": "A cikin Aljanna suna tambayar jũna.",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5536,
            "text": "Game da mãsu laifi.",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5537,
            "text": "(Su ce musu) 'Me ya shigar da ku a cikin Saƙar?'",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5538,
            "text": "Suka ce: 'Ba mu kasance munã a cikin mãsu salla ba.'",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5539,
            "text": "'Kuma ba mu kasance muna ciyar da matalautã ba.'",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5540,
            "text": "'Kuma mun kasance muna kũtsãwa tãre da mãsu kũtsãwa.'",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5541,
            "text": "'Mun kasance munã ƙaryata rãnar sãkamako.'",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5542,
            "text": "'Har gaskiya (wãto mutuwa) ta zo mana.'",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5543,
            "text": "Sabõda haka cẽton mãsu cẽto bã zai amfãne su ba.",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5544,
            "text": "Haba! Me ya same su, suka zama mãsu bijirewa daga wa'azin gaskiya.",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5545,
            "text": "Kamar dai sũ, jãkuna firgitattu ne.",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5546,
            "text": "Sun gudu daga zãki.",
            "numberInSurah": 51,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5547,
            "text": "A'aha! Kõwãne mutum daga cikinsu yanã son a zo masa da takardu (da sũnansa) ana wãtsãwa",
            "numberInSurah": 52,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5548,
            "text": "A'aha! Kai dai, bã su jin tsõron Lãhira.",
            "numberInSurah": 53,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5549,
            "text": "A'aha! Lalle ne, shi (Alƙur'ãni) tunãtarwa ce.",
            "numberInSurah": 54,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5550,
            "text": "Dõmin wanda ya so, ya tuna.",
            "numberInSurah": 55,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5551,
            "text": "Kuma bã zã su tuna ba fãce idan Allah Ya so, Shi ne Ya cancanta a bi Shi da taƙawa kuma Ya cancanta ga Ya yi gãfara.",
            "numberInSurah": 56,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          }
        ]
      },
      {
        "number": 75,
        "name": "سُورَةُ القِيَامَةِ",
        "englishName": "Al-Qiyaama",
        "englishNameTranslation": "The Resurrection",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5552,
            "text": "Bã sai Nã yi rantsuwa da Rãnar ¡iyãma ba.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5553,
            "text": "Bã sai Nã yi rantsuwa da rai mai yawan zargin kansa ba.",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5554,
            "text": "Sin, mutum yana zaton cẽwa bã zã Mu tãra ƙasusuwansa ba?",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5555,
            "text": "Na'am! Mãsu ĩkon yi Muke a kan Mu daidaita gaɓõɓin yãtsunsa.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5556,
            "text": "Ba haka ba! Mutum so yake, ya yi fãjirci, ya ƙaryata abin da yake a gabansa.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5557,
            "text": "Yanã tambaya: 'Yaushe ne Rãnar ¡iyãma?'",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5558,
            "text": "To, idan gani ya ɗimauta (ya yi ƙyalli).",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5559,
            "text": "Kuma, watã ya yi husũfi (haskensa ya dushe).",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5560,
            "text": "Aka tãra rãnã da watã",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5561,
            "text": "Mutum zai ce a rãn nan 'Ina wurin gudu?'",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5562,
            "text": "A'aha! bãbu mafaka.",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5563,
            "text": "zuwa ga Ubangijinka wurin tabbata, a rãnar nan, yake.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5564,
            "text": "Ana gayã wa mutum, a rãnar nan, abin da ya gabatar da wanda ya jinkirtar.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5565,
            "text": "Ba haka ba! Mutum, ga abin da ya shafi kansa, masani ne.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5566,
            "text": "Kuma ko da yã jẽfa uzurorinsa (bã zã a saurãre shi ba).",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5567,
            "text": "Kada ka mõtsar da harshenka game da shi dõmin ka yi gaugãwar riƙe shi (Alƙur'ãni).",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5568,
            "text": "Lalle ne, wãjibi ne a gare Mu, Mu tãra shi. Mu (tsare maka) karãtunsa.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5569,
            "text": "To idan Muka karanta shi sai ka bi karatunsa.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5570,
            "text": "sa'an nan, lalle wãjibi ne a gare Mu, bayãninsa.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5571,
            "text": "A'aha! Bã haka ba kunã son mai gaugawar nan (duniya) ne.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5572,
            "text": "Kunã barin ta ƙarshen (Lãhira).",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5573,
            "text": "Wasu huskõki, a rãnar nan, mãsu annuri ne.",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5574,
            "text": "Zuwa ga Ubangijinsu mãsu kallo ne.",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5575,
            "text": "Wasu huskõki, a rãnar nan, mãsu gintsẽwa ne.",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5576,
            "text": "Sunã zaton a sako musu masĩfa mai karya tsatso.",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5577,
            "text": "A'aha! Iadan (rai) ya kai ga karankarmai.",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5578,
            "text": "kuma aka ce: 'Wãne ne mai tawada?'",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5579,
            "text": "Kuma ya tabbata cẽwa rabuwa dai ce.",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5580,
            "text": "Kuma ƙwabri ya lauye da wani ƙwabri.",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5581,
            "text": "Zuwa ga Ubangijinka, a rãnar nan, magargaɗa take.",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5582,
            "text": "To, bai gaskatã ba, kuma bai yi salla ba!",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5583,
            "text": "Amma dai ya ƙaryata, kuma ya jũya baya!",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5584,
            "text": "Sa'an nan, ya tafi zuwa ga mutãnensa, yana tãƙama.",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5585,
            "text": "Halaka tã tabbata a gare ka, sa'an nan ita ce mafi dãcewa.",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5586,
            "text": "Sa'an nan, wata halaka tã tabbata a gare ka dõmin tã fi dãce wa.",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5587,
            "text": "Shin, mutum nã zaton a bar shi sagaga (wãto bãbu nufin kõme game da shi)?",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5588,
            "text": "Bai kasance iɗgo na maniyyi ba, wanda ake jefarwa (a cikin mahaifa)",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5589,
            "text": "Sa'an nan, ya zama gudan jini, sa'an nan Allah Ya halitta shi, sa'an nan Ya daidaita gaɓõɓinsa;",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5590,
            "text": "Sa'an nan, Ya sanya daga gare shi, nau'i biyu: namiji damace?",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5591,
            "text": "Ashẽ wannan bai zama Mai iko ba bisa ga rãyar da matattu?",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          }
        ]
      },
      {
        "number": 76,
        "name": "سُورَةُ الإِنسَانِ",
        "englishName": "Al-Insaan",
        "englishNameTranslation": "Man",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5592,
            "text": "Lalle ne, wata mudda ta zamani tã zo a kan mutum, bai kasance kõme ba wanda ake ambata.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5593,
            "text": "Lalle ne, Mũ Mun halitta mutum daga ɗigon ruwa garwayayye, Muna jarraba shi, sabõda haka Muka sanya shi mai ji mai gani,",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5594,
            "text": "Lalle ne, Mũ, Mun shiryar da shi ga hanyar ƙwarai, ko ya zama mai gõdiya, kuma ko ya zama mai kãfirci.",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5595,
            "text": "Lalle ne, Mũ, Mun yi tattali, dõmin kãfirai, sarƙoƙi da ƙuƙumma da sa'ĩr.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5596,
            "text": "Lalle ne, mutãnen kirki zã su sha daga finjãlin giya abin gaurayarta yã kãsance kãfur ne.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5597,
            "text": "Wani marmaro ne, daga gare shi bayin Allah suke sha, suna ɓuɓɓugar da shi ɓuɓɓugarwa.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5598,
            "text": "Suna cikãwa da alwãshin (da suka bãkanta), kuma suna tsõron wani yini wanda sharrinsa ya kasance mai tartsatsi ne.",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5599,
            "text": "Kuma suna ciyar da abinci, a kan suna bukãtarsa, ga matalauci da marãya da kãmamme.",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5600,
            "text": "(Suna cẽwa): 'Munã ciyar da ku ne dõmin nẽman yardar Allah kawai, bã mu nufin sãmun wani sakamako daga gare ku, kuma bã mu nufin gõdiya.",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5601,
            "text": "'Lalle ne, mũ muna tsõro daga Ubangijinmu, wani yini mai gintsẽwa, mai murtukẽwa.'",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5602,
            "text": "Sabõda haka Allah Ya tsare musu sharrin wannan yini, kuma Ya hlɗa su da annũrin huska da farin ciki,",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5603,
            "text": "Kuma Ya sãka musu sabõda haƙurin da suka yi, da Aljanna da tufãfin alharini.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5604,
            "text": "Sunã mãsu zaman ginciri, a cikinta, a kan karagu, bã su ganin rãnã a cikinta, kuma bã su ganin jaura.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5605,
            "text": "Kuma, inuwõyinta suna kusa, a kansu, an hõre nunannun 'yã'yan itãcenta, hõrẽwa.",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5606,
            "text": "Kuma ana kẽwayãwa a kansu da finjãlai na azurfa da kofuna waɗanda suka kasance na ƙarau.",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5607,
            "text": "¡arau na azurfa, sun ƙaddara su ƙaddarawa, daidai bukãta.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5608,
            "text": "Ana shayarwa da su, a cikinta, finjalan giya, wadda abin gaurayarta ya kasance zanjabil ne.",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5609,
            "text": "Wani marmaro ne, a cikinta, ana kiran sa salsabil.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5610,
            "text": "Kuma wasu yara samãrin dindindin na kẽwayãwa, a kansu, idan ka gan su, zã ka zaci sũ lu'ulu'u ne wanda aka wãtsa.",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5611,
            "text": "Kuma idan kã ga wannan wurin, to, kã ga wata irin ni'ima da mulki babba.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5612,
            "text": "Tufãfinsu na sama na alharĩ ni ne, kõre da mai walƙiya, kuma an ƙawãce su da mundãye na wata curin azurfa kuma Ubangijinsu, Ya shãyar da su abin sha mai tsarkakẽwar (ciki).",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5613,
            "text": "(A ce musu) 'Lalle ne, wannan ya kasance, a gare ku sakamako, kuma aikink. ya kasance abin gõdewa.'",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5614,
            "text": "Lalle, Mũ ne Muka saukar da Alƙur'ãni a gare ka, saukarwa.",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5615,
            "text": "Sabõda haka ka yi haƙuri ga hukuncin Ubangijinka, kuma kada ka bi, daga cikinsu, mai zunubi ko mai kãfirci.",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5616,
            "text": "Kuma ka ambaci sũnan Ubangijinka, sãfe da maraice.",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5617,
            "text": "Daga dare, sai ka yi sujũda gare Shi, kuma ka tsarkake Shi darẽ mai tsawo.",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5618,
            "text": "Lalle ne, waɗannan suna son mai gaugãwa (dũniya) kuma suna bari, a bãyansu, wani yini mai nauyi.",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5619,
            "text": "Mũ ne Muka halitta su kuma Muka, ƙarfafa halittarsu, kuma idan Mun so, za Mu musanyâ su da wasu mutane kwatankwacinsu, musanyawa.",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5620,
            "text": "Lalle ne, wannan wata tunãtarwa ce, saboda wanda ya so ya riƙa hanyar kirki zuwa ga Ubangijinsa.",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5621,
            "text": "Kuma, bã zã ku so ba, sai Allah Ya so, lalle ne Allah Yã kasance Masani' Mai hikima.",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5622,
            "text": "Yana shigar da wanda Ya so a cikin rahamarsa, kuma azzãlumai, Yã yi musu tattalin wata azãba mai raɗaɗi.",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          }
        ]
      },
      {
        "number": 77,
        "name": "سُورَةُ المُرۡسَلَاتِ",
        "englishName": "Al-Mursalaat",
        "englishNameTranslation": "The Emissaries",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5623,
            "text": "Ina rantsuwa da iskõkin da ake sakõwa jẽre, sunã bin jũna.",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5624,
            "text": "Sa'an nan, su zamã iskõki mãsu ƙarfi suna kaɗãwa da ƙarfi.",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5625,
            "text": "Kuma, mãsu watsa rahama wãtsãwa.",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5626,
            "text": "sa'an nan, da ãyõyi mãsu rarrabe gaskiya da ƙarya rarrabẽwa.",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5627,
            "text": "Sa'an nan da malã'iku mãsu jẽfa tunãtarwa ga Manzanni.",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5628,
            "text": "Domin yanke hamzari ko dõmin gargadi.",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5629,
            "text": "Lalle ne, abin da ake yi muku wa'adi da shi tabbas mai aukuwa ne",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5630,
            "text": "To, idan taurãri aka shãfe haskensu.",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5631,
            "text": "Kuma, idan sama aka tsãge ta.",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5632,
            "text": "Kuma, idan duwãtsu aka nike su.",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5633,
            "text": "Kuma, idan manzanni aka ƙayyade lõkacin tãra su.",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5634,
            "text": "Domin babbar rãnar da aka yi wa ajali.",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5635,
            "text": "Domin rãnar rarrabẽwa.",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5636,
            "text": "Kuma, me ya sanar da kai rãnar rarrabẽwa?",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5637,
            "text": "Bone yã tabbata a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5638,
            "text": "Ashe, ba Mu halakar da (mãsu ƙaryatãw) na farko ba.",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5639,
            "text": "Sa'an nan, kuma zã Mu biyar musu da na ƙarshe?",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5640,
            "text": "Haka nan, Muke aikatãwa da mãsu yin laifi.",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5641,
            "text": "Bone yã tabbata a rãnar nan ga mãsu ƙaryatãwa!",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5642,
            "text": "Ashe, ba Mu halitta ku daga wani ruwa wulakantacce ba.",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5643,
            "text": "Sa'an nan Muka sanya shi a cikin wani wurin natsuwa amintacce.",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5644,
            "text": "Zuwa ga wani gwargwadon mudda sananna.",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5645,
            "text": "Sa'an nan, Muka nũna iyãwarMu? Madalla da Mu, Mãsu nũna iyãwa.",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5646,
            "text": "Bone ya tabbata a rãnar nan ga mãsu ƙaryatãwa!",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5647,
            "text": "Ashe, ba Mu sanya ƙasa matattarã ba.",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5648,
            "text": "Ga rãyayyu da matattu,",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5649,
            "text": "Kuma, Muka sanya, a cikinta, kafaffun duwãtsu maɗaukaka, kuma Muka shayar da ku ruwa mai dãɗi?",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5650,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5651,
            "text": "Ku tafi zuwa ga abin da kuka kasance kuna ƙaryatãwa game da shi!",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5652,
            "text": "Ku tafi zuwa ga wata inuwa mai rassa uku.",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5653,
            "text": "Ita ba inuwar ba, kuma ba ta wadãtarwa daga harshen wuta.",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5654,
            "text": "Lalle ne, ita, tanã jifã da tartsatsi kamar sõraye.",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5655,
            "text": "Kamar dai su raƙumma ne baƙãƙe.",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5656,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5657,
            "text": "Wannan, yini ne da bã zã su iya yin magana ba.",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5658,
            "text": "Bã zã a yi musu izni ba, balle su kãwo hanzari.",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5659,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5660,
            "text": "Wannan rãnar rarrabẽwa ce, Mun tattara ku tare da mutãnen farko.",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5661,
            "text": "To, idan akwai wata dabara gare ku, sai ku yi ma Ni ita.",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5662,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5663,
            "text": "Lalle ne, mãsu taƙawa suna a cikin inuwõwi da marẽmari.",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5664,
            "text": "Da wasu 'ya'yan itãce irin waɗanda suke marmari.",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5665,
            "text": "(A ce musu) 'Ku ci ku sha cikin ni'ima sabõda abin da kuka kasance kuna aikatãwa.'",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5666,
            "text": "Lalle ne, Mũ haka Muke sãka wa mãsu kyautatãwa.",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5667,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5668,
            "text": "(Ana ce musu) 'Ku ci ku ji ɗan dãdi kaɗan, lalle ne dai ku mãsu laifi ne.'",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5669,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5670,
            "text": "Kuma, idan an ce musu: 'Ku yi rukũ'i; bã zã su yi rukũ'in (salla) ba.'",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5671,
            "text": "Bone ya tabbata, a rãnar nan, ga mãsu ƙaryatãwa!",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5672,
            "text": "To, da wane lãbãri (Littãfi), waninsa (Alƙur'ãni) zã su yi ĩmãni",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          }
        ]
      },
      {
        "number": 78,
        "name": "سُورَةُ النَّبَإِ",
        "englishName": "An-Naba",
        "englishNameTranslation": "The Announcement",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5673,
            "text": "A kan mẽ suke tambayar jũna?",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5674,
            "text": "A kan muhimmin lãbãri mai girma (Alƙur'ãni)?",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5675,
            "text": "Wanda suke sãɓa wa jũna a cikinsa?",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5676,
            "text": "A'aha! Zã su sani.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5677,
            "text": "Kuma, a'aha! Zã su sani.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5678,
            "text": "Ashe, ba Mu sanya ƙasa shimfiɗa ba?",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5679,
            "text": "Da duwãtsu turaku (ga riƙe ƙasa)?",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5680,
            "text": "Kuma, Mun halitta ku mazã da mãtã?",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5681,
            "text": "Kuma, Muka sanya barcinku hũtãwa?",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5682,
            "text": "Kuma, Muka sanya dare (ya zama) sutura?",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5683,
            "text": "Kuma, Muka sanya yini (yazama) lõkacin nẽman abinci?",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5684,
            "text": "Kuma, Muka gina, a samanku, sammai bakwai mãsu ƙarfi?",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5685,
            "text": "Kuma, Muka sanya fitila mai tsanin haske (rãnã)?",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5686,
            "text": "Kuma, Muka saukar daga cikakkun girãgizai, ruwa mai yawan zuba?",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5687,
            "text": "Dõmin, Mu fitar da ƙwaya da tsiri game da shi?",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5688,
            "text": "Da itãcen lambuna mãsu lillibniya?",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5689,
            "text": "Lalle ne, rãnar rarrabẽwa tã kasance abin ƙayyadẽ wa lõkaci.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5690,
            "text": "Rãnar da zã a yi bũsa a cikin ƙaho, sai ku zo, jama'a jama'a.",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5691,
            "text": "Kuma, aka buɗe sama, sai ta kasance ƙõfõfi.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5692,
            "text": "Kuma, aka tafiyar da duwãtsu, sai suka kasance ƙũra.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5693,
            "text": "Lalle ne, Jahannama tã kasance madãkata.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5694,
            "text": "Ga mãsu ƙẽtare iyãkõki, tã zama makõma.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5695,
            "text": "Sunã, mãsu zama a cikinta, zãmunna.",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5696,
            "text": "Bã su ɗanɗanãwar wani sanyi a cikinta, kuma bã su ɗanɗana abin sha.",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5697,
            "text": "Fãce tafasasshen ruwa da ruɓaɓɓen jini.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5698,
            "text": "Sakamako mai dãcẽwa.",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5699,
            "text": "Lalle ne, sũ, sun kasance bã su fãtar sauƙin wani hisãbi.",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5700,
            "text": "Kuma, suka ƙaryata game da ãyõyinMu, ƙaryatãwa!",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5701,
            "text": "Alhãli, kõwane abu Mun ƙididdigẽ shi, a rubũce.",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5702,
            "text": "Sabõda haka, ku ɗanɗana domin haka, bã zã Mu ƙara muku kõme ba fãce azãba.",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5703,
            "text": "Lalle ne, mãsu taƙawã nã da wani wurin sãmun babban rabo.",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5704,
            "text": "Lambuna da inabõbi.",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5705,
            "text": "Da cikakkun 'yammata, tsãrar jũna.",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5706,
            "text": "Da hinjãlan giya cikakku.",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5707,
            "text": "Bã su jin yãsassar magana, a cikinta, kuma bã su jin ƙaryatãwa.",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5708,
            "text": "Dõmin sakamako daga Ubangijinka, kyautã mai yawa.",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5709,
            "text": "Ubangijin sammai da ƙasa da abin da yake a tsakãninsu, Mai rahama, bã su da ikon yin wata magana daga gare Shi.",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5710,
            "text": "Rãnar da Rũhi da malã'iku zã su tsaya a cikin safu, bã su magana, sai wanda Allah Ya yi masa izni, kuma ya faɗi abin da ke daidai.",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5711,
            "text": "Wancan, shi ne yini na gaskiya; to wanda ya so, ya riƙa makõma zuwa ga Ubangijinsa.",
            "numberInSurah": 39,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5712,
            "text": "Lalle ne, Mũ, Mun yi muku gargaɗin azãba makusanciya, rãnar da mutum ke dũbi zuwa ga abin da hannãyensa suka aikata, kuma kafiri ya ce: 'Kaitona, dã dai nã zama turɓãya!'",
            "numberInSurah": 40,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          }
        ]
      },
      {
        "number": 79,
        "name": "سُورَةُ النَّازِعَاتِ",
        "englishName": "An-Naazi'aat",
        "englishNameTranslation": "Those who drag forth",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5713,
            "text": "Ina rantsuwa da mala'iku mãsu fisgar rãyuka (na kafirai) da ƙarfi.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5714,
            "text": "Da mãsu ɗibar rãyuka (na mũminai) da sauƙi a cikin nishãɗi.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5715,
            "text": "Da mãsu sauka daga sama da umurnin Allah kamar suna iyo.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5716,
            "text": "Sa'an nan, su zama mãsu gaugãwa (da umurnin Allah) kamar suna tsẽre.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5717,
            "text": "Sa, an nan, su kasance masu shirya gudanar da umurni.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5718,
            "text": "Rãnar da mai girgiza abũbuwa (bũsar farko) zã ta kaɗa.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5719,
            "text": "Mai biyar ta (bũsa ta biyu) nã biye.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5720,
            "text": "Wasu zukãta, a rãnar nan, mãsu jin tsõro ne.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5721,
            "text": "Alhãli idãnunsu na ƙasƙantattu.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5722,
            "text": "Sunã cẽwa 'Ashe lalle zã a iya mayar da mu a kan sãwunmu?",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5723,
            "text": "'Ashe, idan muka zama ƙasusuwa rududdugaggu?'",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5724,
            "text": "Suka ce: 'Waccan kam kõmãwa ce, tãɓaɓɓiya!'",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5725,
            "text": "To, ita kam, tsãwa guda kawai ce.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5726,
            "text": "Sai kawai gã su a bãyan ƙasa.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5727,
            "text": "Shin, lãbãrin Mũsã ya zo maka?",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5728,
            "text": "A lõkacin da Ubangijinsa Ya kirãye shi, a cikin kwari mai tsarki, wato Duwã?",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5729,
            "text": "Ka tafi zuwa ga Fir'auna, lalle ne shi, ya ƙẽtare haddi.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5730,
            "text": "'Sai ka ce masa, Kõ zã ka so ka tsarkaka.",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5731,
            "text": "'Kuma in shiryar da kai zuwa ga Ubangijinka domin ka ji tsoronSa?'",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5732,
            "text": "Sai ya nũna masa ãyar nan mafi girma.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5733,
            "text": "Sai ya ƙaryata, kuma ya sãɓa (umurni),",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5734,
            "text": "Sa'an nan ya jũya bãya, yanã tafiya da sauri.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5735,
            "text": "Sai ya yi gayya, sa'an nan ya yi kira.",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5736,
            "text": "Sai ya ce: 'Nĩ ne Ubangijinku mafi ɗaudaka.'",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5737,
            "text": "Sabõda haka Allah Ya kama shi, dõmin azãbar maganar ƙarshe da ta farko.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5738,
            "text": "Lalle ne, a cikin wannan haƙiƙa akwai abin kula ga wanda yake tsõron Allah.",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5739,
            "text": "Shin, kũ ne mafi wuyar halitta ko sama? Allah Ya gina ta.",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5740,
            "text": "Ya ɗaukaka rufinta, sa'an nan Ya daidaita ta.",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5741,
            "text": "Kuma Ya duhuntar da darenta, kuma Ya fitar da hantsinta.",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5742,
            "text": "Kuma, ƙasa a bayan haka Ya mulmula ta.",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5743,
            "text": "Ya fitar da ruwanta daga gare ta da makiyãyarta.",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5744,
            "text": "Da duwatsu, Yã kafe ta.",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5745,
            "text": "Domin jiyarwa dãɗi a gare ku, kuma ga dabbõbinku.",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5746,
            "text": "To, idan uwar masĩfu, mafi girma, ta zo.",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5747,
            "text": "Rãnar da mutum zai yi tunãnin abin da ya aikata.",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5748,
            "text": "Kuma, a bayyana Jahĩm ga mai gani.",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5749,
            "text": "To, amma wanda ya yi girman kai.",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5750,
            "text": "Kuma, ya zãɓi rãyuwa ta kusa, (wato dũniya).",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5751,
            "text": "To, lalle ne Jahĩm, ita ce makõma.",
            "numberInSurah": 39,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5752,
            "text": "Kuma, amma wanda ya ji tsõron tsayi a gaba ga Ubangijinsa, kuma ya kange kansa daga son rai.",
            "numberInSurah": 40,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5753,
            "text": "To, lalle ne Aljanna ita ce makõma.",
            "numberInSurah": 41,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5754,
            "text": "Sunã tambayar ka game da sa'a, wai yaushe ne matabbatarta?",
            "numberInSurah": 42,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5755,
            "text": "Me ya haɗã ka da ambatonta?",
            "numberInSurah": 43,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5756,
            "text": "Zuwa ga Ubangijinka ƙarshen al'amarinta yake.",
            "numberInSurah": 44,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5757,
            "text": "Kai mai gargaɗi kawai ne ga mai tsõron ta.",
            "numberInSurah": 45,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5758,
            "text": "Kamar sũ a rãnar da zã su gan ta, ba su zauna ba fãce a lõkacin marẽce ko hantsinsa.",
            "numberInSurah": 46,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          }
        ]
      },
      {
        "number": 80,
        "name": "سُورَةُ عَبَسَ",
        "englishName": "Abasa",
        "englishNameTranslation": "He frowned",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5759,
            "text": "Yã game huska kuma ya jũya bãya.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5760,
            "text": "Sabõda makãho yã je masa.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5761,
            "text": "To, me ya sanar da kai cẽwa watakila shi ne zai tsarkaka.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5762,
            "text": "Ko ya tuna, dõmin tunãwar ta amfane shi?",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5763,
            "text": "Amma wanda ya wadãtu da dũkiya.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5764,
            "text": "Sa'an nan kai kuma ka ɗora bijira zuwa gare shi!",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5765,
            "text": "To, me zai cũce ka idan bai tsarkaka ba?",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5766,
            "text": "Kuma, amma wanda ya zomaka yana gaugãwa.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5767,
            "text": "Alhãli shĩ yanã jin tsõrõn Allah.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5768,
            "text": "Kai kuma kã shagala ga barinsa!",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5769,
            "text": "A'aha! Lalle ne, wannan tunãtarwa ce.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5770,
            "text": "Sabõda wanda ya so ya tunaShi (Allah).",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5771,
            "text": "(Tunãtarwa ce) ta cikin littafai abãban girmamãwa,",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5772,
            "text": "Abãban ɗaukakãwa, abãban tsarkakẽwa.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5773,
            "text": "A cikin hannãyen mala'iku marubũta.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5774,
            "text": "Mãsu daraja, mãsu ɗã'a ga Allah.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5775,
            "text": "An la'ani mutum (kafiri). Mẽ yã yi kãfircinsa!",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5776,
            "text": "Daga wane abu, (Allah) Ya halitta shi?",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5777,
            "text": "Daga ɗigon maniyyi, Ya halitta shi sa'an nan Ya ƙaddarã shi (ga halaye).",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5778,
            "text": "Sa'an nan, hanyarsa ta fita Ya sauƙaƙe masa.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5779,
            "text": "Sa'an nan, Ya matar da shi sai Ya sanya shi a cikin kabari.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5780,
            "text": "Sa'an nan, idan Ya so lalle ne zai tãyar da shi.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5781,
            "text": "Haƙĩƙa bai i da aikata abin da Allah Ya umurce shi ba (lõkacin sanya shi a cikin kabari).",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5782,
            "text": "To, mutum ya dũba zuwa ga abincinsa.",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5783,
            "text": "Lalle ne Mũ, Mun zuo ruwa, zubõwa.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5784,
            "text": "Sa'an nan, Muka tsattsãge ƙasa tsattsagewa.",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5785,
            "text": "Sa'an nan, Muka tsirar da ƙwaya, a cikinta.",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5786,
            "text": "Da inabi da ciyãwa.",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5787,
            "text": "Da zaitũni da itãcen dabĩno.",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5788,
            "text": "Da lambuna, mãsu yawan itãce.",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5789,
            "text": "Da 'yã'yan itãcen marmari, da makiyãyã ta dabbõbi.",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5790,
            "text": "Domin jin dãɗi a gare ku, ku da dabbobinku.",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5791,
            "text": "To, idan mai tsãwa (busa ta biyu) ta zo.",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5792,
            "text": "Rãnar da mutum yake gudu daga ɗan'uwansa.",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5793,
            "text": "Da uwarsa da ubansa.",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5794,
            "text": "Da mãtarsa da ɗiyansa.",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5795,
            "text": "Ga kõwane mutum daga cikinsu, a rãnar nan akwai wani sha'ani da ya ishe shi.",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5796,
            "text": "Wasu huskõki, a rãnar nan, mãsu haske ne.",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5797,
            "text": "Mãsu dãriya ne, mãsu bushãra.",
            "numberInSurah": 39,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5798,
            "text": "Wasu huskõki, a rãnar nan, akwai ƙũra a kansu.",
            "numberInSurah": 40,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5799,
            "text": "Baƙi zai rufe su.",
            "numberInSurah": 41,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5800,
            "text": "Waɗannan sũ ne kãfirai fãjirai (ga ayyukansu).",
            "numberInSurah": 42,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          }
        ]
      },
      {
        "number": 81,
        "name": "سُورَةُ التَّكۡوِيرِ",
        "englishName": "At-Takwir",
        "englishNameTranslation": "The Overthrowing",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5801,
            "text": "Idan rãna aka shafe haskenta",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5802,
            "text": "Kuma idan taurãri suka gurɓãce (wani ya shiga a cikin wani).",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5803,
            "text": "Kuma idan duwãtsu aka tafiyar da su.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5804,
            "text": "Kuma idan rãƙuma mãsu cikunna aka sakẽ su wãwai, bã ga kõwa ba.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5805,
            "text": "Kuma idan dabbõbin dãji aka tattara su.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5806,
            "text": "Kuma idan tẽkuna aka mayar da su wuta.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5807,
            "text": "Kuma idan rãyuka aka haɗa su da jikunkunansu.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5808,
            "text": "Kuma idan wadda aka turbuɗe ta da rai aka tambaye ta.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5809,
            "text": "'Sabõda wane laifi ne aka kashe ta?'",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5810,
            "text": "Idan takardun ayyuka aka wãtsa su (ga mãsu su).",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5811,
            "text": "Kuma idan sama aka fẽɗe ta.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5812,
            "text": "Kuma idan Jahĩm aka hũra ta",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5813,
            "text": "Kuma idan Aljanna aka kusantar da ita.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5814,
            "text": "Rai ya san abin da ya halartar (a rãnar nan).",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5815,
            "text": "To, ba sai Na yi rantsuwa da taurãri matafã ba.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5816,
            "text": "Mãsu gudu suna ɓũya.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5817,
            "text": "Da dare idan ya bãyar da bãya.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5818,
            "text": "Da sãfiya idan ta yi lumfashi.",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5819,
            "text": "Lalle ne shi (Alƙur'ãni) haƙƙan, maganar wani manzon (Allah) ne mai girma ga Allah.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5820,
            "text": "Mai ƙarfi, mai daraja a wurin Mai Al'arshi.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5821,
            "text": "Wanda ake yi wa ɗã'a (wato shugaban malã'iku) ne a can, amintacce.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5822,
            "text": "Kuma abokinku ba mahaukaci ba ne.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5823,
            "text": "Kuma lalle ne, yã gan shi a cikin sararin sama mabayyani.",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5824,
            "text": "Kuma shi, ga gaibi bã mai rowa ba ne.",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5825,
            "text": "Kuma shi (Alƙur'ani) bã maganar shaiɗani, wanda aka la'ana, ba ce.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5826,
            "text": "Shin, a inã zã ku tafi?",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5827,
            "text": "Lalle ne shi (Alƙur'ãni), bã kõme ba ne fãce gargaɗi ga talikai.",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5828,
            "text": "Ga wanda ya so, daga cikinku, ya shiryu.",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5829,
            "text": "Kuma bã zã ku so ba sai idan Allah Ubangijin halitta Yã yarda.",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          }
        ]
      },
      {
        "number": 82,
        "name": "سُورَةُ الانفِطَارِ",
        "englishName": "Al-Infitaar",
        "englishNameTranslation": "The Cleaving",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5830,
            "text": "Idan sama ta tsãge.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5831,
            "text": "Kuma idan taurãri suka wãtse.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5832,
            "text": "Kuma idan tẽkuna aka facce su.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5833,
            "text": "Kuma idan kaburbura aka tõne su.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5834,
            "text": "Rai yã san abin da ya gabatar, da abin da yã jinkirtar.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5835,
            "text": "Yã kai mutum! Mẽ ya rũɗe ka game da Ubangijinka, Mai karimci.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5836,
            "text": "Wanda Ya halitta ka sa'an nan ya daidaitã ka, Ya kuma tsakaitã ka.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5837,
            "text": "A cikin kõwace irin sũra Ya so Ya ginã ka a kanta.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5838,
            "text": "A'aha, bã haka ba, kuna ƙaryatãwa game da sakamako!",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5839,
            "text": "Lalle ne, a kanku, haƙiƙa akwai matsara.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5840,
            "text": "Mãsu daraja, marubũta.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5841,
            "text": "Sunã sanin abin da kuke aikatãwa.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5842,
            "text": "Lalle ne, mãsu ɗã'ã ga Allah, dãhir, suna cikin ni'ima.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5843,
            "text": "Kuma lalle ne, fãjirai, dãhir, sunã cikin Jahĩm.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5844,
            "text": "Zã su shigẽ ta a rãnar sakamako.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5845,
            "text": "Bã zã su faku daga gare ta ba.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5846,
            "text": "Kuma mẽ ya sanar da kai abin da ake cẽ wa rẽnar sakamako?",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5847,
            "text": "Sa'an nan, mẽ ya sanar da kai abin da ake cẽ wa rãnar sakamako?",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5848,
            "text": "Rãnã ce da wani rai ba ya iya mallakar kõme dõmin wani rai al'amari, a rãnar nan, ga Allah (ɗai) yake.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          }
        ]
      },
      {
        "number": 83,
        "name": "سُورَةُ المُطَفِّفِينَ",
        "englishName": "Al-Mutaffifin",
        "englishNameTranslation": "Defrauding",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5849,
            "text": "Bone ya tabbata ga mãsu naƙƙasãwa.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5850,
            "text": "Waɗanda suke idan suka auna daga mutãne suna cika mũdu.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5851,
            "text": "Kuma idan sun auna musu da zakka ko da sikẽli, suna ragẽwa",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5852,
            "text": "Ashe! Waɗancan bã su tabbata cẽwa lalle sũ, ana tãyar da su ba?",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5853,
            "text": "Domin yini mai girma.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5854,
            "text": "Yinin da mutãne ke tãshi zuwa ga Ubangijin halitta?",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5855,
            "text": "Haƙĩƙa lalle ne littãfin fãjirai dãhir, yana a cikin Sijjĩn.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5856,
            "text": "Kuma, mẽ ya sanar da kai abin da akẽ cẽ wa Sijjĩn?",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5857,
            "text": "Wani 1ittãfi ne rubũtacce.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5858,
            "text": "Bone yã tabbata a rãnar nan ga mãsu ƙaryatãwa.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5859,
            "text": "Waɗanda suke ƙaryatãwa game da rãnar sakamako.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5860,
            "text": "Babũ mai ƙaryatãwa gare shi fãce dukan mai ƙẽtare haddi mai yawan zunubi.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5861,
            "text": "Idan ana karãtun ãyõyinmu, a kansa, sai ya ce: tãtsũniyõyin mutãnen farko ne.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5862,
            "text": "A'aha! Bã haka ba, abin da suka kasance suna aikatãwa dai, yã yi tsãtsa a cikin zukãtãnsu.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5863,
            "text": "A'aha! Haƙĩƙa, lalle ne sũ daga Ubangijinsu, rãnar nan, waɗanda ake shãmakancẽwa ne.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5864,
            "text": "Sa'an nan, lalle ne, sũ mãsu shiga cikin Jahĩm ne.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5865,
            "text": "Sa'an nan a ce: 'Wannan shi ne abin da kuka kasance kuna ƙaryatãwa game da shi.'",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5866,
            "text": "A'aha! Haƙĩƙa lalle ne littãfin mãsu ɗã'ã yana a cikin Illiyyĩna?",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5867,
            "text": "Kuma mẽne ne yã sanar da kai abin da ake cẽwa Illiyyũna?",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5868,
            "text": "Wani littãfi ne rubũtacce.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5869,
            "text": "Muƙarrabai suke halarta shi.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5870,
            "text": "Lalle ne, mãsu ɗã'ã ga Allah tabbas suna cikin ni'ima.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5871,
            "text": "A kan karagu, suna ta kallo.",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5872,
            "text": "Kana sani a cikin huskõkinsu, akwai kwarjinin ni'ima.",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5873,
            "text": "Ana shãyar da sũ daga wata giya wadda aka yunƙe a kan rufinta.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5874,
            "text": "¡arshen kurɓinta miski ne. To, a cikin wannan, mãsu gwagwarmaya su yi ta gwagwarmayar nẽma.",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5875,
            "text": "Kuma abin da ake gauraya ta da shi, daga tasnĩm yake.",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5876,
            "text": "(Wato) wani marmaro ne wanda muƙarrabai ke sha daga gare shi.",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5877,
            "text": "Lalle ne, waɗanda suka kãfirta sun kasance (a dũniya) suna yi wa waɗanda suka yi ĩmãni dãriya.",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5878,
            "text": "Kuma idan sun shuɗe su sai su dinga yin zunɗe.",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5879,
            "text": "Kuma idan suka jũya zuwa ga iyãlansu, sai su tafi suna kãkãci.",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5880,
            "text": "Kuma idan sun gan su sai su ce: 'Lalle waɗannan ɓatattu ne.'",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5881,
            "text": "Alhãli kuwa, ba a aike su ba dõmin su zama mãsu tsaro a kansu.",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5882,
            "text": "To, yau fa (a Lãhira] waɗanda suka yi ĩmãni, sũ ke yi wa kãfirai dãriya.",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5883,
            "text": "A kan karagu, suna ta kallo.",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5884,
            "text": "Shin an sãka ma kãfirai abin da suka kasance suna aikatawa?",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          }
        ]
      },
      {
        "number": 84,
        "name": "سُورَةُ الانشِقَاقِ",
        "englishName": "Al-Inshiqaaq",
        "englishNameTranslation": "The Splitting Open",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5885,
            "text": "Idan sama ta kẽce,",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5886,
            "text": "Ta saurari Ubangijinta, kuma aka wajabta mata yin saurãron,",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5887,
            "text": "Kuma idan ƙasã aka mĩƙe ta,",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5888,
            "text": "Kuma ta jẽfar da abin da yake a cikinta, tã wõfinta daga kõme.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5889,
            "text": "Kuma ta saurãri Ubangijinta, aka wajabta mata yin saurãren,",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5890,
            "text": "Ya kai mutum! Lalle ne kai mai aikin wahal da kai ne zuwa ga Ubangijinka, wahala mai tsanani, To, kai mai haɗuwa da Shi ne.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5891,
            "text": "To, amma wanda aka bai wa littãfinsa da damansa.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5892,
            "text": "To, za a yi masa hisãbi, hisãbi mai sauƙi.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5893,
            "text": "Kuma ya jũya zuwa ga iyãlinsa (a cikin Aljanna), yanã mai raha.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5894,
            "text": "Kuma amma wanda aka bai wa littãfinsa, daga wajen bãyansa.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5895,
            "text": "To, zã shi dinga kiran halaka!",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5896,
            "text": "Kuma ya shiga sa'ĩr.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5897,
            "text": "Lal1e ne shi, yã kasance (a dũniya) cikin iyãlinsa yanã mai raha.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5898,
            "text": "Lalle ne yã yi zaton bã zai kõmo ba.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5899,
            "text": "Na'am! Lalle ne, Ubangijinsa Ya kasance Mai gani gare shi.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5900,
            "text": "To, ba sai Nã rantse da shafaƙi ba.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5901,
            "text": "Da dare, da abin da ya ƙunsa.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5902,
            "text": "Da watã idan (haskensa) ya cika.",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5903,
            "text": "Lalle ne kunã hawan wani hãli daga wani hãli.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5904,
            "text": "To, mẽ ya sãme su, ba su yin ĩmãni?",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5905,
            "text": "Kuma idan an karanta Alkur'ãni a kansu, bã su yin tawãli'u?",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": {
              "id": 14,
              "recommended": true,
              "obligatory": false
            }
          },
          {
            "number": 5906,
            "text": "Ba haka ba! waɗanda suka kãfirta, sai ƙaryatãwa suke yi.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5907,
            "text": "Alhãli Allah Shĩ ne Mafi sani ga abin a suke tãrãwa.",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5908,
            "text": "Saboda haka, ka yi musu bushãra da azãba mai raɗaɗi.",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5909,
            "text": "Fãce waɗanda suka yi ĩmãni, suka aikata ayyukan ƙwarai, sunã da wani sakamako wanda bã ya yankẽwa.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          }
        ]
      },
      {
        "number": 85,
        "name": "سُورَةُ البُرُوجِ",
        "englishName": "Al-Burooj",
        "englishNameTranslation": "The Constellations",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5910,
            "text": "Inã rantsuwa da sama mai taurãrin lissafin shekara.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5911,
            "text": "Da yinin da aka yi alkawarin zuwansa,",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5912,
            "text": "Da yini mai shaidu, da yini da ake halarta a cikinsa",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5913,
            "text": "An la'ani mutãnen rãmi.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5914,
            "text": "Wato wuta wadda aka hura.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5915,
            "text": "A lõkacin da suke a kan (gefen) ta a zazzaune.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5916,
            "text": "Alhãli sũ, bisa ga abin da suke aikatãwa ga mũminai, sunã halarce.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5917,
            "text": "Kuma ba su tuhumce su ba, fãce kawai domin sun yi ĩmãni da Allah Mabuwãyi, wanda ake gõdewa.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5918,
            "text": "Wanda Yake shi ne da mulkin sammai da ƙasa, kuma Allah a kan kõme halarce Yake.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5919,
            "text": "Lalle ne waɗanda suka fitini mũminai maza da mũminai mãta, sa'an nan ba su tũba ba to, sunã da azãbar Jahannama, kuma sunã da azãbar gõbara.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5920,
            "text": "Lalle ne waɗanda suka yi ĩmãni, kuma suka aikata ayyukan ƙwarai, sunã da gidãjen Aljanna, ƙoramu na gudãna daga ƙarƙashin gidãjen. Wancan abu fa shi ne rabo babba.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5921,
            "text": "Lalle ne damƙar Ubangijinka mai tsanani ce ƙwarai.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5922,
            "text": "Lalle ne Shĩ, Shi ne Mai ƙãga halitta, kuma Ya mayar da ita (bãyan mutuwa).",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5923,
            "text": "Kuma Shi ne Mai gãfara, Mai bayyana sõyayya.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5924,
            "text": "Mai Al'arshi mai girma",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5925,
            "text": "Mai aikatãwa ga abin da Yake nufi.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5926,
            "text": "Ko lãbãrin rundanõni yã zo maka.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5927,
            "text": "Fir'auna da samũdãwa?",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5928,
            "text": "Ã'aha! waɗanda suka kãfirta sunã cikin ƙaryatãwa.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5929,
            "text": "Alhãli, Allah daga bãyansu, Mai kẽwaye su ne (da saninSa).",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5930,
            "text": "Ã'aha! Shi Alƙur'ãni ne mai girma.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5931,
            "text": "A cikin Allo tsararre.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          }
        ]
      },
      {
        "number": 86,
        "name": "سُورَةُ الطَّارِقِ",
        "englishName": "At-Taariq",
        "englishNameTranslation": "The Morning Star",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5932,
            "text": "Inã rantsuwa da sama da mai aukõwa da dare.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5933,
            "text": "To, mẽ yã sanar da kai abin da ake cẽwa mai aukõwa da dare?",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5934,
            "text": "Shi ne taurãron nan mai tsananin haske.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5935,
            "text": "Bãbu wani rai fãce a kansa akwai wani mai tsaro.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5936,
            "text": "To, mutum ya dũba, daga mẽ aka halittã shi?",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5937,
            "text": "An halittã shi daga wani ruwa mai tunkuɗar jũna.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5938,
            "text": "Yanã fita daga tsakanin tsatso da karankarman ƙirji.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5939,
            "text": "Lalle ne Shi (Allah), ga mayar da shi (mutum), tabbas Mai iyãwa ne.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5940,
            "text": "Rãnar da ake jarrabawar asirai.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5941,
            "text": "Saboda haka, bã shi da wani ƙarfi, kuma bã shi da wani mai taimako (da zai iya kãre shi daga azãbar Allah).",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5942,
            "text": "Ina rantsuwa da sama ma'abũciyar ruwa mai kõmãwa yana yankẽwa.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5943,
            "text": "Da ƙasa ma'abũciyar tsãgẽwa,",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5944,
            "text": "Lalle ne shĩ (Alƙur'ãni), haƙĩƙa magana ce daki-daki",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5945,
            "text": "Kuma shĩ bã bananci bane",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5946,
            "text": "Lalle ne sũ, suna ƙulla kaidi na sõsai.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5947,
            "text": "Kuma Ni, Ina mayar da kaidi (gare su) kamar yadda suke ƙulla kaidi.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5948,
            "text": "Saboda haka, ka yi wa kafirai jinkiri, ka dakata musu, sannu-sannu.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          }
        ]
      },
      {
        "number": 87,
        "name": "سُورَةُ الأَعۡلَىٰ",
        "englishName": "Al-A'laa",
        "englishNameTranslation": "The Most High",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5949,
            "text": "Ka tsarkake sũnan Ubangijinka Mafi ɗaukaka.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5950,
            "text": "Wanda Yã yi halitta sa'an nan Ya daidaita abin halittar.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5951,
            "text": "Kuma Wanda Ya ƙaddara (abin da Ya so) sannan Ya shiryar, (da mutum ga hanyar alhẽri da ta sharri).",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5952,
            "text": "Kuma Wanda Ya fitar da makiyãyã.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5953,
            "text": "Sa'an nan Ya mayar da ita ƙeƙasassa, baƙa.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5954,
            "text": "Za mu karantar da kai (Alƙur'ãni) sabõda haka bã zã ka mantã (shi) ba.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5955,
            "text": "Fãce abin da Allah Ya so, lalle ne Shi (Allah) Ya san bayyane da abin da yake bõye.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5956,
            "text": "Kuma za Mu sauƙaƙe maka (al'amari) zuwa ga (Shari'a) mai sauƙi.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5957,
            "text": "Sabõda baka, ka tunãtar, idan tunatarwa zã ta yi amfãni.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5958,
            "text": "Wanda yake tsõron (Allah) Zai tuna.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5959,
            "text": "Kuma shaƙiyyi, zai nisanceta,",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5960,
            "text": "Wanda zai shiga wutar da tã fi girma.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5961,
            "text": "Sa'an nan bã zai mutu ba a cikinta, kuma bã zai rãyu ba.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5962,
            "text": "Lalle ne wanda ya tsarkaka (da ĩmãni) yã sãmu babban rabo.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5963,
            "text": "Kuma ya ambaci sũnan Ubangijinsa, sa'an nan yã yi salla.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5964,
            "text": "Ba haka ba! Kunã zãɓin rãyuwa ta kusa dũniya.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5965,
            "text": "Alhãli Lãhira ita ce mafi alheri kuma mafi wanzuwa.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5966,
            "text": "Lalle ne, wannan yanã a cikin littafan farko.",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5967,
            "text": "Littaffan Ibrãhĩm da Mũsã.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
      },
      {
        "number": 88,
        "name": "سُورَةُ الغَاشِيَةِ",
        "englishName": "Al-Ghaashiya",
        "englishNameTranslation": "The Overwhelming",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5968,
            "text": "Lalle ne labãrin (¡iyãma) mai rufe mutãne da tsoronta yã zo maka?",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5969,
            "text": "Wasu huskõki a rãnar nan ƙasƙantattu ne.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5970,
            "text": "Mãsu aikin wahala ne, mãsu gajiya.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5971,
            "text": "Zã su shiga wata wuta mai zãfi.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5972,
            "text": "Ana shãyar da su daga wani marmaro mai zãfin ruwa.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5973,
            "text": "Ba su da wani abinci fãce dai daga danyi.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5974,
            "text": "Bã ya sanya ƙiba, kuma bã zai wadãtar daga yunwa ba.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5975,
            "text": "Wasu huskõki a rãnar nan mãsu ni'ima ne.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5976,
            "text": "Game da aikinsu, masu yarda ne.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5977,
            "text": "(Suna) a cikin Aljanna maɗaukakiya.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5978,
            "text": "Bã zã su ji yãsassar magana ba, a cikinta.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5979,
            "text": "A cikinta akwai marmaro mai gudãna.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5980,
            "text": "A cikinta akwai gadãje maɗaukaka.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5981,
            "text": "Da kõfuna ar'aje.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5982,
            "text": "Da filõli jẽre,",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5983,
            "text": "Da katifu shimfiɗe.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5984,
            "text": "Ashe to bã zã su dũbãwa ba ga rãƙumã yadda aka halitta su?",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5985,
            "text": "Da zuwa ga sama yadda aka ɗaukaka ta?",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5986,
            "text": "Da zuwa ga duwãtsu yadda aka kafa su?",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5987,
            "text": "Da zuwa ga ƙasa yadda aka shimfiɗã ta?",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5988,
            "text": "sabõda haka, ka yi wa'azi, kai mai yin wa'azi ne kawai.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5989,
            "text": "Ba ka zama mai ĩkon tanƙwasãwa a kansu ba.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5990,
            "text": "Fãce dai duk wanda ya jũya bãya, kuma ya kãfirta.",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5991,
            "text": "To, Allah zai yi masa azãba, azãbar nan da take mafi girma.",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5992,
            "text": "Lalle ne, zuwa gare Mu kõmõwarsu take.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5993,
            "text": "Sa'an nan lalle ne aikinMu ne Mu yi musu hisãbi.",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
      },
      {
        "number": 89,
        "name": "سُورَةُ الفَجۡرِ",
        "englishName": "Al-Fajr",
        "englishNameTranslation": "The Dawn",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5994,
            "text": "Inã rantsuwa da alfijiri.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5995,
            "text": "Da darũruwa gõma.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5996,
            "text": "Da (adadi na) cikã da (na) mãrã.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5997,
            "text": "Da dare idan yana shũɗewa.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5998,
            "text": "Ko a cikin waɗannan akwai abin rantsuwa ga mai hankali (da yake kange shi daga zunubi)?",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5999,
            "text": "Ba ka ga yadda Ubangijinka Ya aikata da Ãdãwa ba?",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6000,
            "text": "Iramawa mãsu sakon ƙĩrar jiki.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6001,
            "text": "Waɗanda ba a halitta kwatankwacinsu ba a cikin garũruwa (na dũniya).",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6002,
            "text": "Da samũdãwa waɗanda suka fasa duwãtsu a cikin Wadi suka yi gidãje)?",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6003,
            "text": "Da Fir'auna mai turãku.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6004,
            "text": "Waɗanda suka ƙẽtare iyãkarsu, a cikin garũruwa?",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6005,
            "text": "Sabõda haka, suka yawaita yin ɓarna a cikinsu.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6006,
            "text": "Sabõda haka Ubangijinka Ya zuba musu bũlãlar azãba.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6007,
            "text": "Lalle ne, Ubangijinka Yana nan a mafaka.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6008,
            "text": "To, amma fa mutum idan Ubangijibsa Ya jarraba shi, wãto Yã girmama shi kuma Ya yi masa ni'ima, sai ya ce: 'Ubangijina Ya girmama ni.'",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6009,
            "text": "Kuma idan Ya jarraba shi, wãto Ya ƙuntata masa abincinsa, sai ya ce: 'Ubangijina Ya walãkanta ni.'",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6010,
            "text": "A'aha! Bari wannan, ai bã ku girmama marãya!",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6011,
            "text": "Bã ku kwaɗaitã wa jũnanku ga (tattalin) abincin matalauci!",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6012,
            "text": "Kuma kuna cin dũkiyar gãdo, ci na tãrãwa.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6013,
            "text": "Kuma kuna son dũkiya, so mai yawa.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6014,
            "text": "A'aha! Idan aka niƙa ƙasa niƙewa sosai.",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6015,
            "text": "Kuma Ubangijinka Ya zo, alhãli malã'iku na jẽre, safũ- safu.",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6016,
            "text": "Kuma a ranar nan aka zo da Jahannama. A ranar nan mutum zai yi tunãni. To, inã fa tunãni yake a gare shi!",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6017,
            "text": "Yana dinga cẽwa, 'Kaitona, dã na gabatar (da aikin ƙwarai) domin rãyuwata!'",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6018,
            "text": "To, a rãnar nan bãbu wani mai yin azãba irin azãbar Allah.",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6019,
            "text": "Kuma bãbu wani mai ɗauri irin ɗaurinSa.",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6020,
            "text": "Yã kai rai mai natsuwa!",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6021,
            "text": "Ka koma zuwa ga Ubangijinka, alhãli kana mai yarda (da abin da Ya ƙaddara maka a dũniya) abar yardarwa (da sakamakon da zã a ba ka a Lãhira).",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6022,
            "text": "Sabõbda haka, ka shiga cikin bãyiNa (mãsu bin umurui a dũniya).",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6023,
            "text": "Kuma ka shiga AljannaTa (a Lãhira).",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
      },
      {
        "number": 90,
        "name": "سُورَةُ البَلَدِ",
        "englishName": "Al-Balad",
        "englishNameTranslation": "The City",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6024,
            "text": "Bã sai Na yi rantsuwa da wannan gari ba.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6025,
            "text": "Alhãli kai kanã mai sauka a cikin wannan gari.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6026,
            "text": "Da mahaifi da abin da ya haifa.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6027,
            "text": "Lalle ne, Mun halitta mutum cikin wahala.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6028,
            "text": "Kõ yanã zaton bãbu wani mai iya sãmun iko, a kansa?",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6029,
            "text": "Yana cẽwa 'Na halakarda dũkiya mai yawa,'",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6030,
            "text": "Shin, yana zaton cewa wani bai gan shi ba?",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6031,
            "text": "Shin, ba Mu sanya masa idãnu biyu ba?",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6032,
            "text": "Da harshe, da leɓɓa biyu.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6033,
            "text": "Kuma ba Mu shiryar da shi ga hanyõyi biyu ba?",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6034,
            "text": "To, don mene ne bai shiga Aƙabã ba?",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6035,
            "text": "Kuma mene ne ya sanar da kai abin da ake cẽ wa Aƙabã?",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6036,
            "text": "Ita ce fansar wuyan bãwa.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6037,
            "text": "Ko kuwa ciyarwa, a cikin yini ma'abũcin yunwa.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6038,
            "text": "Ga marãya ma'abũcin zumunta.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6039,
            "text": "Ko kuwa wani matalauci ma'abũcin turɓãya.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6040,
            "text": "Sa'an nan kuma ya kasance daga waɗanda suka yi ĩmãni, kuma suka yi wa jũna wasiyya da yin haƙuri, kuma suka yi wa jũna wasiyya da tausayi.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6041,
            "text": "Waɗannan ne ma'abũta albarka",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6042,
            "text": "Kuma waɗanda suka kãfirta da ãyõyinMu, sũ ne ma'abũta shu'umci",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6043,
            "text": "A kansu akwai wata wuta abar kullewa.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 91,
        "name": "سُورَةُ الشَّمۡسِ",
        "englishName": "Ash-Shams",
        "englishNameTranslation": "The Sun",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6044,
            "text": "Ina rantsuwa da rãnã da hantsinta.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6045,
            "text": "Kuma da wata idan ya bi ta.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6046,
            "text": "Da yini a lõkacin da ya bayyana ta.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6047,
            "text": "Da dare a lõkacin da ya ke rufe ta.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6048,
            "text": "Da sama da abin da ya gina ta.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6049,
            "text": "Da ƙasã da abin da ya shimfiɗa ta.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6050,
            "text": "Da rai da abin da ya daidaita shi.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6051,
            "text": "Sa'an nan ya sanar da shi fãjircinsa da shiryuwarsa.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6052,
            "text": "Lalle ne wanda ya tsarkake shi (rai) ya sãmi babban rabo.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6053,
            "text": "Kuma lalle ne wanda ya turbuɗe shi (da laifi) ya tãɓe.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6054,
            "text": "Samũdãwa sun ƙaryata (Annabinsu), dõmin girman kansu.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6055,
            "text": "A lõkacin da mafi shaƙãwarsu ya tafi (wurin sõke rãkumar sãlihu).",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6056,
            "text": "Sai Manzon Allah ya gaya musu cewa: 'Ina tsõratar da ku ga rãƙumar Allah da ruwan shanta!'",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6057,
            "text": "Sai suka ƙaryata shi, sa'an nan suka sõke ta. Sabõda haka Ubangijinsu Ya darkãke su, sabõda zunubinsu. Sa'an nan Ya daidaita ta (azãbar ga mai laifi da maras laifi).",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6058,
            "text": "Kuma bã ya tsõron ãƙibarta (ita halakãwar).",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 92,
        "name": "سُورَةُ اللَّيۡلِ",
        "englishName": "Al-Lail",
        "englishNameTranslation": "The Night",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6059,
            "text": "Inã rantsuwa da dare a lõkacin da yake rufẽwa.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6060,
            "text": "Da yini a lõkacin da yake kuranyẽwa.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6061,
            "text": "Da abin da ya halitta namiji da mace.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6062,
            "text": "Lãlle ne ayyukanku, dabam-dabam suke.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6063,
            "text": "To, amma wanda ya yi kyauta, kuma ya yi taƙawa.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6064,
            "text": "Kuma ya gaskata kalma mai kyãwo.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6065,
            "text": "To, zã Mu sauƙaƙe masa har ya kai ga sauƙi.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6066,
            "text": "Kuma amma wanda ya yi rõwa, kuma ya wadãtu da kansa.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6067,
            "text": "Kuma ya ƙaryatar da kalma mai kyãwo.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6068,
            "text": "To, zã Mu sauƙaƙke masa har ya kai ga tsanani.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6069,
            "text": "Kuma dũkiyarsa ba ta wadãtar masa da kõme ba, a lõkacin da ya gangara (a wuta).",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6070,
            "text": "Lãlle aikinMu ne, Mu bayyana shiriya.",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6071,
            "text": "Kuma lalle ne Lãhira da duniya Namu ne.",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6072,
            "text": "Sabõda haka, Na yi maku gargaɗi da wuta mai babbaka.",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6073,
            "text": "Bãbu mai shigarta sai mafi shaƙãwa.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6074,
            "text": "Wanda ya ƙaryata, kuma ya jũya bãya.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6075,
            "text": "Kuma mafi taƙawa zai nisance ta.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6076,
            "text": "Wanda yake bãyar da dũkiyarsa, alhãli yana tsarkaka.",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6077,
            "text": "Alhãli bãbu wani mai wata ni'ima wurinsa wadda ake nẽman sakamakonta.",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6078,
            "text": "Fãce dai nẽman yardar Ubangijinsa Mafi ɗaukaka.",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6079,
            "text": "To, lalle ne zai yarda (da sakamakon da zã a bã shi).",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 93,
        "name": "سُورَةُ الضُّحَىٰ",
        "englishName": "Ad-Dhuhaa",
        "englishNameTranslation": "The Morning Hours",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6080,
            "text": "Inã rantsuwa da hantsi.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6081,
            "text": "Da dare a lõkacin da ya rufe (da duhunsa).",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6082,
            "text": "Ubangijinka bai yi maka bankwana ba, kuma bai ƙĩ kaba.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6083,
            "text": "Kuma lalle ta ƙarshe ce mafi alheri a gare ka daga ta farko.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6084,
            "text": "Kuma lalle ne, Ubangijinka zai yi ta bã ka kyauta sai ka yarda.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6085,
            "text": "Ashe, bai sãme ka marãya ba, sa'an nan Ya yi maka makõma?",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6086,
            "text": "Kuma Ya sãme ka bã ka da shari'ã, sai Ya shiryar da kai?",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6087,
            "text": "Kuma Ya sãme ka matalauci, sai Ya wadãtã ka?",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6088,
            "text": "Sabõda haka, amma marãya, to, kada ka rinjãye shi.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6089,
            "text": "Kuma amma mai tambaya, kada ka yi masa tsãwa.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6090,
            "text": "Kuma amma ni'imar Ubangijinka, sai ka faɗa (dõmin gõdiya).",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 94,
        "name": "سُورَةُ الشَّرۡحِ",
        "englishName": "Ash-Sharh",
        "englishNameTranslation": "The Consolation",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6091,
            "text": "Ba Mu bũɗa maka zũciyarka ba (dõmin ɗaukar haƙuri da fahimta)?",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6092,
            "text": "Kuma Muka saryar maka da nauyinka, Ashe.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6093,
            "text": "Wanda ya nauyayi bãyanka?",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6094,
            "text": "Kuma Muka ɗaukaka maka ambatonka?",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6095,
            "text": "To, lalle ne tãre da tsananin nan akwai wani sauƙi.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6096,
            "text": "Lalle ne tãre da tsananin nan akwai wani sauƙi.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6097,
            "text": "Sabõda haka idan ka ƙãre (ibãda) sai ka kafu (kana rõƙon Allah).",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6098,
            "text": "Kuma zuwa ga Ubangijinka ka yi kwaɗayi.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 95,
        "name": "سُورَةُ التِّينِ",
        "englishName": "At-Tin",
        "englishNameTranslation": "The Fig",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6099,
            "text": "Inã rantsuwa da Attinu da Azzaitũn.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6100,
            "text": "Da Dũr Sĩnĩna.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6101,
            "text": "Da wannan gari amintacce.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6102,
            "text": "Lalle ne, Mun halitta mutum a cikin mafi kyawon tsayuwa.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6103,
            "text": "Sa'an nan, Muka mayar da shi mafi ƙasƙantar masu rauni",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6104,
            "text": "Face waɗanda suka yi imani, kuma suka aikata ayyukan ƙwarai, saboda haka suna da sakamako wanda bã ya yankewa.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6105,
            "text": "To, bãyan haka mẽ ya sanya ka ka ƙaryata sakamako (wanda za a yi a bãyan Tãshin ¡iyãma)?",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6106,
            "text": "Ashe Allah bã Shi ne Mafi mãsu hukunci kyãwon hukunci ba?",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 96,
        "name": "سُورَةُ العَلَقِ",
        "englishName": "Al-Alaq",
        "englishNameTranslation": "The Clot",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6107,
            "text": "Ka yi karatu da sunan Ubangijinka, wanda Ya yi halitta.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6108,
            "text": "Ya hahitta mutum daga gudan jini.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6109,
            "text": "Ka yi karatu, kuma UbangiJinka shi ne Mafi karimci.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6110,
            "text": "Wanda Ya sanar (da mutum) game da alƙalami.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6111,
            "text": "Ya sanar da mutum abin da bai sani ba.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6112,
            "text": "A'aha! Lalle, ne mutum haƙĩƙa, yana girman kai (ya ƙi karatu).",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6113,
            "text": "Dõmin yã ga kansa, yã wadãta.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6114,
            "text": "Lalle ne zuwa ga Ubangijinka makoma take.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6115,
            "text": "Shin, kã ga wanda ke hana.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6116,
            "text": "Bãwã idan yã yi salla?",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6117,
            "text": "Ashe, kã gani, idan (shi bãwan) ya kasance a kan shiriya?",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6118,
            "text": "Ko ya yi umurni da taƙawa?",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6119,
            "text": "Ashe, kã gani, idan (shi mai hanin) ya ƙaryata, kuma ya jũya bãya?",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6120,
            "text": "Ashe, bai sani ba cewa Allah Yanã gani?",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6121,
            "text": "A'aha! Lalle ne, idan bai hanu ba, lalle ne zã Mu ja gãshin makwarkwaɗa.",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6122,
            "text": "Makwarkwaɗa maƙaryaciya, mai laifi.",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6123,
            "text": "Sai ya kirayi ƙungiyarsa.",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6124,
            "text": "Zã Mu kirayi zabaniyãwa (mala'iku mãsu girma).",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6125,
            "text": "A'aha kada ka bĩ shi. Kuma ka yi tawali'u, kuma ka nemi kusanta (zuwa ga Ubangijinka).",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": {
              "id": 15,
              "recommended": false,
              "obligatory": true
            }
          }
        ]
      },
      {
        "number": 97,
        "name": "سُورَةُ القَدۡرِ",
        "englishName": "Al-Qadr",
        "englishNameTranslation": "The Power, Fate",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6126,
            "text": "Lalle ne Mũ, Mun saukar da shi (Alƙur'ãni) a cikin Lailatul ƙadari (daren daraja)",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6127,
            "text": "To, me ya sanar da kai abin da ake cewa Lailatul ¡adari?",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6128,
            "text": "Lailatul ¡adari mafi alheri ne daga wasu watanni dubu.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6129,
            "text": "Mala'iku da Rũhi suna sauka a cikinsa da iznin Ubangijinsu sabõda kowane umurni.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6130,
            "text": "Sallama ne shi daren, har fitar alfijiri.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 98,
        "name": "سُورَةُ البَيِّنَةِ",
        "englishName": "Al-Bayyina",
        "englishNameTranslation": "The Evidence",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 6131,
            "text": "Waɗanda suka kãfirta daga mũtanen Littafi, da mushirikai, ba su kasance masu gushewa daga gaskiya ba har hujja ta je musu.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6132,
            "text": "Wani Manzo daga Allah, yana karatun wasu takardu masu tsarki.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6133,
            "text": "A cikinsu akwai wasu littafai mãsu ƙĩma da daraja.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6134,
            "text": "Kuma waɗanda aka bai wa Littafi ba su sãɓa wa juna ba face bayan hujjar ta je musu.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6135,
            "text": "Kuma ba a umarce su da kome ba fãce bauta wa Allah suna mãsu tsarkake addinin gare Shi, mãsu karkata zuwa ga addinin gaskiya, kuma su tsai da salla kuma su bãyar da zakka, kuma wannan shi ne addinin waɗanda suke a kan hanyar ƙwarai.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6136,
            "text": "Lalle ne waɗanda suka kafirta daga mutanen Littafi da mushirikai sana cikin wutar Jahannama suna madawwama a cikinta. Waɗannan su ne mafi ashararancin tãlikai.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6137,
            "text": "Lalle ne waɗanda suka yi ĩmani, kuma suka aikata ayyukan ƙwarai, waɗannan su ne mafifita alherin halitta.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6138,
            "text": "Sakamakonsu, a wurin Ubangijinsu, shi ne gidajen Aljannar zama, ƙoramu na gudãna daga ƙarƙashinsu sunã madawwama a cikinta har abada. Allah Ya yarda da su, kuma su, sun yarda da Shi. wannan sakamako ne ga wanda ya ji tsõron Ubangijinsa.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 99,
        "name": "سُورَةُ الزَّلۡزَلَةِ",
        "englishName": "Az-Zalzala",
        "englishNameTranslation": "The Earthquake",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 6139,
            "text": "Idan aka girgiza ƙasa, girgizawarta.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6140,
            "text": "Kuma ƙasa ta fitar da kayanta, masu nauyi.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6141,
            "text": "Kuma mutum ya ce 'Mẽ neya same ta?'",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6142,
            "text": "A rãnar nan, zã ta faɗi lãbãrinta.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6143,
            "text": "cewa Ubangijinka Ya yi umurni zuwa gare ta.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6144,
            "text": "A rãnar nan mutane za su fito dabam-dabam domin a nuna musu ayyukansu.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6145,
            "text": "To, wanda ya aikata (wani aiki) gwargwadon nauyin zarra, na alheri, zai gan shi.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6146,
            "text": "Kuma wanda ya aikata gwargwadon nauyin zarra na sharri, zai gan shi.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 100,
        "name": "سُورَةُ العَادِيَاتِ",
        "englishName": "Al-Aadiyaat",
        "englishNameTranslation": "The Chargers",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6147,
            "text": "Ina rantsuwa da dawãki mãsu gudu suna fitar da kũkan ciki.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6148,
            "text": "Mãsu ƙyasta wuta (da kõfatansu a kan duwatsu) ƙyastawa.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6149,
            "text": "Sa'an nan mãsu yin hari a lokacin asuba.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6150,
            "text": "Sai su motsar da ƙũra game da shi.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6151,
            "text": "Sai su shiga, game da ita (ƙũrar), a tsakãnin jama'ar maƙiya.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6152,
            "text": "Lalle ne mutum mai tsananin butulci ne ga Ubangijinsa.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6153,
            "text": "Lalle ne shi mai shaida ne a kan laifinsa dl haka.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6154,
            "text": "Kuma 1alle ne ga dũkiya shi mai tsananin so ne.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6155,
            "text": "Shin, bã ya da sanin lõkacin da aka tõne abin da yake cikin kaburbura.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6156,
            "text": "Aka bayyana abin da ke cikin zukata.",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 542,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6157,
            "text": "Lalle ne Ubangijinsu, game da su a ranar nan Mai ƙididdigewa ne?",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 542,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 101,
        "name": "سُورَةُ القَارِعَةِ",
        "englishName": "Al-Qaari'a",
        "englishNameTranslation": "The Calamity",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6158,
            "text": "Mai ƙwanƙwasar (zukata da tsõro)!",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6159,
            "text": "Mẽnẽ ne mai ƙwanƙwasa?",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6160,
            "text": "Kuma me ya sanar da kai abin da ake ce wa mai ƙwanƙwasa?",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6161,
            "text": "Rãnar da mutãne za su kasance kamar 'ya'yan fari mãsu wãtsuwa.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6162,
            "text": "Kuma duwãtsu su kasance kamar gãshin sũfin da aka saɓe.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6163,
            "text": "To, amma wanda ma'aunansa suka yi nauyi.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6164,
            "text": "To, shi yana a cikin wata rayuwa yardadda.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6165,
            "text": "Kuma amma wanda ma'aunansa suka yi sauƙi (bãbu nauyi).",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6166,
            "text": "To, uwarsa Hãwiya ce.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6167,
            "text": "Kuma me ya sanar da kai mece ce ita?",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6168,
            "text": "Wata wuta ce mai zafi.",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 102,
        "name": "سُورَةُ التَّكَاثُرِ",
        "englishName": "At-Takaathur",
        "englishNameTranslation": "Competition",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6169,
            "text": "Alfahari da yawan dũkiya da dangi ya shagaltar da ku (dagaibada mai amfaninku).",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6170,
            "text": "Har kuka ziyarci kaburbura.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6171,
            "text": "A'aha! (Nan gaba) zã ku sani.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6172,
            "text": "Sa'an nan, tabbas, zã ku sani.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6173,
            "text": "Haƙĩƙa, da kuna da sani sani na yaƙĩni.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6174,
            "text": "Lalle ne da kuna ganin Jahĩm.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6175,
            "text": "Sa'an nan lalle ne za ku gan ta, da idanu, bayyane.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6176,
            "text": "Sa'an nan lalle ne za a tambaye ku, a rãnar nan lãbãrin ni'imar (da aka yi muku).",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 103,
        "name": "سُورَةُ العَصۡرِ",
        "englishName": "Al-Asr",
        "englishNameTranslation": "The Declining Day, Epoch",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6177,
            "text": "Ina rantsuwa da zãmani.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 545,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6178,
            "text": "Lalle ne mutum yana a cikin hasara.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 545,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6179,
            "text": "Fãce waɗanda suka yi ĩmani, kuma suka aikata ayyukan ƙwarai, kuma suka yi wa juna wasiyya da bin gaskiya, kuma suka yi wa juna wasiyya da yin haƙuri (su kam, basu cikin hasara).",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 545,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 104,
        "name": "سُورَةُ الهُمَزَةِ",
        "englishName": "Al-Humaza",
        "englishNameTranslation": "The Traducer",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6180,
            "text": "Bone ya tabbata ga duk mai nune, mai zunɗe (mai raɗa).",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6181,
            "text": "Wanda ya tara dũkiya, kuma ya mayar da ita abar tattalinsa.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6182,
            "text": "Yana zaton cẽwa dũkiyarsa za ta dawwamar da shi.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6183,
            "text": "A'aha! Lalle ne zã a jẽfa shi a cikin Huɗama.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6184,
            "text": "Kuma me ya sanar da kai abin da ake ce wa Huɗama?",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6185,
            "text": "Wutar Allah ce wadda ake hurawa.",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6186,
            "text": "Wadda take lẽƙãwa a kan zukata.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6187,
            "text": "Lalle ne ita abar kullẽwa cea kansu.",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6188,
            "text": "A cikin wasu ginshiƙai mĩƙaƙƙu.",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 105,
        "name": "سُورَةُ الفِيلِ",
        "englishName": "Al-Fil",
        "englishNameTranslation": "The Elephant",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6189,
            "text": "Ashe, ba ka ga yadda Ubangijinka Ya aikata ga mutanen giwa ba?",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6190,
            "text": "Ashe, bai sanya kaidinsu a cikin ɓata ba?",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6191,
            "text": "Kuma Ya sako, a kansu, wasu tsuntsãye, jama'a-jama'a.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6192,
            "text": "Suna jifar su da wasu duwatsu na yumɓun wuta.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6193,
            "text": "Sa'an nan Ya sanya su kamar karmami wanda aka cinye?",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 106,
        "name": "سُورَةُ قُرَيۡشٍ",
        "englishName": "Quraish",
        "englishNameTranslation": "Quraysh",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6194,
            "text": "Sabõda sãbon ¡uraishawa.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6195,
            "text": "Sãbonsu na tafiyar hunturu da ta bazara.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6196,
            "text": "Sabõda haka sai su bauta wa Ubangijin wannan Gida (Ka'abah).",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6197,
            "text": "wanda Ya ciyar da su (Ya hana su) daga yunwa, kuma Ya amintar da su daga wani tsõro.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 107,
        "name": "سُورَةُ المَاعُونِ",
        "englishName": "Al-Maa'un",
        "englishNameTranslation": "Almsgiving",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6198,
            "text": "Shin, ka ga wanda ke ƙaryatãwa game da sakamako?",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6199,
            "text": "To, wannan shi ne ke tunkue marãya (daga haƙƙinsa).",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6200,
            "text": "Kuma bã ya kwaaitarwa bisa bãyar da abinci ga matalauci.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6201,
            "text": "To, bone yã tabbata ga masallata.",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6202,
            "text": "Waɗanda suke masu shagala daga sallarsu.",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6203,
            "text": "Waɗanda suke yin riya (ga ayyukansu)",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6204,
            "text": "Kuma suna hana taimako.",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 108,
        "name": "سُورَةُ الكَوۡثَرِ",
        "englishName": "Al-Kawthar",
        "englishNameTranslation": "Abundance",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6205,
            "text": "Lalle ne Mu, Mun yi maka kyauta mai yawa.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 550,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6206,
            "text": "Saboda haka, ka yi salla dõmin Ubangijinka, kuma ka sõke (baiko, wato sukar raƙumi).",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 550,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6207,
            "text": "Lalle mai aibanta ka shi ne mai yankakkiyar albarka.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 550,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 109,
        "name": "سُورَةُ الكَافِرُونَ",
        "englishName": "Al-Kaafiroon",
        "englishNameTranslation": "The Disbelievers",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6208,
            "text": "Ka ce: 'Ya kũ kãfirai!'",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6209,
            "text": "'Bã zan bautã wa abin da kuke bautã wa ba.'",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6210,
            "text": "'Kuma kũ, ba ku zama masu bauta wa abin da nake bautã wa ba.'",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6211,
            "text": "'Kuma nĩ ban zama mai bautã wa abin da kuka bautã wa ba.'",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6212,
            "text": "'Kuma kũ, ba ku zama mãsu bautã wa abin da nake bautã wa ba.'",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6213,
            "text": "'Addininku na garẽ ku, kuma addinina yanã gare ni.'",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 110,
        "name": "سُورَةُ النَّصۡرِ",
        "englishName": "An-Nasr",
        "englishNameTranslation": "Divine Support",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 6214,
            "text": "Idan taimakon Allah ya zo da cin nasara.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6215,
            "text": "Kuma ka ga mutane suna shiga addinin Allah, ƙungiya-ƙungiya.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6216,
            "text": "To, ka yi tasbihi game da gõde wa Ubangijinka, kuma ka nẽme Shi gafara, lalle Shi (Ubangijinka) Ya kasance Mai karɓar tũba ne.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 111,
        "name": "سُورَةُ المَسَدِ",
        "englishName": "Al-Masad",
        "englishNameTranslation": "The Palm Fibre",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6217,
            "text": "Hannãye biyu na Abũlahabi sun halaka, kuma ya halaka.",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6218,
            "text": "Dũkiyarsa ba ta tsare masa kõme ba, da abin da ya tãra.",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6219,
            "text": "Zã ya shiga wuta mai hũruwa.",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6220,
            "text": "Tãre da matarsa mai ɗaukar itacen (wuta).",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6221,
            "text": "A cikin kyakkyãwan wuyanta akwai igiya ta kaba (Rãnar ¡iyãma).",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 112,
        "name": "سُورَةُ الإِخۡلَاصِ",
        "englishName": "Al-Ikhlaas",
        "englishNameTranslation": "Sincerity",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6222,
            "text": "Ka ce: 'Shi ne Allah Makaɗaĩci.'",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6223,
            "text": "'Allah wanda ake nufin Sa da buƙata.'",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6224,
            "text": "'Bai haifa ba, kuma ba a haife Shi ba.'",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6225,
            "text": "'Kuma babu ɗaya da ya kasance tamka a gare Shi.'",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 113,
        "name": "سُورَةُ الفَلَقِ",
        "englishName": "Al-Falaq",
        "englishNameTranslation": "The Dawn",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6226,
            "text": "Ka ce 'ina neman tsari ga Ubangijin safiya'",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6227,
            "text": "'Daga sharrin abin da Ya halitta.'",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6228,
            "text": "'Da sharrin dare, idan ya yi duhu.'",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6229,
            "text": "'Da sharrin mãtã mãsu tõfi a cikin ƙulle-ƙulle.'",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6230,
            "text": "'Da sharrin mai hãsada idan ya yi hasada.'",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 114,
        "name": "سُورَةُ النَّاسِ",
        "englishName": "An-Naas",
        "englishNameTranslation": "Mankind",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6231,
            "text": "Ka ce 'Ina nẽman tsari ga Ubangijin mutãne.'",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6232,
            "text": "'Mamallakin mutane.'",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6233,
            "text": "'Abin bautãwar mutãne.'",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6234,
            "text": "'Daga sharrin mai sanya wasuwãsi, mai ɓoyewa.'",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6235,
            "text": "'Wanda ke sanya wasuwãsi a cikin ƙirãzan mutane.'",
