        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5830,
            "text": "كله چې اسمان وچوي",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5831,
            "text": "او كله چې ستوري ګډوډ شي (او) راولوېږي",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5832,
            "text": "او كله چې دریابونه یو ځاى روان كړى شي",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5833,
            "text": "او كله چې قبرونه لاندې باندې كړى شي",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5834,
            "text": "هر نفس به په هغه څه پوه شي چې ده وړاندې لېږلي دي او وروسته يې پرېښې دي",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5835,
            "text": "اى (كافره) انسانه! ته په خپل كریم (او سخي) رب باندې څه شي دوكه كړى يې",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5836,
            "text": "هغه چې ته يې پیدا كړې، نو ته يې ښه برابر كړې",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5837,
            "text": "په هر صورت (شكل) كې چې هغه غوښتل، ته يې پیوند (او جوړ) كړې",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5838,
            "text": "داسې بېخي نه ده، بلكې تاسو د بدلې ورځ (قیامت) دروغ ګڼئ",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5839,
            "text": "په داسې حال كې چې پر تاسو باندې ساتونكي (ملايك) مقرر دي",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5840,
            "text": "چې عزتمن لیكونكي دي",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5841,
            "text": "دوى په هغه څه پوهېږي چې تاسو يې كوئ",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5842,
            "text": "بېشكه نېكان (مومنان) به خامخا په نعمتونو كې وي",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5843,
            "text": "او بېشكه چې بدكاران (كافران) به خامخا په جهنم كې وي",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5844,
            "text": "دوى به ده ته د بدلې په ورځ ننوځي",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5845,
            "text": "او دوى به له دغه (دوزخ) نه له سره پټېدونكي نه وي",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5846,
            "text": "او ته څه شي پوه كړې چې د بدلې ورځ (قیامت) څه شى دى؟",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5847,
            "text": "بیا ته څه شي پوه كړې چې د بدلې ورځ څه شى دى",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5848,
            "text": "(یاده كړه) هغه ورځ چې هېڅ یو نفس به د بل نفس لپاره د هېڅ شي اختیار ونه لري۔ او په دغې ورځ كې به امر (حكم) خاص الله لره وي",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          }
        ]
      },
      {
        "number": 83,
        "name": "سُورَةُ المُطَفِّفِينَ",
        "englishName": "Al-Mutaffifin",
        "englishNameTranslation": "Defrauding",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5849,
            "text": "(په تول او پېمانه كې) كمي كوونكو لره هلاكت دى",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5850,
            "text": "هغه كسان چې دوى له خلقو نه ځان ته پېمانه اخلي (، نو) پوره (يې) اخلي",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5851,
            "text": "او كله چې دغو (خلقو) ته پېمانه وركوي، یا يې دوى ته تلي (، نو) كمى كوي",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5852,
            "text": "ایا دغه خلق یقین نه كوي چې بېشكه دوى به بیا راژوندي كول شي",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5853,
            "text": "په ډېره لویه ورځ كې (د حساب لپاره)",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5854,
            "text": "هغه ورځ چې خلق به ’’رب العلمین‘‘ ته ودرېږي",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5855,
            "text": "هیڅكله داسې نه ده په كار، بېشكه د فاجرانو (كافرانو) كتاب (لیكل شوي عملونه) خامخا په سجین كې دي",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5856,
            "text": "او ته څه شي پوه كړې چې سجین څه شى دى",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5857,
            "text": "یو دفتر (كتاب) دى لیكل شوى",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5858,
            "text": "په دغې ورځ كې د دروغ ګڼونكو لپاره هلاكت دى",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5859,
            "text": "هغه كسان چې د بدلې ورځ دروغ ګڼي",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5860,
            "text": "او دغه (ورځ) دروغ نه ګڼي مګر هر زیاتي كوونكى، سخت ګناه ګار",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5861,
            "text": "كله چې ده ته زمونږ ایتونه تلاوت كولى شي؛ دى وايي: (دا) د ړومبنیو (خلقو) باطلې قصې دي",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5862,
            "text": "داسې نه ده، بلكې د دوى په زړونو باندې هغو (د ګناه عملونو) زنګ لګولى دى چې دوى به كول",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5863,
            "text": "داسې نه ده په كار، بېشكه دوى به په دغې ورځ كې له خپل رب نه یقینًا په حجاب (بند) كړى شوي وي",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5864,
            "text": "بیا بېشكه دوى یقینًا دوزخ ته ننوتونكي دي",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5865,
            "text": "بیا به (دوى ته) ویلى شي: دا هغه (دوزخ) دى چې تاسو به دا دروغ ګاڼه",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5866,
            "text": "داسې نه ده، بېشكه د نېكانو (خلقو) عملنامې (لیكلى كتاب) خامخا په ’’علِّیین‘‘ كې دي",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5867,
            "text": "او ته څه شي پوه كړې چې ’’علّیون‘‘ څه شى دى",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5868,
            "text": "یو دفتر (كتاب) دى چې لیكل شوى دى",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5869,
            "text": "چې ده ته مقرب (بنده ګان او ملايك) حاضرېږي",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5870,
            "text": "بېشكه نېكان خلق یقینًا په نعمتونو كې دي",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5871,
            "text": "دوى به په تختونو باندې (ناست) وي، ګوري به",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5872,
            "text": "(اى كتونكیه!) ته به د دوى په مخونو كې د نعمتونو تازګي وینې",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5873,
            "text": "په دوى به مُهر (ټاپه) كړى شوي خالص شراب څښولى شي",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5874,
            "text": "چې د دغو مُهر (ټاپه) به مشك وي، او په دغه (نعمتونو) كې، پس په كار ده چې رغبت كوونكي رغبت وكړي",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5875,
            "text": "د دغو (شرابو) ګډون له تسنیم (چينې) څخه دى",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5876,
            "text": "چې یوه چینه ده چې له دې نه به (د الله) مقربین څښي",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5877,
            "text": "بېشكه هغه كسان چې جرم (د كفر) يې كړى دى۔، دوى به په هغو كسانو پورې خندا كوله چې ایمان يې راوړى و",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5878,
            "text": "او كله چې به د دوى (مومنانو) په خوا كې تېرېدل (نو) په خپلو منځونو كې به يې سترګكونه وهل",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5879,
            "text": "او كله چې به دوى خپلو كورونو ته واپس كېدل (نو) خوشاله به واپس كېدل",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5880,
            "text": "او كله چې به دوى دغه (مسلمانان) وليدل (نو) ويل به يې: بېشكه دغه خلق یقینًا ګمراهان دي",
            "numberInSurah": 32,
