          }
        ]
      },
      {
        "number": 84,
        "name": "سُورَةُ الانشِقَاقِ",
        "englishName": "Al-Inshiqaaq",
        "englishNameTranslation": "The Splitting Open",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5885,
            "text": "كله چې اسمان څیرې شي",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5886,
            "text": "او خپل رب ته غوږ كېږدي او دى (اسمان) د همدې لايق دى",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
