        "englishNameTranslation": "The Beneficent",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 4902,
            "text": "خداوند رحمان‌",
            "numberInSurah": 1,
            "juz": 27,
            "manzil": 7,
            "page": 531,
            "ruku": 466,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
