            "number": 4960,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 59,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4961,
            "text": "آيا جزاى نيكوكارى، جز نيكوكارى است؟",
            "numberInSurah": 60,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4962,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 61,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4963,
            "text": "و جز آن دو نيز دو بوستان است‌",
            "numberInSurah": 62,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4964,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 63,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4965,
            "text": "كه سبز سير سيه‌تاب‌اند",
            "numberInSurah": 64,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4966,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 65,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4967,
            "text": "در آن دو، دو چشمه فواره‌زن هست‌",
            "numberInSurah": 66,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4968,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 67,
            "juz": 27,
            "manzil": 7,
            "page": 533,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4969,
            "text": "در آن دو [درختان‌] ميوه و خرما و انار هست‌",
            "numberInSurah": 68,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4970,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 69,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4971,
            "text": "در آنها دوشيزگان نيكخو و زيبا روست‌",
            "numberInSurah": 70,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4972,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 71,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4973,
            "text": "حوريان پرده‌نشين در خيمه‌ها",
            "numberInSurah": 72,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4974,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 73,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4975,
            "text": "پيش از آنان، هيچ انسان و هيچ جنى با آنان آميزش نكرده است‌",
            "numberInSurah": 74,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4976,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 75,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4977,
            "text": "تكيه زده بر بالش‌[هاى‌] سبزرنگ و بر فرش‌[هاى‌] گرانمايه نيكو",
            "numberInSurah": 76,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4978,
            "text": "پس كدامين نعمتهاى پروردگارتان را انكار مى‌كنيد؟",
            "numberInSurah": 77,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          },
          {
            "number": 4979,
            "text": "متبرك باد نام پروردگارت كه شكوهمند و گرامى است‌",
            "numberInSurah": 78,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 468,
            "hizbQuarter": 213,
            "sajda": false
          }
        ]
      },
      {
        "number": 56,
        "name": "سُورَةُ الوَاقِعَةِ",
        "englishName": "Al-Waaqia",
        "englishNameTranslation": "The Inevitable",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 4980,
            "text": "چون واقعه بزرگ [قيامت‌] واقع شود",
            "numberInSurah": 1,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4981,
            "text": "در وقوع آن دروغى نيست‌",
            "numberInSurah": 2,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4982,
            "text": "هم فرو دارنده‌[ى كافران‌] است و هم فرادارنده‌[ى مؤمنان‌]",
            "numberInSurah": 3,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4983,
            "text": "آنگاه كه زمين به جنبشى سخت جنبانده شود",
            "numberInSurah": 4,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4984,
            "text": "و كوهها سخت خرد و ريز شود",
            "numberInSurah": 5,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4985,
            "text": "و همچون غبارى پراكنده گردد",
            "numberInSurah": 6,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4986,
            "text": "و شما گروههاى سه‌گانه‌اى باشيد",
            "numberInSurah": 7,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4987,
            "text": "[يكى‌] اصحاب يمين، و چه حال دارند اصحاب يمين‌",
            "numberInSurah": 8,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4988,
            "text": "و [ديگرى‌] اصحاب شمال، و چه حال دارند اصحاب شمال‌",
            "numberInSurah": 9,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4989,
            "text": "و [سومين‌] سابقان كه پيشتازانند",
            "numberInSurah": 10,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4990,
            "text": "اينانند كه مقرب‌اند",
            "numberInSurah": 11,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4991,
            "text": "در بهشتهاى پرناز و نعمت‌",
            "numberInSurah": 12,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4992,
            "text": "گروهى بسيار از پيشينيان‌",
            "numberInSurah": 13,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4993,
            "text": "و اندكى از واپسينان‌",
            "numberInSurah": 14,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4994,
            "text": "بر تختهاى گوهرنشان‌",
            "numberInSurah": 15,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4995,
            "text": "رودرروى هم بر آنها تكيه زده‌اند",
            "numberInSurah": 16,
            "juz": 27,
            "manzil": 7,
            "page": 534,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4996,
            "text": "جاودانه جوانان بر گرد آنان مى‌گردند",
            "numberInSurah": 17,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4997,
            "text": "همراه با كوزه‌ها و ابريقها و جامهايى از شراب جارى‌",
            "numberInSurah": 18,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4998,
            "text": "كه [بهشتيان‌] از آن سردرد نگيرند و بد مست نشوند",
            "numberInSurah": 19,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 4999,
            "text": "و ميوه‌هايى از آنچه بر مى‌گزينند",
            "numberInSurah": 20,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5000,
            "text": "و گوشت مرغ از آنچه خوش دارند",
            "numberInSurah": 21,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5001,
            "text": "و حوريان چشم درشت‌",
            "numberInSurah": 22,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5002,
            "text": "كه همانندان مرواريد نهفته‌اند",
            "numberInSurah": 23,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5003,
            "text": "اين پاداش كارى است كه كرده‌اند",
            "numberInSurah": 24,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5004,
            "text": "در آنجا [سخنان‌] بيهوده و [گزاف‌] گناه‌آلود نشنوند",
            "numberInSurah": 25,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5005,
            "text": "نشنوند مگر سخنى كه سلام است و سلام‌",
            "numberInSurah": 26,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5006,
            "text": "و اصحاب يمين چه حال دارند اصحاب يمين‌",
            "numberInSurah": 27,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5007,
            "text": "در جوار درختان سدر بى‌خار",
            "numberInSurah": 28,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5008,
            "text": "و موزهاى توبرتو",
            "numberInSurah": 29,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5009,
            "text": "و سايه گسترده‌",
            "numberInSurah": 30,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5010,
            "text": "و آبى ريزان‌",
            "numberInSurah": 31,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5011,
            "text": "و ميوه بسيار",
            "numberInSurah": 32,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5012,
            "text": "كه نه پايان‌پذير است و نه بازداشته‌",
            "numberInSurah": 33,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5013,
            "text": "و دوشيزگان گرامى‌",
            "numberInSurah": 34,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5014,
            "text": "ما ايشان را به ابداع آفريده‌ايم‌",
            "numberInSurah": 35,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5015,
            "text": "و دوشيزه‌شان داشته‌ايم‌",
            "numberInSurah": 36,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5016,
            "text": "همسر دوست و هم سن و سال‌",
            "numberInSurah": 37,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5017,
            "text": "خاص اصحاب يمين‌",
            "numberInSurah": 38,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 469,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5018,
            "text": "جماعتى بسيار از پيشينيان‌",
            "numberInSurah": 39,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5019,
            "text": "و جماعتى بسيار از واپسينان‌",
            "numberInSurah": 40,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5020,
            "text": "و اصحاب شمال، چه حال دارند اصحاب شمال‌",
            "numberInSurah": 41,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5021,
            "text": "در ميان آتشباد و آب جوشند",
            "numberInSurah": 42,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5022,
            "text": "و سايه‌اى از دوده‌",
            "numberInSurah": 43,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5023,
            "text": "كه نه خنك است و نه خوش‌",
            "numberInSurah": 44,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5024,
            "text": "ايشان پيش از اين نازپرورده بودند",
            "numberInSurah": 45,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5025,
            "text": "بر گناه بزرگ مداومت مى‌كردند",
            "numberInSurah": 46,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5026,
            "text": "و مى‌گفتند آيا چون مرديم و خاك و استخوانها[ى پوسيده‌] شديم، آيا برانگيخته خواهيم شد؟",
            "numberInSurah": 47,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5027,
            "text": "و همچنين نياكان ما؟",
            "numberInSurah": 48,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5028,
            "text": "بگو كه پيشينيان و واپسينان‌",
            "numberInSurah": 49,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5029,
            "text": "براى موعد روزى معين گرد آيند",
            "numberInSurah": 50,
            "juz": 27,
            "manzil": 7,
            "page": 535,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5030,
            "text": "سپس شمايان اى گمراهان دروغ انگار [و اهل انكار]",
            "numberInSurah": 51,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5031,
            "text": "خورندگان از درخت زقوميد",
            "numberInSurah": 52,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5032,
            "text": "و شكم انباران از آن‌",
            "numberInSurah": 53,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5033,
            "text": "و بر آن آب جوش آشامندگانيد",
            "numberInSurah": 54,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5034,
            "text": "و مانند نوشيدن شتران عطش زده مى‌آشاميد",
            "numberInSurah": 55,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5035,
            "text": "اين پيشكش ايشان در روز جزاست‌",
            "numberInSurah": 56,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5036,
            "text": "ما شما را آفريده‌ايم، پس چرا تصديق نمى‌كنيد؟",
            "numberInSurah": 57,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5037,
            "text": "آيا انديشيده‌ايد آنچه [از منى‌] را كه [در رحمها] مى‌ريزيد",
            "numberInSurah": 58,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5038,
            "text": "آيا شما آن را آفريده‌ايد يا ما آفريننده‌ايم‌",
            "numberInSurah": 59,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5039,
            "text": "ما در ميان شما مرگ را مقدر داشته‌ايم، و ما درمانده نيستيم‌",
            "numberInSurah": 60,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5040,
            "text": "كه همانندان شما را جانشين شما گردانيم، و شما را در هيئتى كه نمى‌دانيد باز آفرينيم‌",
            "numberInSurah": 61,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5041,
            "text": "و به راستى نشاه نخستين را شناخته‌ايد، پس چرا پند نمى‌گيريد؟",
            "numberInSurah": 62,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5042,
            "text": "آيا انديشيده‌ايد در آنچه مى‌كاريد؟",
            "numberInSurah": 63,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5043,
            "text": "آيا شما آن را مى‌رويانيد يا ما روياننده‌ايم؟",
            "numberInSurah": 64,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5044,
            "text": "اگر خواهيم آن را خرد و ريز گردانيم و شما حسرت‌زده شويد",
            "numberInSurah": 65,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5045,
            "text": "[و گوييد] ما غرامت ديدگانيم‌",
            "numberInSurah": 66,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5046,
            "text": "بلكه ما بى‌بهرگانيم‌",
            "numberInSurah": 67,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5047,
            "text": "آيا انديشيده‌ايد به آبى كه مى‌آشاميد؟",
            "numberInSurah": 68,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5048,
            "text": "آيا شما آن را از ابر فرو فرستاده‌ايد يا ما فرو فرستنده‌ايم؟",
            "numberInSurah": 69,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5049,
            "text": "اگر خواهيم آن را شور و تلخ گردانيم پس چرا سپاس نمى‌گزاريد؟",
            "numberInSurah": 70,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5050,
            "text": "آيا انديشيده‌ايد به آتشى كه مى‌افروزيد؟",
            "numberInSurah": 71,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5051,
            "text": "آيا شما درختش را آفريده‌ايد يا ما آفريننده‌ايم؟",
            "numberInSurah": 72,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5052,
            "text": "ما آن را پندآموزى ساخته‌ايم و توشه‌اى براى رهروان‌",
            "numberInSurah": 73,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5053,
            "text": "پس به نام پروردگارت كه بزرگ است تسبيح گوى‌",
            "numberInSurah": 74,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 470,
            "hizbQuarter": 214,
            "sajda": false
          },
          {
            "number": 5054,
            "text": "سوگند مى‌خورم به منزلگاههاى ستارگان‌",
            "numberInSurah": 75,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5055,
            "text": "و آن اگر بدانيد سوگندى عظيم است‌",
            "numberInSurah": 76,
            "juz": 27,
            "manzil": 7,
            "page": 536,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5056,
            "text": "آن قرآنى كريم است‌",
            "numberInSurah": 77,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5057,
            "text": "در كتابى نهفته‌",
            "numberInSurah": 78,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5058,
            "text": "جز پاكيزگان به آن دسترس ندارند",
            "numberInSurah": 79,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5059,
            "text": "فرو فرستاده‌اى از سوى پروردگار جهانيان است‌",
            "numberInSurah": 80,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5060,
            "text": "آيا شما در كار اين سخن سستى مى‌ورزيد؟",
            "numberInSurah": 81,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5061,
            "text": "و سپاس روزيتان را چنان كرده‌ايد كه [آن را] انكار مى‌كنيد",
            "numberInSurah": 82,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5062,
            "text": "پس چرا چون جان به گلوگاه رسد",
            "numberInSurah": 83,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5063,
            "text": "و شما در آن هنگام نظاره‌گريد",
            "numberInSurah": 84,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5064,
            "text": "و ما به آن [جان شما] از شما نزديكتريم ولى شما به چشم بصيرت نمى‌نگريد",
            "numberInSurah": 85,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5065,
            "text": "پس چرا اگر شما جزا دادنى نيستيد",
            "numberInSurah": 86,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5066,
            "text": "اگر راست مى‌گوييد چرا آن را باز نمى‌گردانيد؟",
            "numberInSurah": 87,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5067,
            "text": "سپس آنگاه اگر از مقربان باشد",
            "numberInSurah": 88,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5068,
            "text": "رهايش و گشايش است و بهشت پرناز و نعمت‌",
            "numberInSurah": 89,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5069,
            "text": "و اما اگر از اصحاب يمين باشد",
            "numberInSurah": 90,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5070,
            "text": "پس سلام بر تو باد از اصحاب يمين‌",
            "numberInSurah": 91,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5071,
            "text": "و اما اگر از منكران گمراه باشد",
            "numberInSurah": 92,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5072,
            "text": "پيشكش [او] از آب‌جوشان است‌",
            "numberInSurah": 93,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5073,
            "text": "و ورود به جهنم‌",
            "numberInSurah": 94,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5074,
            "text": "اين همانا حق‌اليقين است‌",
            "numberInSurah": 95,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5075,
            "text": "پس به نام پروردگارت كه بزرگ است، تسبيح بگوى‌",
            "numberInSurah": 96,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 471,
            "hizbQuarter": 215,
            "sajda": false
          }
        ]
      },
      {
        "number": 57,
        "name": "سُورَةُ الحَدِيدِ",
        "englishName": "Al-Hadid",
        "englishNameTranslation": "The Iron",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5076,
            "text": "آنچه در آسمانها و زمين است، خداوند را تسبيح مى‌گويد، و اوست پيروزمند فرزانه‌",
            "numberInSurah": 1,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5077,
            "text": "او راست فرمانروايى آسمانها و زمين، كه زنده مى‌دارد و مى‌ميراند، و او بر هر كارى تواناست‌",
            "numberInSurah": 2,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5078,
            "text": "اوست اول و آخر و ظاهر و باطن و او به همه چيز داناست‌",
            "numberInSurah": 3,
            "juz": 27,
            "manzil": 7,
            "page": 537,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5079,
            "text": "اوست كه آسمانها و زمين را در شش روز آفريد و سپس بر عرش استيلاء يافت، مى‌داند كه چه چيزى وارد زمين مى‌شود و چه چيزى از آن بيرون مى‌رود، و آنچه از آسمان فرو مى‌آيد و آنچه به آن فرا مى‌رود، و او هرجا كه باشيد با شماست، و خداوند به آنچه مى‌كنيد بيناست‌",
            "numberInSurah": 4,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5080,
            "text": "فرمانروايى آسمانها و زمين او راست، و كارها به خداوند باز گردانده شود",
            "numberInSurah": 5,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5081,
            "text": "از شب مى‌كاهد و بر روز مى‌افزايد و از روز مى‌كاهد و بر شب مى‌افزايد و او به راز دلها داناست‌",
            "numberInSurah": 6,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5082,
            "text": "به خداوند و پيامبر او ايمان بياوريد و از آنچه شما را در آن جانشين ساخته است انفاق كنيد، آرى كسانى از شما كه ايمان آورده و انفاق كنند پاداش بزرگى دارند",
            "numberInSurah": 7,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5083,
            "text": "و شما را چه مى‌شود كه به خداوند ايمان نمى‌آوريد، حال آنكه پيامبر شما را دعوت مى‌كند كه به پروردگارتان ايمان آوريد، و به راستى اگر ايمان داشته باشيد از شما پيمانتان را گرفته است‌",
            "numberInSurah": 8,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5084,
            "text": "اوست كه بر بنده‌اش آياتى روشنگر فرو فرستاده است تا شما را از تاريكيها[ى جهل و كفر] به سوى نور [علم و ايمان‌] بازبرد، و بى‌گمان خداوند در حق شما رئوف مهربان است‌",
            "numberInSurah": 9,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5085,
            "text": "و شما را چه مى‌شود كه در راه خدا انفاق نمى‌كنيد، و حال آنكه ميراث آسمانها و زمين از خداوند است، و از ميان شما كسى كه پيش از پيروزى [فتح مكه‌] انفاق و كارزار كرده باشد [با ديگران‌] برابر نيست، اينان بلندمرتبه‌ترند از كسانى كه بعد از آن انفاق و كارزار كرده‌اند، و همه را خداوند وعده نيكى [/بهشت‌] داده است، و خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 10,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 472,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5086,
            "text": "كيست كه در راه خداوند قرض‌الحسنه دهد، تا برايش دوچندان سازد و براى او پاداشى ارجمند است‌",
            "numberInSurah": 11,
            "juz": 27,
            "manzil": 7,
            "page": 538,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5087,
            "text": "روزى كه مردان مؤمن و زنان مؤمن را بينى كه نورشان پيشاپيش آنان و در سمت راستشان مى‌شتابد، امروز بشارت شما باغهايى است كه از فرودست آن جويباران جارى است و جاودانه در آن هستيد، اين همانا رستگارى بزرگ است‌",
            "numberInSurah": 12,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5088,
            "text": "روزى كه مردان منافق و زنان منافق به مؤمنان گويند به خاطر ما درنگ كنيد تا از نورتان روشنى فراگيريم، گفته شود پس پشت خويش باز گرديد و نورى بجوييد، سپس ميان آنان ديوارى زده شود كه درى دارد، از درون‌سو در آن رحمت است و از برون سوى آن عذاب است‌",
            "numberInSurah": 13,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5089,
            "text": "آنان را ندا دهند كه مگر ما با شما نبوديم؟ گويند چرا ولى شما خودتان را در بلا افكنديد و چشم به راه [حوادث سوء براى مسلمانان‌] بوديد و شك و شبهه ورزيديد و آرزوها شما را فريفت، تا آنكه فرمان الهى [/مرگ‌] در رسيد، و [شيطان‌] فريبكار شما را در حق خداوند فريب داد",
            "numberInSurah": 14,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5090,
            "text": "پس امروز نه از شما و نه از كافران، بدل و بلاگردانى پذيرفته نشود، سرا و سرانجام شما آتش [دوزخ‌] است، او پناه شماست و چه بد سرانجامى است‌",
            "numberInSurah": 15,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 215,
            "sajda": false
          },
          {
            "number": 5091,
            "text": "آيا مؤمنان را هنگام آن نرسيده است كه دلهايشان به ياد خداوند، و آنچه از حق كه نازل شده است، خشوع يابد، و مانند كسانى نباشند كه پيشترها به آنان كتاب آسمانى داده شده است، و سپس روزگارشان دراز نمود، آنگاه دلهايشان سخت شد و بسيارى از آنان نافرمان بودند",
            "numberInSurah": 16,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5092,
            "text": "بدانيد كه خداوند زمين را پس از پژمردنش زنده مى‌دارد، به راستى كه آيات [خود] را براى شما به روشنى بيان كرديم، باشد كه تعقل كنيد",
            "numberInSurah": 17,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5093,
            "text": "بى‌گمان مردان صدقه‌بخش و زنان صدقه‌بخش و كسانى كه در راه خدا قرض‌الحسنه داده‌اند، براى آنان دوچندان شود، و براى آنان پاداشى ارجمند است‌",
            "numberInSurah": 18,
            "juz": 27,
            "manzil": 7,
            "page": 539,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5094,
            "text": "و كسانى كه به خداوند و پيامبرانش ايمان آورده‌اند، اينانند كه صديقند، و شهيدان نزد پروردگارشان هستند، و اجرشان و نورشان را دارند، و كسانى كه كفرورزيده‌اند و آيات ما را دروغ انگاشته‌اند اينانند كه دوزخى‌اند",
            "numberInSurah": 19,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 473,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5095,
            "text": "بدانيد كه همانا زندگانى دنيا بازيچه و سرگرمى، و زيور و فخرفروشى در ميان شما و افزون طلبى در اموال و اولاد است، همانند بارانى كه كشاورزان را گياه آن خوش آيد، سپس پژمرده شود و آن را زرد شده بينى، سپس خرد و ريز شود، و در آخرت هم عذابى شديد هست و هم آمرزش و خشنودى از جانب خداوند، و زندگانى دنيا جز مايه فريب نيست‌",
            "numberInSurah": 20,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5096,
            "text": "به سوى آمرزشى از پروردگارتان، و بهشتى كه پهناى آن همچند پهناى آسمان و زمين است و براى مؤمنان به خداوند و پيامبران او آماده شده است، بشتابيد، اين بخشش الهى است كه به هر كس خواهد ارزانى‌اش دارد، و خداوند داراى بخشش و بخشايش بيكران است‌",
            "numberInSurah": 21,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5097,
            "text": "هيچ مصيبتى در زمين [به جسم و مال‌] و به جانهاى شما نرسد مگر آنكه پيش از آنكه آن را آفريده باشيم در كتابى [ثبت‌] است، بى‌گمان اين امر بر خداوند آسان است‌",
            "numberInSurah": 22,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5098,
            "text": "تا آنكه بر آنچه از دست شما رود اندوه مخوريد و بر آنچه به شما بخشد شادمانى مكنيد، و خداوند هيچ متكبر فخر فروشى را دوست ندارد",
            "numberInSurah": 23,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5099,
            "text": "همان كسانى كه بخل مى‌ورزند و مردمان را نيز به بخل مى‌فرمايند، و هر كس روى بر تابد، [بداند كه‌] خداوند بى نياز ستوده است‌",
            "numberInSurah": 24,
            "juz": 27,
            "manzil": 7,
            "page": 540,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5100,
            "text": "به راستى كه پيامبرانمان را همراه با پديده‌هاى روشنگر فرستاديم و همراه آنان كتاب آسمانى و سنجه فرو فرستاديم، تا مردم به دادگرى برخيزند، و آهن را پديد آورديم كه در آن [مايه‌] ستيز و صلابت است و نيز سودهايى براى مردم، تا سرانجام خداوند معلوم بدارد كه چه كسى در نهان [جانب‌] او و پيامبرانش را يارى مى‌دهد، بى‌گمان خداوند نيرومند پيروزمند است‌",
            "numberInSurah": 25,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 474,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5101,
            "text": "و به راستى نوح و ابراهيم را به رسالت فرستاديم، و در زاد و رود آن دو، پيامبرى و كتاب آسمانى قرار داديم، آنگاه بعضى از آنان رهيافته و بسيارى از آنان نافرمانند",
            "numberInSurah": 26,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5102,
            "text": "سپس از پى ايشان پيامبرانمان را آورديم، و عيسى بن مريم را از پى آورديم، و به او انجيل داديم و در دل كسانى كه از او پيروى كردند رأفت و رحمت قرار داديم، و رهبانيتى را به صورت بدعتى در پيش گرفتند كه ما آن را بر ايشان واجب نكرده بوديم، مگر آنكه در طلب خشنودى الهى آن را در پيش گرفته بودند، ولى چنانكه شايسته رعايت بود، رعايتش نكردند، آنگاه به مؤمنان آنان پاداششان را بخشيديم و بسيارى از آنان نافرمانند",
            "numberInSurah": 27,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5103,
            "text": "اى مؤمنان از خداوند پروا كنيد و به پيامبر او ايمان آوريد، تا بهره دوچندان از رحمت خويش به شما ارزانى دارد، و براى شما نورى قرار دهد كه با آن [به درستى‌] راه رويد و شما را بيامرزد، و خداوند آمرزگار مهربان است‌",
            "numberInSurah": 28,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          },
          {
            "number": 5104,
            "text": "تا اهل كتاب بدانند كه صاحب اختيار چيزى از بخشش و بخشايش الهى نيستند، و بخشش و بخشايش به دست خداوند است، به هر كس كه خواهد ارزانى‌اش دارد، و خداوند داراى بخشش بيكران است‌",
            "numberInSurah": 29,
            "juz": 27,
            "manzil": 7,
            "page": 541,
            "ruku": 475,
            "hizbQuarter": 216,
            "sajda": false
          }
        ]
      },
      {
        "number": 58,
        "name": "سُورَةُ المُجَادلَةِ",
        "englishName": "Al-Mujaadila",
        "englishNameTranslation": "The Pleading Woman",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5105,
            "text": "به راستى كه خداوند سخن زنى را كه با تو درباره همسرش مجادله مى‌كرد و به خداوند شكايت حال خود مى‌گفت، شنيد، و خداوند گفت و گوى شما دو تن را مى‌شنود، چرا كه خداوند شنواى بيناست‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5106,
            "text": "كسانى كه از ميان شما با زنانشان ظهار مى‌كنند، [بدانند كه‌] آن زنان [با اين ماجرا در حكم‌] مادران آنان نيستند، مادران آنان جز كسانى كه آنان را زاده‌اند، نيستند، و ايشان [با اين عمل‌] سخنى ناپسند و ناحق مى‌گويند، و بى‌گمان خداوند بخشاينده آمرزگار است‌",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5107,
            "text": "و كسانى كه با زنانشان ظهار مى‌كنند، سپس از آنچه گفته‌اند برمى‌گردند [و مى‌خواهند به همسر خود بازگردند] بايد پيش از آنكه با همديگر تماس گيرند، برده‌اى آزاد كنند، اين اندرزى است كه به شما داده مى‌شود، و خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5108,
            "text": "و هر كس كه [امكان آن را] نيافت، بايد كه پيش از آنكه با همديگر تماس گيرند، دو ماه پى در پى روزه بگيرد، سپس [بازهم‌] اگر نتوانست [روزه بگيرد]، در آن صورت بايد به اطعام شصت فقير بپردازد، اين [كفاره‌] براى آن است كه به [امر] خداوند و پيامبر او ايمان آوريد، و اين احكام الهى است، و براى كافران عذابى دردناك [مقرر] است‌",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5109,
            "text": "بى‌گمان كسانى كه با [امر] خداوند و پيامبر او مخالفت مى‌كنند، سرنگون شوند، چنانكه پيشينيانشان هم سرنگون شدند، و به راستى آياتى روشنگر فرو فرستاده‌ايم، و براى كافران عذابى خفت‌بار [درپيش‌] است‌",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5110,
            "text": "روزى كه خداوند همگى آنان را [از گور] برانگيزد، سپس آنان را به [حقيقت و نتيجه‌] آنچه كرده‌اند آگاه سازد، خداوند آن را در شمار آورده است اما [خود] آنان فراموشش كرده‌اند، و خداوند بر هر چيز گواه است‌",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 542,
            "ruku": 476,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5111,
            "text": "آيا نينديشيده‌اى كه خداوند آنچه در آسمانها و آنچه در زمين است مى‌داند، هيچ رازگويى سه تن نباشد مگر آنكه او چهارمين آنان است، و نه [رازگويى‌] پنج تن مگر آنكه او ششمين آنان است، و نه كمتر از اين و نه بيشتر مگر آنكه او هر جا كه باشند با ايشان است، سپس در روز قيامت آنان را به [نتيجه و حقيقت‌] كار و كردارشان آگاه مى‌سازد، بى‌گمان خداوند به هر چيزى داناست‌",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5112,
            "text": "آيا ننگريسته‌اى در كار كسانى كه از نجوا نهى شده‌اند، سپس به آنچه از آن نهى شده‌اند، باز مى‌گردند و به گناه و ستمكارى و نافرمانى از پيامبر باهم نجوا مى‌كنند، و چون به نزدت آيند به نحوى به تو درود مى‌فرستند كه خداوند آن گونه به تو درود نفرستاده است، و در دلهايشان گويند چرا به خاطر آنچه مى‌گوييم خداوند عذابمان نمى‌كند؟ جهنم برايشان كافى است كه به آن اندر آيند، و چه بد سرانجامى است‌",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5113,
            "text": "اى مؤمنان چون رازگويى كنيد، به قصد گناه و ستمكارى و نافرمانى از پيامبر نجوا مكنيد، و به قصد نيكى و تقوا نجوا كنيد، و از خداوندى كه نزد او محشور مى‌گرديد پروا كنيد",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5114,
            "text": "جز اين نيست كه نجوا از سوى شيطان است تا مؤمنان را غمگين كند، و هيچ زيان رسان به آنان نيست، مگر به اذن الهى، و مؤمنان بايد فقط بر خداوند توكل كنند",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5115,
            "text": "اى مؤمنان چون به شما گفته شود در مجالس جا باز كنيد، باز كنيد، كه خداوند در كار شما گشايش آورد، و چون گفته شود برخيزيد، برخيزيد، خداوند از ميان شما مؤمنان و دانش يافتگان را به درجاتى رفعت دهد و خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 543,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5116,
            "text": "اى مؤمنان چون خواهيد كه با پيامبر راز گوييد، پيش از نجوايتان صدقه‌اى تقديم داريد اين براى شما بهتر و پاكيزه‌تر است، اما اگر چيزى نيافتيد، بى‌گمان خداوند آمرزگار مهربان است‌",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5117,
            "text": "[سپس كه كسى جز على ع توفيق نيافت فرمود] آيا ترسيديد كه پيش از نجوايتان صدقاتى تقديم داريد؟ حال كه چنين نكرديد و خداوند هم از شما درگذشت، پس نماز را برپا داريد و زكات را بپردازيد و از خداوند و پيامبرش اطاعت كنيد، و خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 477,
            "hizbQuarter": 217,
            "sajda": false
          },
          {
            "number": 5118,
            "text": "آيا ننگريسته‌اى كسانى را كه با قومى كه خداوند بر آنان خشم آورده است، و نه از شما هستند و نه از آنان [يهوديان‌]، دوستى ورزيده‌اند؟ و آگاهانه به دروغ سوگند مى‌خورند",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5119,
            "text": "خداوند براى آنان عذابى سهمگين آماده ساخته است، آنان عملكردشان بد است‌",
            "numberInSurah": 15,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5120,
            "text": "سوگندهايشان را سپر بلا كرده‌اند، و [مردمان را] از راه خدا باز مى‌دارند، پس عذابى خفت‌بار [در پيش‌] دارند",
            "numberInSurah": 16,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5121,
            "text": "اموال و اولادشان به هيچ روى از [عذاب‌] خدا بازشان ندارد، اينان دوزخى‌اند و جاودانه در آنند",
            "numberInSurah": 17,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5122,
            "text": "روزى كه خداوند همگى آنان را برانگيزد، آنگاه [مشركان‌] براى او به همان گونه سوگند ياد مى‌كنند كه براى شما ياد مى‌كنند و گمان مى‌كنند كه حقى در دست دارند، بدانيد كه آنان دروغگويانند",
            "numberInSurah": 18,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5123,
            "text": "شيطان بر آنان دست يافت، سپس ياد خدا را از خاطر آنان برد، اينان حزب شيطانند، هان بى‌گمان حزب شيطان زيانكارانند",
            "numberInSurah": 19,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5124,
            "text": "بى‌گمان كسانى كه با [امر] خداوند و پيامبر او مخالفت مى‌كنند، اينان از زمره فرومايگانند",
            "numberInSurah": 20,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5125,
            "text": "خداوند مقرر داشته است كه من و پيامبرانم پيروز گرديم، بى‌گمان خداوند نيرومند پيروزمند است‌",
            "numberInSurah": 21,
            "juz": 28,
            "manzil": 7,
            "page": 544,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5126,
            "text": "قومى نيابى كه به خداوند و روز بازپسين ايمان داشته باشند و با كسانى كه با خداوند و پيامبر او مخالفت مى‌ورزند، دوستى كنند، ولو آنكه پدرانشان يا فرزندانشان يا برادرانشان يا خاندانشان باشند اينانند كه [خداوند] در دلهايشان [نقش‌] ايمان نگاشته است، و به فيضى از جانب خويش استوارشان داشته است، و [ايشان را] به بوستانهايى درآورد كه جويباران از فرودست آن جارى است، جاودانه در آنند، خداوند از آنان خشنود است، و آنان هم از او خشنودند، اينان خدا خواهانند، بدانيد كه خدا خواهان رستگارانند",
            "numberInSurah": 22,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 478,
            "hizbQuarter": 218,
            "sajda": false
          }
        ]
      },
      {
        "number": 59,
        "name": "سُورَةُ الحَشۡرِ",
        "englishName": "Al-Hashr",
        "englishNameTranslation": "The Exile",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5127,
            "text": "آنچه در آسمانها و آنچه در زمين است خداوند را تسبيح مى‌گويد، و او پيروزمند فرزانه است‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5128,
            "text": "اوست كه كافران اهل كتاب [يهوديان بنى‌نضير] را از خانه و كاشانه‌شان در آغاز هنگامه رهسپارى انبوه آواره كرد، گمان نمى‌برديد كه بيرون روند، و [خودشان هم‌] گمان مى‌كردند كه دژهايشان باز دارنده آنان در برابر امر و عقوبت الهى است، و امر [/عذاب‌] الهى از جايى كه گمانش را نمى‌بردند بر آنان فرود آمد و در دلهايشان هراس انداخت، [چندانكه‌] خانه‌هايشان را به دستهاى خود و دستان مؤمنان ويران مى‌كردند، پس اى ديده‌وران عبرت گيريد",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5129,
            "text": "و اگر خداوند جلاى وطن را بر آنان مقرر نداشته بود، در دنيا [به عذابى سهمگين‌] معذبشان مى‌داشت، و براى آنان در آخرت عذاب آتش دوزخ است‌",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 545,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5130,
            "text": "اين از آن است كه با [امر] خداوند و پيامبرش مخالفت كردند، و هر كس با امر خداوند مخالفت كند، بى‌گمان خداوند سخت كيفر است‌",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5131,
            "text": "هر چه از درختان خرما بريديد يا آن را ايستاده با ريشه‌هايش باقى گذارديد، همه به اذن الهى بود تا نافرمانان را خوار بدارد",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5132,
            "text": "و هر چه خداوند از ايشان به [صورت فى‌ء] عايد پيامبرش گرداند، شما در آن نه اسبان را به تاخت در آورديد و نه شتران را، بلكه خداوند پيامبرانش را بر هر كس كه خواهد سلطه دهد، و خداوند بر هر كارى تواناست‌",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5133,
            "text": "آنچه خداوند [به صورت فى‌ء] از اموال اهل آباديها عايد پيامبرش گرداند، خاص خداوند و پيامبر و خويشاوندان و يتيمان و بينوايان و در راه ماندگان است، تا در ميان توانگران شما دستگردان نشود، و آنچه پيامبر شما را دهد آن را بپذيريد و آنچه شما را از آن بازدارد، از آن دست بداريد، و از خداوند پروا كنيد، كه بى‌گمان خداوند سخت كيفر است‌",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5134,
            "text": "براى بينوايان از مهاجران است كه از خانه و كاشانه‌هايشان و ملك و مالهايشان رانده شده‌اند، و در طلب بخشش و خشنودى خداوندند و [دين‌] خداوند و پيامبر او را يارى مى‌دهند، اينانند كه راستگويانند",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5135,
            "text": "و كسانى كه پيش از آنان در سراى اسلام [مدينه‌] جاى گرفتند و ايمان را پذيرفتند، كسانى را كه به سوى آنان هجرت كرده‌اند، دوست مى‌دارند و نسبت به آنچه به ايشان داده شده است، در دل خود احساس نياز نمى‌كنند، و [آنان را] ولو خود نيازمندى داشته باشند، بر خود بر مى‌گزينند، و كسانى كه از آزمندى نفس خويش در امان مانند، آنانند كه رستگارانند",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 546,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5136,
            "text": "و كسانى كه پس از آنان آمده‌اند گويند پروردگارا ما و برادرانمان را كه در ايمان بر ما سبقت دارند، بيامرز، و در دلهاى ما نسبت به مؤمنان كينه‌اى مگذار، پروردگارا تويى كه رئوف مهربانى‌",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 479,
            "hizbQuarter": 218,
            "sajda": false
          },
          {
            "number": 5137,
            "text": "آيا ننگريسته‌اى كسانى را كه نفاق ورزيدند به دوستانشان از اهل كتاب كه كفرورزيدند، گفتند اگر شما رانده شويد، ما نيز همراه شما بيرون مى‌آييم، و به زيان شما هرگز از امر هيچ كس اطاعت نخواهيم كرد، و اگر با شما كارزار شود، حتما شما را يارى خواهيم كرد، خداوند شاهد است كه آنان دروغگو هستند",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5138,
            "text": "اگر آنان [يهوديان بنى‌نضير] رانده شوند، ايشان همراه آنان بيرون نروند، و اگر با آنان كارزار شود، ايشان را يارى ندهند، و اگر بخواهند ياريشان دهند، پشت كنند، سپس از هيچ كس يارى نيابند",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5139,
            "text": "بى‌شك شما در دلهاى آنان هراس‌انگيزتريد از خداوند، اين از آن است كه ايشان قومى بى‌تميزند",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5140,
            "text": "هرگز همگى با شما كارزار نكنند مگر در ميان برزنهاى محصور، يا از پشت ديوارها، ستيز و صلابت آنان در ميان خودشان سخت است، آنان را همدست مى‌انگارى، حال آنكه دلهايشان پراكنده است، اين از آن است كه قومى نابخردند",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5141,
            "text": "همانند پيشينيان نزديكشان كه عقوبت كار [نادرست‌]شان را چشيدند و عذابى دردناك [در پيش‌] دارند",
            "numberInSurah": 15,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5142,
            "text": "همانند شيطان كه به انسان گويد كافر شو، و چون كافر شود، گويد من از تو برى و بر كنارم، كه من از خداوند -پروردگار جهانيانمى‌ترسم‌",
            "numberInSurah": 16,
            "juz": 28,
            "manzil": 7,
            "page": 547,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5143,
            "text": "و سرانجام آن دو چنين است كه هر دو در آتش جهنم‌اند، و جاودانه در آنند، و اين جزاى ستمكاران [مشرك‌] است‌",
            "numberInSurah": 17,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 480,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5144,
            "text": "اى مؤمنان از خداوند پروا كنيد و هر كس بنگرد كه براى فردا چه پيش انديشى كرده است، و از خداوند پروا كنيد، چرا كه خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 18,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5145,
            "text": "و همانند كسانى كه خداوند را فراموش كردند، مباشيد كه خداوند هم [تيمار] خودشان را از يادشان برد، اينانند كه نافرمانند",
            "numberInSurah": 19,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5146,
            "text": "دوزخيان و بهشتيان برابر نيستند، بهشتيانند كه رستگارند",
            "numberInSurah": 20,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5147,
            "text": "اگر اين قرآن را بر كوهى فرو فرستاده بوديم، بى‌شك آن را از ترس خداوند خاكسار و فرو پاشيده مى‌ديدى، و اينها مثلهايى است كه براى مردم مى‌زنيم باشد كه انديشه كنند",
            "numberInSurah": 21,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5148,
            "text": "او خداوندى است كه خدايى جز او نيست، داناى پنهان و پيدا، اوست خداى رحمان مهربان‌",
            "numberInSurah": 22,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5149,
            "text": "اوست خداوندى كه خدايى جز او نيست، فرمانرواى قدوس سلام مؤمن مهيمن پيروزمند جبار صاحب كبرياست، پاك و برتر است خداوند از آنچه براى او شريك مى‌انگارند",
            "numberInSurah": 23,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5150,
            "text": "اوست خداوند آفريدگار پديدآور صورتگر، او راست نامهاى نيك، آنچه در آسمانها و زمين است او را تسبيح مى‌گويد، و اوست پيروزمند فرزانه‌",
            "numberInSurah": 24,
            "juz": 28,
            "manzil": 7,
            "page": 548,
            "ruku": 481,
            "hizbQuarter": 219,
            "sajda": false
          }
        ]
      },
      {
        "number": 60,
        "name": "سُورَةُ المُمۡتَحنَةِ",
        "englishName": "Al-Mumtahana",
        "englishNameTranslation": "She that is to be examined",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5151,
            "text": "اى مؤمنان اگر براى جهاد در راه من و طلب خشنودى من از [وطن خود] بيرون آمده‌ايد، دشمن من و دشمن خودتان را دوست مگيريد، كه به آنها مهربانى كنيد، و حال آنكه آنان به دين حقى كه براى شما آمده است، كفر و انكار ورزيده‌اند و پيامبر و شما را آواره كرده‌اند، چرا كه به خداوند -پروردگارتانايمان داريد، [شماييد كه‌] با آنان سر و سر دوستانه داريد و من به آنچه پنهان داريد و آنچه آشكار داريد، آگاه‌ترم، و هر كس از شما كه چنين كند به راستى كه از راه راست گم گشته است‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5152,
            "text": "اگر بر شما دست يابند دشمنان شما خواهند بود و دست و زبانشان را به بدى در حق شما مى‌گشايند، و خوش دارند كه شما كفر بورزيد",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5153,
            "text": "خويشاوندانتان و همچنين فرزندانتان به شما سود نمى‌رسانند [و] در روز قيامت در ميان شما داورى كند و خداوند به آنچه مى‌كنيد بيناست‌",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5154,
            "text": "براى شما در ابراهيم و همراهان او سرمشقى نيكوست، آنگاه كه به قومشان گفتند و ما از شما و آنچه به جاى خداوند مى‌پرستيد، برى و بركناريم، ما منكر شماييم و همواره در ميان ما و شما دشمنى و كينه پديد آمده است تا آنكه فقط به خداوند ايمان آوريد، مگر در اين سخن ابراهيم به پدرش كه حتما براى تو آمرزش خواهم طلبيد، و براى تو در برابر خداوند صاحب اختيار چيزى نيستم، پروردگارا بر تو توكل كرده‌ايم و به تو روى آورده‌ايم و سير و سرانجام [همه‌] به سوى توست‌",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5155,
            "text": "پروردگارا ما را زيردست كافران مگردان و ما را بيامرز، پروردگارا تويى كه پيروزمند فرزانه‌اى‌",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 549,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5156,
            "text": "به راستى براى شما در آنان سرمشقى نيكوست، براى كسى كه به خداوند و روز بازپسين اميد داشته باشد، و هر كس روى برتابد [بداند] كه خداوند بى‌نياز ستوده است‌",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 482,
            "hizbQuarter": 219,
            "sajda": false
          },
          {
            "number": 5157,
            "text": "چه بسا خداوند در ميان شما و كسانى از آنان كه باهم دشمنى داريد، مهربانى پديد آورد، و خداوند تواناست، و خداوند آمرزگار مهربان است‌",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5158,
            "text": "خداوند شما را از كسانى كه با شما در كار دين كارزار نكرده‌اند، و شما را از خانه و كاشانه‌تان آواره نكرده‌اند، نهى نمى‌كند از اينكه در حقشان نيكى كنيد و با آنان دادگرانه رفتار كنيد، بى‌گمان خداوند دادگران را دوست دارد",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5159,
            "text": "خداوند فقط شما را از كسانى كه با شما در كار دين كارزار كرده‌اند و شما را از خانه و كاشانه‌تان آواره كرده‌اند و براى راندنتان [با ديگران‌] همدستى كرده‌اند، نهى مى‌كند از اينكه دوستشان بداريد، و هر كس دوستشان بدارد، آنانند كه ستمكار [مشرك‌] هستند",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5160,
            "text": "اى مؤمنان هر گاه زنان مؤمن مهاجر به نزد شما آيند، آنان را بيازماييد، خداوند به [حقيقت‌] ايمانشان آگاه‌تر است، پس اگر آنان را مؤمن شناختيد ديگر آنان را به سوى كفار باز مگردانيد، نه ايشان بر آنان حلال هستند و نه آنان برايشان حلالند، و به آنان آنچه خرج كرده‌اند بدهيد، و بر شما گناهى نيست كه پس از آنكه مهرهايشان را پرداختيد، با آنان ازدواج كنيد، و دست در دامان زنان كافر مزنيد، و [چون زنان شما نزد آنان روند] آنچه خرج كرده‌ايد از آنان [كافران‌] طلب كنيد و آنان هم آنچه خرج كرده‌اند از شما طلب كنند، اين حكم الهى است كه در ميان شما داورى مى‌كند و خداوند داناى فرزانه است‌",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5161,
            "text": "و اگر از دست شما زنى به سوى كافران رود، و سپس با آنان تلافى كرديد و غنيمت گرفتيد، آنگاه به كسانى كه همسرانشان [به سوى كفار] رفته‌اند، معادل خرجى را كه كرده‌اند [از غنايم‌] بپردازيد، و از خداوندى كه شما به او ايمان داريد پروا كنيد",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 550,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5162,
            "text": "اى پيامبر چون زنان مؤمن به نزد تو آمدند كه با تو بيعت كنند كه براى خداوند شريكى نياورند و دزدى نكنند و زنا نكنند و فرزندانشان را نكشند و فرزندى را از راه بهتان به دست نياورند كه نسبت آن را به دست و پاى خويش دهند، و در كار خيرى از تو نافرمانى نكنند، پس با آنان بيعت كن و برايشان از خداوند آمرزش بخواه، چرا كه خداوند آمرزگار مهربان است‌",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5163,
            "text": "اى مؤمنان قومى را كه خداوند بر آنان خشم آورده است، به دوستى مگيريد، كه از آخرت به همان گونه نوميدند كه كافران از بازگشت در گور خفتگان‌",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 483,
            "hizbQuarter": 220,
            "sajda": false
          }
        ]
      },
      {
        "number": 61,
        "name": "سُورَةُ الصَّفِّ",
        "englishName": "As-Saff",
        "englishNameTranslation": "The Ranks",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5164,
            "text": "آنچه در آسمانها و آنچه در زمين است خداوند را نيايش كند، و اوست پيروزمند فرزانه‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5165,
            "text": "اى مؤمنان چرا چيزى مى‌گوييد كه انجام نمى‌دهيد",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5166,
            "text": "نزد خداوند بس منفور است كه چيزى را بگوييد كه انجام نمى‌دهيد",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5167,
            "text": "در حقيقت خداوند دوست دارد كسانى را كه در راه او صف در صف جهاد مى‌كنند، چنانكه گويى ايشان بنيادى ريخته از سرب‌اند",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5168,
            "text": "و چنين بود كه موسى به قومش گفت اى قوم من چرا مرا مى‌آزاريد و حال آنكه مى‌دانيد كه پيامبر خداوند به سوى شما هستم، پس چون [از حق‌] برگشتند خدا دلهايشان را برگردانيد، و خداوند نافرمانان را هدايت نمى‌كند",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 551,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5169,
            "text": "و چنين بود كه عيسى بن مريم گفت اى بنى‌اسرائيل من پيامبر خداوند به سوى شما هستم، [و] استوار دارنده توراتى كه پيشاروى من است و بشارتگر به پيامبرى كه پس از من مى‌آيد و نامش احمد است، و چون بر ايشان پديده‌هاى شگرف آورد، گفتند اين جادوى آشكار است‌",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5170,
            "text": "و كيست ستمكارتر از كسى كه بر خداوند دروغ بندد، و حال آنكه به سوى اسلام دعوت مى‌گردد، و خداوند قوم ستمكار را هدايت نمى‌كند",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5171,
            "text": "مى‌خواهند نور الهى را با سخنان خويش خاموش كنند و حال آنكه خداوند كمال بخش نور خويش است، ولو آنكه كافران ناخوش داشته باشند",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5172,
            "text": "او كسى است كه پيامبرش را با هدايت و دين حق فرستاده است تا آنان را بر همه اديان پيروز گرداند، ولو مشركان ناخوش داشته باشند",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 484,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5173,
            "text": "اى مؤمنان آيا شما را به داد و ستدى رهنمون شوم كه شما را از عذاب دردناك مى‌رهاند؟",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5174,
            "text": "[و آن اينكه‌] به خداوند و پيامبر او ايمان آوريد و به مال و جان در راه خدا جهاد كنيد، اين، اگر بدانيد، برايتان بهتر است‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5175,
            "text": "تا بر شما گناهانتان را بيامرزد و شما را به بوستانهايى كه جويباران از فرودست آن جارى است، و خانه‌هاى پاك و پسنديده در بهشتهاى عدن درآورد، اين رستگارى بزرگ است‌",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5176,
            "text": "و [نعمت‌] ديگرى كه آن را خوش داريد يارى از جانب خداوند و پيروزى نزديك، و مؤمنان را بشارت ده‌",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          },
          {
            "number": 5177,
            "text": "اى مؤمنان ياوران [دين‌] خدا باشيد، چنانكه عيسى بن مريم به حواريون گفت چه كسانى در راه دين خدا ياوران منند؟ حواريون گفتند ما ياوران [دين‌] خداونديم، سپس گروهى از بنى‌اسرائيل ايمان آوردند و گروهى [انكار و] كفرورزيدند، آنگاه مؤمنان را در برابر دشمنشان يارى داديم، و پيروز گرديدند",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 552,
            "ruku": 485,
            "hizbQuarter": 220,
            "sajda": false
          }
        ]
      },
      {
        "number": 62,
        "name": "سُورَةُ الجُمُعَةِ",
        "englishName": "Al-Jumu'a",
        "englishNameTranslation": "Friday",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5178,
            "text": "آنچه در آسمانها و آنچه در زمين است خداوند را نيايش كند، [خداوندى كه‌] فرمانرواى قدوس پيروزمند فرزانه است‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5179,
            "text": "اوست كه در ميان قوم بى‌كتاب [عرب‌] پيامبرى از ميان خودشان برانگيخت كه آيات او را بر آنان مى‌خواند و پاكيزه‌شان مى‌دارد، و به آنان كتاب و حكمت مى‌آموزد، و حقا كه در گذشته در گمراهى آشكارى بودند",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5180,
            "text": "و ديگرانى از آنان كه هنوز به آنان نپيوسته‌اند، و اوست پيروزمند فرزانه‌",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5181,
            "text": "اين بخشش الهى است كه به هر كس خواهد ارزانى‌اش دارد، و خداوند داراى بخشش و بخشايش بيكران است‌",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5182,
            "text": "داستان كسانى كه [عمل به‌] تورات بر آنان تكليف شد، سپس آن را [چنانكه بايد و شايد] رعايت نكردند، همانند چارپايى بر او كتابى چند است، چه بد است وصف گروهى كه آيات الهى را تكذيب كردند، و خداوند قوم ستمكار [مشرك‌] را هدايت نمى‌كند",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5183,
            "text": "بگو اى يهوديان اگر گمان مى‌كنيد كه شما از ميان همه مردم، دوستان خدا هستيد، اگر راست مى‌گوييد آرزوى مرگ كنيد",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5184,
            "text": "و به خاطر كار و كردار پيشينشان هرگز آن را آرزو نمى‌كنند، و خداوند به ستمكاران آگاه است‌",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5185,
            "text": "بگو همان مرگى كه از آن مى‌گريزيد، روى آور به شماست، سپس به سوى داناى پنهان و پيدا بازگردانده شويد، آنگاه شما را به [نتيجه و حقيقت‌] آنچه كرده‌ايد، آگاه مى‌سازد",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 553,
            "ruku": 486,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5186,
            "text": "اى مؤمنان چون براى نماز روز جمعه بانگ [اذن‌] در داده شود، به سوى ياد كرد خداوند بشتابيد و خريد و فروش را رها كنيد، اگر بدانيد اين برايتان بهتر است‌",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 487,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5187,
            "text": "سپس چون نماز گزارده شود، در زمين پراكنده شويد [و به راه خود برويد] و [روزى خود] از بخشش الهى بجوييد، و خداوند را بسيار ياد كنيد، باشد كه رستگار شويد",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 487,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5188,
            "text": "و [غافلان‌] چون داد و ستدى يا سرگرمى‌اى ببينند، به سوى آن بشتابند و تو را ايستاده رها كنند، بگو آنچه نزد خداوند است از سرگرمى و از داد و ستد بهتر است، و خداوند بهترين روزى‌دهندگان است‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 487,
            "hizbQuarter": 221,
            "sajda": false
          }
        ]
      },
      {
        "number": 63,
        "name": "سُورَةُ المُنَافِقُونَ",
        "englishName": "Al-Munaafiqoon",
        "englishNameTranslation": "The Hypocrites",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5189,
            "text": "منافقان چون به نزد تو آيند گويند گواهى مى‌دهيم كه تو پيامبر خدا هستى، و خداوند مى‌داند كه تو پيامبرش هستى، و خداوند گواهى مى‌دهد كه منافقان دروغگو هستند",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5190,
            "text": "سوگندهايشان را سپر بلا گرفته‌اند، سپس [مردمان را] از راه خدا بازداشته‌اند، چه بد است كارى كه آنان مى‌كنند",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5191,
            "text": "اين از آن است كه ايشان ايمان آورده‌اند سپس كفر ورزيده‌اند، و بر دلهايشان مهر نهاده شده است، و ايشان در نمى‌يابند",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 221,
            "sajda": false
          },
          {
            "number": 5192,
            "text": "و چون ايشان را بنگرى، بدنهاى ايشان تو را به شگفت‌آورد، و چون سخن گويند به سخنانشان گوش دهى، گويى همانند الوارهاى تكيه داده بر ديوارند، و هر بانگى را بر ضد خويش مى‌انگارند، آنان دشمن‌اند، از آنان بر حذر باش، خداوند لعنتشان كند، چگونه بيراهه مى‌روند؟",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 554,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5193,
            "text": "و چون به ايشان گفته شود بياييد كه پيامبر خدا براى شما آمرزش بطلبد، سرهاى خود را [به انكار] برگردانند، و بنگريشان كه [از اعتذار و استغفار] رويگردانند و گردنكش‌اند",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5194,
            "text": "در حق آنان يكسان است چه برايشان آمرزش بخواهى، چه بر ايشان آمرزش نخواهى، هرگز خداوند آنان را نخواهد آمرزيد، بى‌گمان خداوند نافرمانان را هدايت نمى‌كند",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5195,
            "text": "ايشان كسانى هستند كه مى‌گويند براى كسانى كه نزد پيامبر خدا هستند چيزى هزينه نكنيد تا آنكه [از پيرامون او] پراكنده شوند، و حال آنكه خزانه‌هاى آسمان و زمين از آن خداوند است، ولى منافقان در نمى‌يابند",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5196,
            "text": "مى‌گويند چون به مدينه بازگرديم، بلندپايگان، فرومايگان را از آنجا بيرون خواهند كرد، حال آنكه عزت، خاص خداوند و پيامبر او و مؤمنان است، ولى منافقان نمى‌دانند",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 488,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5197,
            "text": "اى مؤمنان اموال و اولادتان، شما را از ياد خداوند بازندارد، و هر كس چنين كند، آنانند كه زيانكارانند",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 489,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5198,
            "text": "و پيش از آنكه مرگ به سراغ هر يك از شما بيايد و او بگويد پروردگارا چرا مرا تا مهلتى نزديك باز پس نداشتى كه بتوانم صدقه بدهم و از شايستگان باشم، از آنچه روزيتان كرده‌ايم، بخشش كنيد",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 489,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5199,
            "text": "و خداوند هرگز كسى را چون اجلش فرا رسد، بازپس ندارد، و خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 555,
            "ruku": 489,
            "hizbQuarter": 222,
            "sajda": false
          }
        ]
      },
      {
        "number": 64,
        "name": "سُورَةُ التَّغَابُنِ",
        "englishName": "At-Taghaabun",
        "englishNameTranslation": "Mutual Disillusion",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5200,
            "text": "آنچه در آسمانها و آنچه در زمين است، خداوند را نيايش كند، فرمانروايى او راست، و سپاس او راست، و او بر هر كارى تواناست‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5201,
            "text": "و اوست كه شما را آفريده است، و بعضى از شما كافرند، و بعضى از شما مؤمن‌اند، و خداوند به آنچه مى‌كنيد بيناست‌",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5202,
            "text": "آسمانها و زمين را به حق آفريد و شما را نقش و نگار كرد و پيكرهاى شما را بر آراست، و سير و سرانجام به سوى اوست‌",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5203,
            "text": "آنچه در آسمانها و زمين است مى‌داند و آنچه پنهان مى‌داريد و آنچه آشكار مى‌داريد، مى‌داند، و خداوند به راز دلها داناست‌",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5204,
            "text": "آيا خبر كسانى كه پيش از اين كفرورزيدند به شما نرسيده است كه عقوبت كارشان را چشيدند و براى آنان عذابى دردناك [در پيش‌] است‌",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5205,
            "text": "اين از آن است كه پيامبرانشان براى آنان پديده‌هاى شگرف آوردند، و گفتند آيا بشرى مى‌خواهد هدايتمان كند، پس كفر [و انكار]ورزيدند و روى بر تافتند و خداوند بى‌نيازى نمود و خداوند بى‌نياز ستوده است‌",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5206,
            "text": "كافران پنداشته‌اند كه هرگز برانگيخته نمى‌شوند، بگو آرى سوگند به پروردگارم كه برانگيخته خواهيد شد، سپس از [حقيقت‌] آنچه كرده‌ايد، آگاهتان كنند، و اين بر خداوند آسان است‌",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5207,
            "text": "پس به خداوند و پيامبر او و نورى كه فرو فرستاده‌ايم ايمان آوريد، و خداوند به آنچه مى‌كنيد آگاه است‌",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5208,
            "text": "روزى كه براى روز محشر گردتان آورد، آن روز تغابن است و هر كس به خداوند ايمان آورد و نيكوكارى كند، گناهانش را از او مى‌زدايد، و او را به بوستانهايى كه جويباران از فرودست آن جارى است، درآورد، كه جاودانه در آن باشند، اين رستگارى بزرگ است‌",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 556,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5209,
            "text": "و كسانى كه به آيات ما كفر و انكار ورزيده‌اند، اينان دوزخيانند، جاودانه در آنند و بد سرانجامى است‌",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 490,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5210,
            "text": "هيچ مصيبتى [به كسى‌] نرسد مگر به اذن الهى، و هر كس به خداوند ايمان آورد [او] دلش را هدايت كند، و خداوند به هر چيزى داناست‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5211,
            "text": "و از خداوند اطاعت كنيد، و از پيامبر [هم‌] اطاعت كنيد، و اگر روى برتابيد [بدانيد كه‌] بر پيامبر ما فقط پيام رسانى آشكار است‌",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5212,
            "text": "خداوند است كه خدايى جز او نيست، و مؤمنان فقط بر خداوند بايد توكل كنند",
            "numberInSurah": 13,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5213,
            "text": "اى مؤمنان بى‌گمان از ميان همسران و فرزندانتان بعضى دشمن شما هستند، از آنان بر حذر باشيد، و اگر گذشت و بخشايش پيشه كنيد و درگذريد، خداوند هم آمرزگار مهربان است‌",
            "numberInSurah": 14,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5214,
            "text": "همانا اموال و اولاد شما مايه آزمون [و دردسر]اند، و پاداش بيكران فقط نزد خداوند است‌",
            "numberInSurah": 15,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5215,
            "text": "تا آنجا كه بتوانيد از خداوند پروا كنيد و [حق را] بشنويد و اطاعت پيشه كنيد و به انفاق بپردازيد كه به خير خودتان است، و كسانى كه از آزمندى نفس خويش در امان مانند رستگارند",
            "numberInSurah": 16,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5216,
            "text": "اگر در راه خداوند قرض‌الحسنه دهيد، براى شما [اجرش را] دو چندان كند و شما را بيامرزد، و خداوند قدردان بردبار است‌",
            "numberInSurah": 17,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          },
          {
            "number": 5217,
            "text": "داناى پنهان و پيدا [و] پيروزمند فرزانه است‌",
            "numberInSurah": 18,
            "juz": 28,
            "manzil": 7,
            "page": 557,
            "ruku": 491,
            "hizbQuarter": 222,
            "sajda": false
          }
        ]
      },
      {
        "number": 65,
        "name": "سُورَةُ الطَّلَاقِ",
        "englishName": "At-Talaaq",
        "englishNameTranslation": "Divorce",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5218,
            "text": "اى پيامبر، چون خواهيد زنان را طلاق گوييد، از هنگامى كه عده‌شان آغاز تواند شد [طهر غير مواقعه‌]، طلاق دهيد، و حساب عده را نگه داريد، و از خداوند، پروردگارتان، پروا كنيد، آنان را از خانه‌هايشان بيرون مكنيد، و خودشان هم بيرون نروند، مگر آنكه مرتكب كار ناشايست آشكارى شوند، و اين احكام الهى است و هر كس از [حد و مرز] احكام الهى تجاوز كند، به راستى در حق خود ستم روا داشته است، ندانى باشد كه خداوند بعد از اين كارى تازه پديد آورد",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5219,
            "text": "پس چون به پايان عده‌شان نزديك شوند، يا به نيكويى نگاهشان داريد [و رجوع كنيد]، يا با خير و خوشى از آنان جدا شويد، و [بر اين كار] دو [شاهد] عادل از ميان خودتان را شاهد بگيريد، و شهادت را براى خداوند برپا داريد، اين است كه هر كس به خداوند و روز بازپسين ايمان دارد، بدان اندرز داده شود، و هر كس كه از خداوند پروا [و پارسايى پيشه‌] كند، [خداوند] براى او بيرون‌شدى بگذارد",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5220,
            "text": "و او را از جايى كه گمان نمى‌برد، روزى رساند، و هر كس بر خداوند توكل كند، همو وى را كافى است، بى‌گمان خداوند سررشته‌دار كار خويش است، به راستى كه خداوند براى هر چيز اندازه‌اى نهاده است‌",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5221,
            "text": "و كسانى از زنان شما كه از حيض مايوسند، اگر در كارشان شك داريد، بدانيد كه عده آنان سه ماه [/سه پاكى‌] است، همچنين [است امر] زنانى كه حيض نديده‌اند، و بارداران، سرآمد عده‌شان اين است كه وضع حمل كنند، و هر كس از خداوند پرواكند، در كارش آسانى پديد آورد",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5222,
            "text": "اين امر الهى است كه به سوى شما نازلش كرده است، و هر كس از خداوند پرواكند، گناهانش را از او مى‌زدايد و پاداش او را بزرگ گرداند",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 558,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5223,
            "text": "آنان را در همانجا كه خود سكنا داريد، در حد وسعتان سكنا دهيد، و به آنان زيان مرسانيد كه عرصه را بر آنان تنگ بگيريد، و اگر [مطلقات‌] باردار باشند، خرجشان را بدهيد تا آنكه وضع حمل كنند، و اگر براى شما [نوزاد را] شير دادند، پس اجرتشان را بپردازيد، و در ميان خود به نيكى رايزنى كنيد، و اگر به توافق نرسيديد [زن‌] ديگرى او را شير دهد",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5224,
            "text": "تا توانگر بر وفق توان خود خرج كند و كسى كه روزى‌اش تنگ شده است، در همان حد كه خداوند به او بخشيده است خرج كند، خداوند بر هيچ كس جز بر وفق آنچه به او بخشيده است، تكليف نمى‌كند، زودا كه خداوند پس از تنگنا، آسايش مقرر دارد",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 492,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5225,
            "text": "و چه بسا شهرها كه [اهالى آن‌] از فرمان پروردگارش و پيامبران او سرپيچيد، آنگاه به سختى از آنان حساب كشيديم، و به عذابى بى‌مانند معذبشان داشتيم‌",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5226,
            "text": "بدين‌سان عقوبت كار خود را چشيد و عاقبت كارش زيان بود",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5227,
            "text": "خداوند براى آنان عذابى سهمگين آماده كرده است، پس اى خردمندان مؤمن از خداوند پروا كنيد، به راستى كه خداوند به سوى شما پندآموزى فرستاده است‌",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5228,
            "text": "پيامبرى كه آيات روشنگر الهى را بر شما مى‌خواند، تا كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، از تاريكيها به سوى روشنايى برآورد، و هر كس به خداوند ايمان آورد و نيكوكارى كند، او را به بوستانهايى كه جويباران از فرودست آن جارى است، درآورد، و جاودانه در آنند، به راستى كه خداوند روزى او را نيكو گردانده است‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          },
          {
            "number": 5229,
            "text": "خداوند كسى است كه هفت آسمان را آفريده است، و از زمين نيز همانند آن را، فرمان او از ميان آنها نازل مى‌گردد، تا بدانيد كه خداوند بر هر كارى توانا است، و اينكه خداوند بر هر چيز احاطه علمى دارد",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 559,
            "ruku": 493,
            "hizbQuarter": 223,
            "sajda": false
          }
        ]
      },
      {
        "number": 66,
        "name": "سُورَةُ التَّحۡرِيمِ",
        "englishName": "At-Tahrim",
        "englishNameTranslation": "The Prohibition",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5230,
            "text": "اى پيامبر چرا در طلب خشنودى [بعضى از] همسرانت چيزى را كه خداوند بر تو حلال گردانده است، تحريم مى‌كنى؟ و خداوند [در همه حال‌] آمرزگار مهربان است‌",
            "numberInSurah": 1,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5231,
            "text": "به راستى كه خداوند شكستن سوگندهايتان را [با دادن كفاره‌] بر شما روا داشته است، و خداوند سرور شماست و او داناى فرزانه است‌",
            "numberInSurah": 2,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5232,
            "text": "و چنين بود كه پيامبر سخنى را پوشيده‌وار به بعضى از همسرانش گفت، و چون آن را [آن زن به زن ديگر] خبر داد، و خداوند او را از آن آگاه كرد، بخشى از آن را [در عتاب خود به آنان‌] بيان داشت و از بخش ديگر صرفنظر كرد، و چون آن زن [صاحب راز و افشاءگر آن‌] را از آن آگاه كرد، گفت چه كسى تو را از اين خبر داد؟ فرمود مرا خداوند داناى آگاه با خبر كرد",
            "numberInSurah": 3,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5233,
            "text": "اگر شما دو زن به درگاه خداوند توبه كنيد، و به راستى هم دلهاى شما [از طاعت خداوند و پيامبر او در اين امر] برگشته است [از گناه شما در مى‌گذرد]، و اگر در برابر او همدستى كنيد، بدانيد كه خداوند دوست اوست و جبرئيل و صالح‌[ترين‌] مؤمنان، و علاوه بر آن فرشتگان هم پشتيبانند",
            "numberInSurah": 4,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5234,
            "text": "چه بسا اگر شما را طلاق دهد، پروردگارش براى او همسرانى بهتر از شما جانشين گرداند كه زنانى مسلمان، مؤمن، فرمانبردار، توبه‌كار، پرستشگر خداوند، روزه‌دار، اعم از بيوه و دوشيزه باشند",
            "numberInSurah": 5,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5235,
            "text": "اى مؤمنان خود و خانواده‌تان را از آتشى حفظ كنيد كه هيزم آن مردم و سنگها هستند و فرشتگانى درشت‌خو و سخت‌رو بر آن گمارده‌اند كه از خداوند در آنچه فرمانشان دهد سرپيچى نمى‌كنند، و هر چه به ايشان فرمان داده مى‌شود، انجام دهند",
            "numberInSurah": 6,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5236,
            "text": "اى كافران امروز عذرخواهى مكنيد، فقط در برابر كارى كه كرده‌ايد جزا يابيد",
            "numberInSurah": 7,
            "juz": 28,
            "manzil": 7,
            "page": 560,
            "ruku": 494,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5237,
            "text": "اى مؤمنان به درگاه خداوند توبه‌اى خالصانه كنيد، چه بسا پروردگارتان گناهانتان را از شما بزدايد و شما را به بوستانهايى كه جويباران از فرودست آن جارى است، در آورد [در] روزى كه خداوند پيامبر و ايمان آوردگان همراه او را فرو نگذارد، نورشان در پيشاپيش و سمت راستشان مى‌شتابد، گويند پروردگارا نور ما را براى ما به كمال برسان، و ما را بيامرز، كه تو بر هر كارى توانايى‌",
            "numberInSurah": 8,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5238,
            "text": "اى پيامبر با كافران و منافقان جهاد كن، و با آنان درشتى كن كه سرا و سرانجامشان جهنم است و بد سرانجامى است‌",
            "numberInSurah": 9,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5239,
            "text": "خداوند درباره كافران مثلى مى‌زند، و آن همسر نوح است و همسر لوط كه در حباله دو بنده از بندگان شايسته ما بودند، سپس به ايشان خيانت كردند، و آن دو [پيامبر] در برابر امر [و عذاب‌] الهى چيزى را از آنان باز نداشتند، و [به آنان‌] گفته شود، همراه ساير وارد شوندگان، وارد آتش [جهنم‌] شويد",
            "numberInSurah": 10,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5240,
            "text": "و نيز خداوند درباره مؤمنان مثلى مى‌زند و آن همسر فرعون است كه [در اوج سختى كشيدن‌] گفت پروردگارا براى من در نزد خودت، خانه‌اى در بهشت بنا كن، و مرا از شر فرعون و عمل او رهايى ده، و از قوم ستمكار نجاتم بخش‌",
            "numberInSurah": 11,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          },
          {
            "number": 5241,
            "text": "و نيز مريم دختر عمران را كه ناموس خود را محفوظ داشت، آنگاه از روح خويش در آن دميديم، و سخنان و كتابهاى پروردگارش را استوار گرفت، و از فرمانبرداران بود",
            "numberInSurah": 12,
            "juz": 28,
            "manzil": 7,
            "page": 561,
            "ruku": 495,
            "hizbQuarter": 224,
            "sajda": false
          }
        ]
      },
      {
        "number": 67,
        "name": "سُورَةُ المُلۡكِ",
        "englishName": "Al-Mulk",
        "englishNameTranslation": "The Sovereignty",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5242,
            "text": "بزرگا كسى كه فرمانروايى به دست اوست و او بر هر كارى تواناست‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5243,
            "text": "كسى كه مرگ و زندگى را آفريد تا شما را بيازمايد كه كداميك نيكو كردارتريد، و او پيروزمند آمرزگار است‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5244,
            "text": "كسى كه هفت آسمان را توبرتو آفريد، در آفرينش خداوند رحمان، هيچ گونه نابسامانى نمى‌بينى، باز چشم بگردان آيا هيچ رخنه‌اى مى‌بينى؟",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5245,
            "text": "سپس دوباره چشم بگردان، چشمت خسته و مانده باز مى‌گردد",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5246,
            "text": "و به راستى آسمان دنيا را به چراغهاى [ستارگان‌] آراستيم و آنها را رماننده شياطين گردانديم و براى آنان عذاب آتش افروخته، آماده ساختيم‌",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5247,
            "text": "و براى كسانى كه به پروردگارشان كفرورزيدند عذاب جهنم [آماده‌] است، و چه بد سرانجامى است‌",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5248,
            "text": "چون در آنجا افكنده شوند، نعره آن را مى‌شنوند، و آن بر مى‌جوشد",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5249,
            "text": "نزديك است كه از خشم پاره پاره گردد، هرگاه كه گروهى در آن افكنده شوند، نگهبانانش از ايشان پرسند آيا هشداردهنده‌اى نزد شما نيامد؟",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5250,
            "text": "گويند چرا، هشداردهنده‌اى نزد ما آمد، آنگاه [او را] تكذيب كرديم و گفتيم خداوند چيزى فرو نفرستاده است، شما جز در گمراهى بزرگ نيستيد",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5251,
            "text": "و گويند اگر [سخنان آنان را] به سمع قبول مى‌شنيديم يا تعقل مى‌كرديم، از زمره دوزخيان نبوديم‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5252,
            "text": "و بدين‌سان به گناهشان اعتراف مى‌كنند، لعنت باد دوزخيان را",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5253,
            "text": "بى‌گمان كسانى كه به نهان از پروردگارشان مى‌هراسند، براى آنان آمرزش و پاداشى بزرگ است‌",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 562,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5254,
            "text": "و سخنتان را چه پوشيده داريد چه آشكار، او به راز دلها داناست‌",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5255,
            "text": "آيا كسى كه آفريده است، علم ندارد؟ و اوست باريك‌بين آگاه‌",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 496,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5256,
            "text": "اوست كسى كه زمين را براى شما رام گردانيد، پس در گوشه و كنار آن رهسپار شويد و از روزى او بخوريد، و رستاخيز به امر [و در پيشگاه‌] اوست‌",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5257,
            "text": "آيا از آن كه در آسمان است ايمنيد كه شما را در زمين فرو برد، در حالتى كه آن ناگاه مى‌جنبد؟",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5258,
            "text": "يا از آن كه در آسمان است ايمنيد كه بر شما شن‌بادى بفرستد، زودا كه بدانيد هشدار من چگونه است‌",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5259,
            "text": "و به راستى كه پيشينيان آنان تكذيب پيشه كردند پس [بنگر كه‌] عقوبت من چگونه بود",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5260,
            "text": "آيا به پرندگان بر فراز سرشان ننگريسته‌اند، كه بال گشاده‌اند و فرو بندند [آن را]، هيچ كس جز خداوند رحمان آنها را نگه نمى‌دارد، كه او به هر چيزى بيناست‌",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5261,
            "text": "يا كيست آنكه سپاه شماست كه شما را در برابر خداى رحمان يارى دهد؟ كافران جز در [توهم و] فريب خوردگى نيستند",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5262,
            "text": "يا كيست آنكه اگر [خداوند] روزى‌اش را باز گيرد، شما را روزى دهد؟، حق اين است كه در گردنكشى و رميدگى پاى مى‌فشرند",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5263,
            "text": "آيا كسى كه به رو در افتاده نگونسار مى‌رود، رهيافته‌تر است، يا كسى كه استوار بر راه راست ره مى‌سپارد؟",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5264,
            "text": "بگو او كسى است كه شما را پديد آورده است و براى شما گوش [ها] و چشم [ها] و قلب [ها] آفريده است، چه اندكمايه سپاس مى‌گزاريد",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5265,
            "text": "بگو او كسى است كه شما را در زمين پديد آورد، و به نزد او محشور مى‌شويد",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5266,
            "text": "و مى‌گويند اگر راست مى‌گوييد، پس كى اين وعده فرا مى‌رسد؟",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5267,
            "text": "بگو آگاهى از آن فقط نزد خداوند است، و من فقط هشداردهنده‌اى آشكارم‌",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 563,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5268,
            "text": "و چون آن را نزديك بينند، چهره‌هاى كافران اندوهگين شود، و [به آنان‌] گفته شود، اين همان است كه [در دنيا از ما به شتاب‌] مى‌طلبيديد",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5269,
            "text": "بگو ملاحظه كنيد، اگر خداوند مرا و همراهانم را نابود كند، يا بر ما رحمت آورد، پس كيست كه كافران را از عذابى دردناك پناه دهد؟",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5270,
            "text": "بگو او خداوند رحمان است، كه به او ايمان آورده‌ايم و بر او توكل كرده‌ايم، زودا بدانيد كه چه كسى در گمراهى آشكار است‌",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          },
          {
            "number": 5271,
            "text": "بگو ملاحظه كنيد، اگر آب شما در زمين فرو رود [و ناپديد گردد] چه كسى براى شما آب روان مى‌آورد؟",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 497,
            "hizbQuarter": 225,
            "sajda": false
          }
        ]
      },
      {
        "number": 68,
        "name": "سُورَةُ القَلَمِ",
        "englishName": "Al-Qalam",
        "englishNameTranslation": "The Pen",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5272,
            "text": "ن [نون‌]، سوگند به قلم و آنچه نويسند",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5273,
            "text": "تو -به لطف پروردگارتديوانه نيستى‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5274,
            "text": "و تو را پاداشى ناكاسته [/بى‌منت‌] است‌",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5275,
            "text": "و تو داراى اخلاق بسيار بزرگوارانه‌اى هستى‌",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5276,
            "text": "زودا كه بنگرى و بنگرند",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5277,
            "text": "كه كداميك از شما ديوانه‌ايد",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5278,
            "text": "بى‌گمان پروردگارت به كسى كه از راه او گم گشته و به رهيافتگان آگاه‌تر است‌",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5279,
            "text": "پس از منكران اطاعت مكن‌",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5280,
            "text": "خوش دارند كه با آنان سازگارى كنى تا با تو سازگارى كنند",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5281,
            "text": "و از هيچ بيهوده سوگند خوار پستى اطاعت مكن‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5282,
            "text": "عيبجوى روانه در پى سخن‌چينى‌",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5283,
            "text": "باز دارنده، از نيكى، تجاوزگر گناهكار",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5284,
            "text": "درشت‌خوى، و بعد از اين همه، ناپاكزاد",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5285,
            "text": "به خاطر آنكه مالدار است و صاحب پسران‌",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5286,
            "text": "چون آيات ما بر او خوانده شود، گويد افسانه‌هاى پيشينيان است‌",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 564,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5287,
            "text": "زودا كه بر بينى او نشانه‌اى بگذاريم‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5288,
            "text": "ما آنان را آزموده‌ايم، همچنانكه باغداران را هم آزموده‌ايم، آنگاه كه سوگند خوردند كه صبحگاهان ميوه‌هاى آن را بازچينند",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5289,
            "text": "و ان شاء الله نگفتند",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5290,
            "text": "سپس بلايى شبانه از جانب پروردگارت، در حالى كه ايشان خفته بودند، بر آن زد",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5291,
            "text": "و مانند خاكستر سياه شد",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5292,
            "text": "[آنان بى‌خبر] صبحگاهان همديگر را فراخواندند",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5293,
            "text": "كه اگر ميوه‌چين هستيد، پگاه به سراغ كشتزارتان برويد",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5294,
            "text": "سپس رهسپار شدند، در حالى كه آهسته با همديگر سخن مى‌گفتند",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5295,
            "text": "كه مبادا بى‌نوايى به آنجا بر شما وارد شود",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5296,
            "text": "و بر منع و مضايقه توانا شدند",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5297,
            "text": "و چون آن [باغ سياه‌سوخته‌] را ديدند [جا خوردند و] گفتند ما گم گشته‌ايم‌",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5298,
            "text": "نه، مثل اينكه، بى‌بهره‌ايم‌",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5299,
            "text": "بهترينشان گفت مگر به شما نگفته بودم كه چرا تسبيح [و ان شاء الله‌] نمى‌گوييد؟",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5300,
            "text": "گفتند پاك است پروردگارمان، ما خود ستمكار بوديم‌",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5301,
            "text": "سپس به سرزنش به همديگر روى آوردند",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5302,
            "text": "گفتند واى بر ما، ما سركش بوده‌ايم‌",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5303,
            "text": "چه بسا پروردگارمان بهتر از آن را برايمان جانشين گرداند، ما به سوى پروردگارمان مى‌گراييم‌",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5304,
            "text": "اين چنين است عذاب، و اگر مى‌دانستند عذاب اخروى سهمگين‌تر است‌",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 498,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5305,
            "text": "براى پرهيزگاران در نزد پروردگارشان بهشتهاى پرناز و نعمت است‌",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5306,
            "text": "آيا مسلمانان را همانند گناهكاران مى‌شماريم؟",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5307,
            "text": "شما را چه مى‌شود چگونه داورى مى‌كنيد؟",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5308,
            "text": "يا مگر كتابى داريد كه در آنها مطالعه مى‌كنيد",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5309,
            "text": "كه در آن براى شما هرچه آرزو كنيد هست؟",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5310,
            "text": "يا شما را پيمانهايى است با ما كه حكم آن تا قيامت باقى باشد كه هر چه حكم كنيد براى شما باشد؟",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5311,
            "text": "از ايشان بپرس كه كداميك از آنان ضامن اين امر است؟",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5312,
            "text": "يا شركايى دارند، پس اگر راست مى‌گويند شريكانشان را به ميان آورند",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5313,
            "text": "روزى كه هنگامه بالا گيرد، و آنان را به سجده دعوت كنند ولى نتوانند",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 565,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5314,
            "text": "ديدگانشان [از ترس‌] فرو افتاده باشد، غبار خفت و خوارى بر رخسارشان نشسته، و چنين بود كه به سجده دعوت مى‌شدند و تندرست بودند",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5315,
            "text": "پس مرا با كسى كه اين سخن را تكذيب مى‌كند، وابگذار، زودا كه آهسته آهسته به نحوى كه در نيابند، فرو خواهيمشان گرفت‌",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5316,
            "text": "و مهلتشان مى‌دهم، زيرا تدبير من سخت استوار است‌",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5317,
            "text": "يا مگر از آنان مزدى درخواست مى‌كنى، و آنان خود را زير بار تاوان، گرانبار مى‌يابند؟",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5318,
            "text": "يا مگر علم و اسرار غيب با ايشان است و از روى آن مى‌نويسند؟",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5319,
            "text": "پس در انتظار حكم پروردگارت شكيبايى كن و همانند صاحب ماهى [ يونس‌] مباش كه [در تاريكى‌] ندا در داد و خشم فرو خورده بود",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5320,
            "text": "اگر نعمتى از جانب پروردگارش او را دستگيرى نمى‌كرد، به كرانه‌[ى بى‌آب و علف‌] افكنده شده بود و قابل ملامت بود",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5321,
            "text": "سپس پروردگارش او را برگزيد و از شايستگانش قرار داد",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5322,
            "text": "و بسيار نزديك بود كه كافران چون قرآن را شنيدند، تو را با ديدگانشان آسيب برسانند و گفتند او ديوانه است‌",
            "numberInSurah": 51,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          },
          {
            "number": 5323,
            "text": "و حال آنكه آن جز پندى براى جهانيان نيست‌",
            "numberInSurah": 52,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 499,
            "hizbQuarter": 226,
            "sajda": false
          }
        ]
      },
      {
        "number": 69,
        "name": "سُورَةُ الحَاقَّةِ",
        "englishName": "Al-Haaqqa",
        "englishNameTranslation": "The Reality",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5324,
            "text": "حاقه‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5325,
            "text": "حاقه چيست؟",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5326,
            "text": "و چه دانى حاقه چيست؟",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5327,
            "text": "قوم ثمود و عاد درهم كوب [/قيامت‌] را دروغ انگاشتند",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5328,
            "text": "و اما ثمود، بر اثر طغيان خود به نابودى كشيده شدند",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5329,
            "text": "و اما عاد، با تندبادى سخت سرد و بنيان‌كن به نابودى كشيده شدند",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5330,
            "text": "كه خداوند آن را هفت شب و هشت روز پيوسته بر آنان گماشت و آن قوم را در آن حال از پا در افتاده بينى، گويى ايشان خرمابنانى هستند ريشه كن شده‌",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5331,
            "text": "پس آيا اثرى از ايشان بازمانده مى‌بينى؟",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 566,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5332,
            "text": "و فرعون و پيشينيان او و [اهل‌] شهرهاى نگونسار، طغيان پيش آوردند",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5333,
            "text": "سپس از پيامبر پروردگارشان نافرمانى كردند، آنگاه [خداوند] آنان را به مؤاخذه‌اى سخت فرو گرفت‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5334,
            "text": "ما آنگاه كه سيلاب طغيان كرد، شما را در كشتى سوار كرديم‌",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5335,
            "text": "تا سرانجام آن را پندآموزى براى شما گردانيم، و گوشهاى نيوشا آن را فرا مى‌گيرد",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5336,
            "text": "و چون دمى يگانه در صور دميده شود",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5337,
            "text": "و زمين و كوهها برداشته شده و يكباره درهم كوبيده شود",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5338,
            "text": "پس در چنين روزى واقعه‌[ى قيامت‌] روى دهد",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5339,
            "text": "و آسمان از هم بشكافد و آن در چنين روزى سست پيوند است‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5340,
            "text": "و فرشتگان بر كناره‌ها[ى آسمان‌] ايستاده باشند، و عرش پروردگارت را در آن روز هشت تن برفرازشان حمل مى‌كنند",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5341,
            "text": "در چنين روزى [بر خداوند] عرضه شويد، و هيچ رازتان پوشيده نماند",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5342,
            "text": "سپس هر كس كه كارنامه‌اش به دست راستش داده شود، [شادمانه‌] گويد بياييد كارنامه مرا بخوانيد",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5343,
            "text": "به يقين مى‌دانستم كه با حساب و كتاب خود مواجه خواهم شد",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5344,
            "text": "آنگاه او در زندگى پسنديده‌اى است‌",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5345,
            "text": "در بهشتى برين‌",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5346,
            "text": "كه ميوه‌هايش دسترس است‌",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5347,
            "text": "[به آنان گويند] به خاطر آنچه در روزگاران گذشته انجام داده‌ايد، بخوريد و بياشاميد گوارا",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5348,
            "text": "و اما كسى كه كارنامه‌اش به دست چپش داده شود، [سوكمندانه‌] گويد اى كاش كارنامه‌ام به من داده نمى‌شد",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5349,
            "text": "و نمى‌دانستم حساب و كتابم چيست‌",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5350,
            "text": "كاش آن [مرگ‌] يكسره بود",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5351,
            "text": "مال و منال من به كار من نيامد",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5352,
            "text": "دستگاه من از دستم برفت‌",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5353,
            "text": "[گويند] او را فرو گيريد و دربندش كنيد",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5354,
            "text": "سپس به دوزخش درآوريد",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5355,
            "text": "سپس در زنجيرى كه طولش هفتاد ذرع است، بندش كنيد",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5356,
            "text": "كه او به خداوند بزرگ ايمانى نداشت‌",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5357,
            "text": "و بر اطعام بينوايان ترغيب نمى‌كرد",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 567,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5358,
            "text": "حال امروز و اينجا دوستى ندارد",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5359,
            "text": "و نه خوراكى، بجز زردابه‌",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5360,
            "text": "كه آن را جز خطاكاران نمى‌خورند",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 500,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5361,
            "text": "پس به آنچه مى‌بينيد سوگند مى‌خورم‌",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5362,
            "text": "و به آنچه نمى‌بينيد",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5363,
            "text": "كه آن [قرآن‌] برخوانده فرستاده گرامى است‌",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5364,
            "text": "و سخن هيچ شاعرى نيست، چه اندك ايمان مى‌آوريد",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5365,
            "text": "و سخن هيچ كاهنى نيست، چه اندك پند مى‌گيريد",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5366,
            "text": "و فرو فرستاده‌اى از سوى پروردگار جهانيان است‌",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5367,
            "text": "و اگر بر ما سخنانى مى‌بست‌",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5368,
            "text": "دست راستش را مى‌گرفتيم‌",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5369,
            "text": "سپس شاهرگش را قطع مى‌كرديم‌",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5370,
            "text": "و هيچ‌يك از شما مدافع او نبود",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5371,
            "text": "و آن پندآموزى براى پرهيزگاران است‌",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5372,
            "text": "و ما به يقين مى‌دانيم كه از ميان شما تكذيب‌كنندگانى هستند",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5373,
            "text": "و آن [پيام‌]، مايه حسرتى بر كافران است‌",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5374,
            "text": "و آن [پيام‌]، حق‌اليقين است‌",
            "numberInSurah": 51,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5375,
            "text": "پس به نام پروردگار بزرگ خود تسبيح گوى‌",
            "numberInSurah": 52,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 501,
            "hizbQuarter": 227,
            "sajda": false
          }
        ]
      },
      {
        "number": 70,
        "name": "سُورَةُ المَعَارِجِ",
        "englishName": "Al-Ma'aarij",
        "englishNameTranslation": "The Ascending Stairways",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5376,
            "text": "خواهنده‌اى عذابى رخ دادنى را طلب كرد",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5377,
            "text": "كه براى كافران است [و] بازدارنده‌اى ندارد",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5378,
            "text": "از جانب خداوند صاحب درجات‌",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5379,
            "text": "كه فرشتگان و روح در روزى كه به اندازه پنجاه هزار سال [دنيوى‌] است به سوى او فرا مى‌روند",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5380,
            "text": "پس صبرى نيكو پيشه كن‌",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5381,
            "text": "آنان آن را دوردست مى‌بينند",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5382,
            "text": "و ما نزديك مى‌بينيمش‌",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5383,
            "text": "روزى كه آسمان چون فلز گداخته شود",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5384,
            "text": "و كوهها همچون پشم رنگين گردد",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5385,
            "text": "و هيچ خويشاوندى از حال خويشاوندى نپرسد",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 568,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5386,
            "text": "در حالى كه به همديگر نشان داده شوند، گناهكار آرزو كند كاش عذاب چنين روزى را،",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5387,
            "text": "با فدا كردن پسرانش و همسرش و برادرش باز خرد",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5388,
            "text": "همچنين خاندانش كه به او سر و سامان داده است‌",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5389,
            "text": "همچنين هر كس كه در روى زمين است، آنگاه باز رهاندش‌",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5390,
            "text": "حاشا، آن آتشى زبانه‌زن است‌",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5391,
            "text": "بركننده پوست سر",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5392,
            "text": "كه هر كس را كه [به حق‌] پشت كرد و [از ايمان‌] روى گرداند، به خود مى‌خواند",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5393,
            "text": "همان كسى كه مال اندوخت و جا به جا كرد",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 227,
            "sajda": false
          },
          {
            "number": 5394,
            "text": "بى‌گمان انسان ناشكيب آفريده شده است‌",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5395,
            "text": "چون شر به او رسد، بى‌تاب است‌",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5396,
            "text": "و چون خير به او رسد، بازدارنده است‌",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5397,
            "text": "مگر نمازگزاران‌",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5398,
            "text": "آنان كه در نمازهايشان پيگيرند",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5399,
            "text": "و كسانى كه در اموالشان حقى معين است‌",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5400,
            "text": "براى خواهنده و بى‌بهره‌",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5401,
            "text": "و كسانى كه روز جزا را باور مى‌دارند",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5402,
            "text": "و كسانى كه از عذاب پروردگارشان بيمناكند",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5403,
            "text": "بى‌گمان عذاب پروردگارشان، بدون ايمنى است‌",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5404,
            "text": "و كسانى كه ناموسشان را حفظ مى‌كنند",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5405,
            "text": "مگر در برابر همسرانشان يا ملك يمينهايشان، كه در اين صورت سرزنش ناپذيرند",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5406,
            "text": "پس هر كس فراتر از اين بجويد، آنانند كه تجاوزكارند",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5407,
            "text": "و كسانى كه در برابر امانتها و پيمانهايشان رعايتگرند",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5408,
            "text": "و كسانى كه در مقام گواهى دادنهايشان ايستاده‌اند",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5409,
            "text": "و كسانى كه بر نمازهايشان مواظبند",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5410,
            "text": "اينان در بوستانهايى، گرامى داشته شده‌اند",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 502,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5411,
            "text": "پس كافران را چه مى‌شود كه به سوى تو شتابانند",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5412,
            "text": "از جانب راست و از جانب چپ، گروه در گروه‌",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5413,
            "text": "آيا هر كدام از آنان طمع مى‌برد كه به بهشت پرناز و نعمت درآورده شود",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5414,
            "text": "حاشا ما ايشان را از آنچه مى‌دانند آفريده‌ايم‌",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 569,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5415,
            "text": "به پروردگار مشرق‌ها و مغرب‌ها سوگند مى‌خورم كه ما تواناييم‌",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5416,
            "text": "كه بهتر از آنان را جانشين آنان سازيم و ما درمانده نيستيم‌",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5417,
            "text": "پس بگذارشان تا ژاژخايى و بازيگوشى كنند تا به ديدار آن روزشان كه وعده‌اش را به ايشان داده‌اند، برسند",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5418,
            "text": "روزى كه از گورها بيرون شتابند، گويى به سوى [قربانگاه‌] بتان مى‌شتابند",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5419,
            "text": "ديدگانشان از [ترس‌] فرو افتاده باشد، غبار خفت و خوارى بر رخسارشان نشسته، اين همان روزى است كه به آنان وعده داده بودند",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 503,
            "hizbQuarter": 228,
            "sajda": false
          }
        ]
      },
      {
        "number": 71,
        "name": "سُورَةُ نُوحٍ",
        "englishName": "Nooh",
        "englishNameTranslation": "Noah",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5420,
            "text": "ما نوح را به سوى قومش فرستاده‌ايم كه قومت را پيش از آنكه عذابى دردناك بر سرشان بيايد، هشدار ده‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5421,
            "text": "گفت اى قوم من، من هشداردهنده آشكار شما هستم‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5422,
            "text": "كه خداوند را بپرستيد و از او پروا و از من پيروى كنيد",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5423,
            "text": "تا گناهانتان را براى شما بيامرزد و شما را تا سرآمدى معين واپس‌بدارد، چرا كه اگر بدانيد اجل الهى، چون فرا رسد، بازپس داشته نمى‌شود",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5424,
            "text": "گفت پروردگارا من قومم را شب و روز [به خداپرستى‌] دعوت كردم‌",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5425,
            "text": "ولى دعوت من جز بر گريزشان نيفزود",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5426,
            "text": "و من هرچه دعوتشان مى‌كنم تا سرانجام آنان را بيامرزى، انگشتانشان را در گوشهايشان مى‌گذارند، و خود را در جامه‌هايشان مى‌پوشانند و [در كفر] پاى مى‌فشرند و سخت گردنكشى مى‌كنند",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5427,
            "text": "سپس باز ايشان را آشكارا دعوت كردم‌",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5428,
            "text": "آنگاه آشكارا به ايشان گفتم و پنهانى به ايشان گفتم‌",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5429,
            "text": "سپس گفتم از پروردگارتان آمرزش بخواهيد چرا كه او آمرزگار است‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 570,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5430,
            "text": "تا بر شما از آسمان بارانى پيوسته و پيگير بفرستد",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5431,
            "text": "و شما را با بخشيدن اموال و پسران مدد رساند و براى شما باغها و براى شما جويبارها پديد آورد",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5432,
            "text": "شما را چه مى‌شود كه از عظمت خداوند نمى‌ترسيد؟",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5433,
            "text": "و حال آنكه شما را گونه‌گون آفريده است‌",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5434,
            "text": "آيا نينديشيده‌ايد كه خداوند چگونه هفت آسمان را توبرتو آفريده است؟",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5435,
            "text": "و ماه را در آن پرتوافشان و خورشيد را [چون‌] چراغى قرار داده است‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5436,
            "text": "و خداوند شما را مانند گياهى از زمين روياند",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5437,
            "text": "سپس شما را به آن باز مى‌گرداند، و [پس از آن‌] شما را [دوباره‌] بيرون مى‌آورد",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5438,
            "text": "و خداوند زمين را زيرانداز شما كرد",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5439,
            "text": "تا به شاهراههاى گشاده آن راه يابيد",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 504,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5440,
            "text": "نوح گفت پروردگارا آنان از من نافرمانى كردند و از كسى كه مال و فرزندش جز به زيانكاريش نيفزوده است، پيروى كردند",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5441,
            "text": "و نيرنگى سترگ در كار آوردند",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5442,
            "text": "و گفتند نه هرگز دست از خدايانتان و نه هرگز دست از ود و سواع و يغوث و يعوق و نسر برداريد",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5443,
            "text": "و بسيارى را بيراه كردند و جز بر بيراهى ستمكاران [مشرك‌] ميفزاى‌",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5444,
            "text": "بر اثر گناهانشان بود كه به غرق كشيده شدند، سپس به آتشى درآورده شدند و براى خود در برابر خداوند ياورى نيافتند",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5445,
            "text": "و نوح گفت پروردگارا بر روى زمين از كافران ديارى باقى مگذار",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5446,
            "text": "چرا كه اگر باقيشان گذارى، بندگانت را گمراه كنند و جز فاجر كفران‌پيشه‌اى به بار نياورند",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          },
          {
            "number": 5447,
            "text": "پروردگارا مرا و پدر و مادرم را و هر كس را كه مؤمن به خانه من درآيد و نيز مردان مؤمن و زنان مؤمن را بيامرز، و جز بر تباهى ستمكاران [مشرك‌] ميفزاى‌",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 571,
            "ruku": 505,
            "hizbQuarter": 228,
            "sajda": false
          }
        ]
      },
      {
        "number": 72,
        "name": "سُورَةُ الجِنِّ",
        "englishName": "Al-Jinn",
        "englishNameTranslation": "The Jinn",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5448,
            "text": "بگو به من وحى فرستاده شده است كه گروهى از جن گوش [به قرآن‌] سپردند، سپس گفتند ما قرآنى شگرف را شنيده‌ايم‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5449,
            "text": "كه به راستى راه مى‌نمايد، آنگاه به آن ايمان آورديم، و هرگز براى پروردگارمان كسى را شريك نمى‌آوريم‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5450,
            "text": "و حقا كه بلند است شان پروردگار ما، نه همسرى برگزيده است، و نه فرزندى‌",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5451,
            "text": "و نادان ما بود كه در حق خداوند، پريشان مى‌گفت‌",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5452,
            "text": "و ما چنين مى‌پنداشتيم كه انس و جن هرگز بر خداوند دروغ نمى‌بندند",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5453,
            "text": "و چنين بود كه مردانى از انسانها به مردانى از جنيان پناه مى‌بردند، و بر سركشى آنان مى‌افزودند",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5454,
            "text": "و آنان نيز مى‌پنداشتند همچنانكه شما مى‌پنداريد كه خداوند هرگز كسى را [به حشر يا پيامبرى‌] بر نمى‌انگيزد",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5455,
            "text": "و ما در آسمان جستيم و آن را سرشار از نگهبانان نيرومند و شهابها يافتيم‌",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5456,
            "text": "و ما خود در نشستنگاههايى براى [دزديده‌] شنيدن از آن مى‌نشستيم، اما اكنون هر كس گوش بسپارد، شهابى در كمين خود يابد",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5457,
            "text": "و ما خود نمى‌دانيم كه در حق زمينيان شر خواسته شده است، يا پروردگارشان در حق آنان رهيافت خواسته است‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5458,
            "text": "و از ميان ما هم صالحان هست و هم از ميان ما جز اين، ما [روندگان‌] راههاى گوناگونيم‌",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5459,
            "text": "و ما يقين داشتيم كه هرگز در زمين از خداوند گزير و گريزى نداريم‌",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5460,
            "text": "و ما چون [پيام‌] هدايت را شنيديم، به آن ايمان آورديم، پس هر كس به پروردگارش ايمان آورد، نه از نقصانى بيم داشته باشد، و نه از ستمى‌",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 572,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5461,
            "text": "و جمعى از ميان ما مسلمان است، و جمعى كژراه، پس كسانى كه اسلام آورند، آنانند كه هدايت جسته‌اند",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5462,
            "text": "و اما كژراهان همانا هيزم جهنمند",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5463,
            "text": "و اگر در شيوه‌[ى ناصواب‌] پايدارى ورزند [از روى استدراج‌] آنان را آب [و امكانات‌] فراوان ارزانى مى‌داريم‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5464,
            "text": "تا سرانجام در آن بيازماييمشان، و هر كس از ياد پروردگارش دل بگرداند، او را به عذابى سهمگين بكشاند",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5465,
            "text": "و همه مسجدها خاص خداوند است، پس در جنب خداوند هيچ كس را [به پرستش‌] مخوان‌",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5466,
            "text": "و چنين بود كه چون بنده خدا [محمد -ص-] او را به نيايش مى‌خواند، نزديكشان بود كه بر سر او بريزند",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 506,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5467,
            "text": "بگو فقط پروردگارم را به نيايش مى‌خوانم و هيچ كس را شريك او نمى‌شمارم‌",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5468,
            "text": "بگو من نه صاحب اختيار زيانى براى شما هستم، نه سامانى‌",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5469,
            "text": "بگو هيچ كس نيست كه هرگز مرا در برابر خداوند پناه دهد، و هرگز بجز او پناهى نمى‌يابم‌",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5470,
            "text": "[در اختيار ندارم‌] مگر پيامى از جانب خداوند و رسالت او، و هر كس از خداوند و پيامبر او سرپيچى كند، او راست آتش دوزخ كه جاودانه در آن ماند",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5471,
            "text": "تا هنگامى رسد كه آنچه به آنان وعده داده شده است، ببينند، و زودا بدانند كه چه كسى سست ياورتر و اندك سپاه‌تر است‌",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5472,
            "text": "بگو نمى‌دانم آنچه به شما وعده داده‌اند نزديك است، يا پروردگار من براى آن فاصله‌اى [دراز] قرار داده است‌",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5473,
            "text": "اوست داناى [راز] پنهان، كه هيچ كس را از غيب خويش آگاه نمى‌سازد",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5474,
            "text": "مگر پيامبرى كه او بپسندد، كه پيشاپيش و پشت سر او نگهبانانى راه دهد",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5475,
            "text": "تا معلوم بدارد كه رسالت پروردگارشان را گزارده‌اند و به آنچه نزد آنان است احاطه دارد، و هر چيز را به شماره مى‌شمارد",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 573,
            "ruku": 507,
            "hizbQuarter": 229,
            "sajda": false
          }
        ]
      },
      {
        "number": 73,
        "name": "سُورَةُ المُزَّمِّلِ",
        "englishName": "Al-Muzzammil",
        "englishNameTranslation": "The Enshrouded One",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5476,
            "text": "اى مرد جامه بر خود پيچيده‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5477,
            "text": "شب را به پاى‌دار، مگر اندكى از آن را",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5478,
            "text": "نيمه‌اش، يا اندكى از نيمه كم نما",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5479,
            "text": "يا اندكى بر آن بيفزا، و قرآن را شمرده و شيوا بخوان‌",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5480,
            "text": "ما سخنى سنگين بر تو نازل مى‌كنيم‌",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5481,
            "text": "بى‌گمان شب‌خيزى بيشتر موافقت [دل و زبان‌] در بردارد و از لحاظ سخن استوارتر است‌",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5482,
            "text": "تو را در روز كار و كوشش بسيار است‌",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5483,
            "text": "و نام پروردگارت را ياد كن و از همه گسسته و با او پيوسته شو",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5484,
            "text": "پروردگار مشرق و مغرب، كه خدايى جز او نيست، او را كارساز خود بشمار",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5485,
            "text": "و بر آنچه مى‌گويند شكيبايى كن و از آنان به نيكويى دورى كن‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5486,
            "text": "و مرا با منكران متنعم واگذار و اندكى به آنان مهلت ده‌",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5487,
            "text": "بى‌گمان نزد ما بندها و آتش دوزخ است‌",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5488,
            "text": "و خوراكى گلوگير و عذابى دردناك‌",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5489,
            "text": "روزى كه زمين و كوهها بلرزد و كوهها [چون‌] ريگهاى روان شود",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5490,
            "text": "ما به سوى شما پيامبرى فرستاده‌ايم كه بر شما گواه است، همچنانكه به سوى فرعون هم پيامبرى فرستاديم‌",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5491,
            "text": "آنگاه فرعون از پيامبر نافرمانى كرد، و او را به گير و دارى سخت فرو گرفتيم‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5492,
            "text": "پس اگر كفرورزيد، چگونه از روزى كه كودكان را پير مى‌گرداند، مى‌پرهيزيد [و در امان مى‌مانيد]؟",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5493,
            "text": "و آسمان بدان شكافته گردد، كه وعده او انجام گرفتنى است‌",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5494,
            "text": "اين پندآموزى است، پس هر كس كه خواهد راهى به سوى پروردگارش پيش گيرد",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 574,
            "ruku": 508,
            "hizbQuarter": 229,
            "sajda": false
          },
          {
            "number": 5495,
            "text": "بى‌گمان پروردگارت مى‌داند كه تو و گروهى از همراهانت نزديك به دو سوم شب و [گاه‌] نصف آن و [گاه‌] يك سوم آن را [به نماز] بر مى‌خيزى، و خداوند است كه شب و روز را سامان مى‌دهد، و معلوم داشت كه شما هرگز آن را تاب نمى‌آوريد، لذا از شما درگذشت، حال هر چه مقدور باشد از قرآن بخوانيد، معلوم داشت كه بعضى از شما [ممكن است‌] بيمار باشند و ديگران در اين سرزمين [تلاش مى‌كنند و] گام مى‌زنند و روزى خود از فضل الهى مى‌جويند، و ديگرانى هم هستند كه در راه خدا كارزار مى‌كنند، لذا هر چه مقدور باشد از آن بخوانيد، و نماز [پنج‌گانه واجب‌] را برپا داريد و زكات بپردازيد، و در راه خدا قرض‌الحسنه دهيد، و هر آنچه از كارهاى خير كه براى خود پيش انديشى كنيد، پاداش آن را بهتر و بزرگتر نزد خداوند خواهيد يافت، و از خداوند آمرزش خواهيد، كه خداوند آمرزگار مهربان است",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 509,
            "hizbQuarter": 230,
            "sajda": false
          }
        ]
      },
      {
        "number": 74,
        "name": "سُورَةُ المُدَّثِّرِ",
        "englishName": "Al-Muddaththir",
        "englishNameTranslation": "The Cloaked One",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5496,
            "text": "اى مرد جامه بر خود پيچيده‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5497,
            "text": "برخيز و هشدار ده‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5498,
            "text": "و پروردگارت را تكبير گوى‌",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5499,
            "text": "و دامنت را پاكيزه بدار",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5500,
            "text": "و از آلايش [شرك‌] بپرهيز",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5501,
            "text": "و بخششى مكن كه پاداش [و پاسخ‌] بزرگتريابى‌",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5502,
            "text": "و در راه پروردگارت شكيبايى كن‌",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5503,
            "text": "و چون در صور دميده شود",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5504,
            "text": "بدين‌سان چنين روز، روزى دشوار است‌",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5505,
            "text": "[و] بر كافران آسان نيست‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5506,
            "text": "مرا با كسى كه تنها آفريده‌ام واگذار",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5507,
            "text": "و براى او مالى روزافزون قرار دادم‌",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5508,
            "text": "و پسرانى حاضر و ناظر",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5509,
            "text": "و به او [چه بسيار] ميدان [و امكان‌] دادم‌",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5510,
            "text": "باز طمع دارد كه افزونتر كنم‌",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5511,
            "text": "حاشا، كه او به آيات ما ستيزه‌جوست‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5512,
            "text": "زودا كه مشقتى سنگين بر او تحميل كنم‌",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 575,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5513,
            "text": "چرا كه او انديشيد و سگاليد",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5514,
            "text": "پس مرگ بر او باد چگونه سگاليد",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5515,
            "text": "باز مرگ بر او باد چگونه سگاليد",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5516,
            "text": "سپس بازنگريست‌",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5517,
            "text": "و آنگاه روى درهم كشيد و ترشرويى كرد",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5518,
            "text": "سپس روى برتافت و گردن كشيد",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5519,
            "text": "و گفت اين جز جادويى فرا گرفته [از ديگران‌] نيست‌",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5520,
            "text": "اين جز گفتار آدمى نيست‌",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5521,
            "text": "زودا كه به دوزخش در آوريم‌",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5522,
            "text": "و تو چه دانى كه دوزخ چيست‌",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5523,
            "text": "نه باقى مى‌گذارد، نه رها مى‌كند",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5524,
            "text": "گرداننده رنگ پوستهاست [با سوختن‌]",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5525,
            "text": "بر آن نوزده [نگهبان‌] است‌",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5526,
            "text": "و ما نگهبانان آتش دوزخ را جز از فرشتگان نگماشته‌ايم، و شماره آنان را جز آزمونى براى كافران نگردانده‌ايم، تا سرانجام اهل كتاب يقينى افزونتر يابند، و اهل ايمان، بر ايمان خود بيفزايند، و اهل كتاب و مؤمنان [/مسلمانان‌] شك و شبهه نورزند، و تا سرانجام بيماردلان و كافران بگويند، خداوند از اين توصيف چه مى‌خواهد؟ بدين‌سان خداوند هر كس را بخواهد بيراه مى‌گذارد، و هر كس را بخواهد به راه مى‌آورد، و سپاهيان پروردگارت را كسى جز خود او نمى‌شناسد، و اين جز پندآموزى براى بشر نيست‌",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 510,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5527,
            "text": "چنين نيست [كه گويند]، سوگند به ماه‌",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5528,
            "text": "و شب چون بگذرد",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5529,
            "text": "[و] سوگند به صبح چون روشن شود",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5530,
            "text": "آن يكى از سترگهاست‌",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5531,
            "text": "هشداردهنده بشر است‌",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5532,
            "text": "براى هر كس از شما كه خواهد [در راه حق و ايمان‌] پيشتر آيد، يا بازپس ماند",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5533,
            "text": "هر كس در گرو كردار خويش است‌",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5534,
            "text": "مگر اصحاب يمين‌",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5535,
            "text": "كه در بوستانهايى [هستند و] همپرسى مى‌كنند",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5536,
            "text": "از گناهكاران‌",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5537,
            "text": "كه چه چيزى شما را به راه دوزخ كشاند؟",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5538,
            "text": "گويند از نمازگزاران نبوديم‌",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5539,
            "text": "و بينوايان را هم اطعام نمى‌كرديم‌",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5540,
            "text": "و با ژاژخايان ژاژخايى مى‌كرديم‌",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5541,
            "text": "و روز جزا را دروغ مى‌انگاشتيم‌",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5542,
            "text": "تا آنكه مرگ به سراغمان آمد",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 576,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5543,
            "text": "حال شفاعت شفيعان سودشان ندهد",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5544,
            "text": "ايشان را چه مى‌شود كه از اندرز رويگردانند",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5545,
            "text": "گويى درازگوشانى رمانند",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5546,
            "text": "كه از شير مى‌گريزند",
            "numberInSurah": 51,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5547,
            "text": "بلكه هر يك از ايشان مى‌خواهد كه به او نامه‌هايى سرگشاده دهند",
            "numberInSurah": 52,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5548,
            "text": "حاشا، حق اين است كه از آخرت نمى‌ترسند",
            "numberInSurah": 53,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5549,
            "text": "حاشا، آن پندآموزى است‌",
            "numberInSurah": 54,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5550,
            "text": "و هر كس خواهد از آن پندگيرد",
            "numberInSurah": 55,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          },
          {
            "number": 5551,
            "text": "و پند نگيرند مگر آنكه خدا خواهد، اوست كه سزاوار پرهيز ورزيدن است، و سزاوار آمرزگارى‌",
            "numberInSurah": 56,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 511,
            "hizbQuarter": 230,
            "sajda": false
          }
        ]
      },
      {
        "number": 75,
        "name": "سُورَةُ القِيَامَةِ",
        "englishName": "Al-Qiyaama",
        "englishNameTranslation": "The Resurrection",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5552,
            "text": "سوگند به روز قيامت مى‌خورم‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5553,
            "text": "و سوگند به نفس ملامت‌گر",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5554,
            "text": "آيا انسان چنين مى‌پندارد كه هرگز استخوانهاى [پوسيده و پراكنده‌] او را گرد نمى‌آوريم؟",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5555,
            "text": "حق اين است كه تواناييم بر اين كه سرانگشتهاى او را فراهم آوريم‌",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5556,
            "text": "بلكه انسان مى‌خواهد در مهلتى كه در پيش دارد، فسق و فجور كند",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5557,
            "text": "[از سر استكبار] مى‌پرسد روز قيامت چه وقت است؟",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5558,
            "text": "آنگاه كه ديدگان خيره شود",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5559,
            "text": "و ماه تيره شود",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5560,
            "text": "و خورشيد و ماه فرا يكديگر آورده شوند",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5561,
            "text": "انسان گويد در چنين روزى گريزگاه كجاست؟",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5562,
            "text": "حاشا، پناهى نيست‌",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5563,
            "text": "سرانجام [همه چيز و همه كار] با پروردگار توست‌",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5564,
            "text": "در اين روز انسان را از آنچه پيش فرستاده يا باز پس داشته است آگاه سازند",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5565,
            "text": "حق اين است كه انسان بر نفس خود بصير است‌",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5566,
            "text": "ولو بهانه‌هايش را در ميان آورد",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5567,
            "text": "زبانت را به [بازخوانى وحى‌] مجنبان كه در كار آن شتاب كنى‌",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5568,
            "text": "گردآورى و بازخوانى آن بر عهده ماست‌",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5569,
            "text": "و چون آن را باز خوانيم، از بازخوانى‌اش پيروى كن‌",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5570,
            "text": "سپس شرح و بيان آن بر عهده ماست‌",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 577,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5571,
            "text": "حاشا، حق اين است كه بهره زودياب [دنيوى‌] را دوست داريد",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5572,
            "text": "و [بهره ديرياب‌] آخرت را فرو مى‌گذاريد",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5573,
            "text": "در چنين روز، چهره‌هايى تازه و خرم باشد",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5574,
            "text": "به سوى پروردگارشان نگران‌",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5575,
            "text": "و در چنين روز چهره‌هايى دژم باشد",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5576,
            "text": "كه مى‌داند بلايى كمرشكن بر سر او آيد",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5577,
            "text": "حاشا، چون جان به گلوگاهها رسد",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5578,
            "text": "و گفته شود كيست افسونگر [درمانگر]",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5579,
            "text": "و به يقين داند كه هنگام جدايى است‌",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5580,
            "text": "و هنگامه بالا گيرد",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5581,
            "text": "در چنين روز سير و سرانجام به سوى پروردگار توست‌",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 512,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5582,
            "text": "[مدعى‌] نه [حق را] تصديق كرد، و نه نمازگزارد",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5583,
            "text": "بلكه دروغ انگاشت و رويگردان شد",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5584,
            "text": "سپس تبختركنان به سوى خانواده‌اش رفت‌",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5585,
            "text": "واى بر تو، باز هم واى بر تو",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5586,
            "text": "سپس واى بر تو، و باز هم واى بر تو",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5587,
            "text": "آيا انسان مى‌پندارد كه به امان خود رها خواهد شد؟",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5588,
            "text": "آيا نطفه‌اى از منى كه [در رحم‌] ريخته شده بود، نبود؟",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5589,
            "text": "سپس خون بسته‌اى بود كه [خداوندش‌] آفريد و به سامان كرد",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5590,
            "text": "و از آن جفتى نرينه و مادينه پديد آورد",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5591,
            "text": "آيا آن [آفريدگار] توانا بر آن نيست كه مردگان را زنده كند؟",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 513,
            "hizbQuarter": 231,
            "sajda": false
          }
        ]
      },
      {
        "number": 76,
        "name": "سُورَةُ الإِنسَانِ",
        "englishName": "Al-Insaan",
        "englishNameTranslation": "Man",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 5592,
            "text": "آرى مدتى از روزگار بر انسان گذشت كه هنوز چيزى قابل ذكر نبود",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5593,
            "text": "ما انسان را از نطفه‌اى آميخته آفريده‌ايم و به آزمونش كشانده‌ايم، و او را شنوا و بينا ساخته‌ايم‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5594,
            "text": "ما او را به راه آورده‌ايم، چه سپاسگزار باشد، چه ناسپاس‌",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5595,
            "text": "ما براى كافران زنجيرها و بندها و آتش فروزان آماده ساخته‌ايم‌",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5596,
            "text": "بى‌گمان نيكان از جامى كه آميزه آن كافور است، مى‌نوشند",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 578,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5597,
            "text": "چشمه‌اى كه بندگان [درستكار] خداوند از آن مى‌آشامند و روان مى‌كنندش هرگونه بخواهند",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5598,
            "text": "آنان به نذر خود وفا مى‌كنند و از روزى كه شرش دامن‌گستر است، بيم دارند",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5599,
            "text": "و خوراك را با وجود دوست داشتنش به بينوا و يتيم و اسير اطعام كنند",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5600,
            "text": "[و در دل گويند] ما فقط براى خشنودى خداوند شما را اطعام مى‌كنيم، از شما نه پاداشى مى‌خواهيم، و نه سپاسى‌",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5601,
            "text": "ما از پروردگارمان در روزى كه دژم و دشوار است، مى‌ترسيم‌",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5602,
            "text": "سپس خداوند آنان را از شر آن روز در امان دارد و به ايشان تازگى و خرمى بنماياند",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5603,
            "text": "و به آنان به خاطر شكيبى كه ورزيده‌اند بوستانى [بهشتى‌] و [جامه‌] ابريشم پاداش دهد",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5604,
            "text": "در آنجا بر اورنگها تكيه زده، در آنجا نه [گرماى‌] خورشيدى بينند و نه سرماى سختى‌",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5605,
            "text": "و سايه‌هاى آن به ايشان نزديك باشد، و ميوه‌هاى آن در كمال دسترس پذيرى باشد",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5606,
            "text": "و بر ايشان ظرفهايى سيمين و كوزه‌هايى كه آبگينه است مى‌گردانند",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5607,
            "text": "آبگينه‌هايى از سيم كه آنها را به اندازه پيموده باشند",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5608,
            "text": "و در آنجا به آنان جامى بنوشانند كه آميزه آن زنجبيل است‌",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5609,
            "text": "از چشمه‌اى در آنجا كه سلسبيل ناميده مى‌شود",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 231,
            "sajda": false
          },
          {
            "number": 5610,
            "text": "و پسرانى جاويدان برگرد آنان گردند، كه چون بنگريشان، پنداريشان كه مرواريد پراكنده‌اند",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5611,
            "text": "و چون آنجا را بنگرى، ناز و نعمت [فراوان‌] و ملك بيكران بينى‌",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5612,
            "text": "بر بالاى آنان جامه‌هايى از ابريشم نازك سبز و ابريشم ستبر است، و به دستبندهاى سيمين آراسته‌اند، و پروردگارشان به آنان شرابى پاكيزه نوشاند",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5613,
            "text": "اين پاداشى براى شماست و سعى شما مشكور است‌",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 514,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5614,
            "text": "ما خود قرآن را بر تو چنانكه بايد و شايد فرو فرستاده‌ايم‌",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5615,
            "text": "به انتظار حكم پروردگارت شكيبا باش و از آنان از هيچ گناهكار يا ناسپاسى اطاعت مكن‌",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5616,
            "text": "و هر بامداد و شامگاهى نام پروردگارت را ياد كن‌",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 579,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5617,
            "text": "و در بخشى از شب براى او [به نماز] سجده كن و در بخشى بلند از شب او را نيايش كن‌",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5618,
            "text": "بى‌گمان اينان بهره [زودياب‌] دنيوى را دوست دارند و روزى سنگين را كه در پيش دارند، رها مى‌كنند",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5619,
            "text": "ما آنان را آفريده‌ايم و پيوندشان را استوار داشته‌ايم، و چون خواهيم آنان را به همانندانشان جانشين مى‌سازيم‌",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5620,
            "text": "اين پندآموزى است، پس هر كس كه خواهد راهى به سوى پروردگارش در پيش گيرد",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5621,
            "text": "و [چنين چيزى‌] نخواهيد مگر آنكه خدا خواهد، بى‌گمان خداوند داناى فرزانه است‌",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5622,
            "text": "هر كس را كه خواهد به جوار رحمت خويش در آورد، و براى ستمكاران [مشرك‌] عذابى دردناك آماده ساخته است‌",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 515,
            "hizbQuarter": 232,
            "sajda": false
          }
        ]
      },
      {
        "number": 77,
        "name": "سُورَةُ المُرۡسَلَاتِ",
        "englishName": "Al-Mursalaat",
        "englishNameTranslation": "The Emissaries",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5623,
            "text": "سوگند به فرستادگان پياپى‌",
            "numberInSurah": 1,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5624,
            "text": "و تندبادهاى سخت‌",
            "numberInSurah": 2,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5625,
            "text": "و سوگند به بادهاى پراكنده ساز [ابرها]",
            "numberInSurah": 3,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5626,
            "text": "و جداسازان [حق و باطل‌]",
            "numberInSurah": 4,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5627,
            "text": "و سوگند به فرشتگان وحى‌آور",
            "numberInSurah": 5,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5628,
            "text": "براى اتمام حجت و هشدار",
            "numberInSurah": 6,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5629,
            "text": "كه آنچه به شما وعده داده شده است، رخ دادنى است‌",
            "numberInSurah": 7,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5630,
            "text": "پس چون ستارگان تيره شوند",
            "numberInSurah": 8,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5631,
            "text": "و چون آسمان شكافته شود",
            "numberInSurah": 9,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5632,
            "text": "و چون كوهها بر باد رود",
            "numberInSurah": 10,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5633,
            "text": "و چون پيامبران را ميقات معين كنند",
            "numberInSurah": 11,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5634,
            "text": "براى كدامين روز زمان داده شود؟",
            "numberInSurah": 12,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5635,
            "text": "براى روز داورى‌",
            "numberInSurah": 13,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5636,
            "text": "و تو چه دانى روز داورى چيست؟",
            "numberInSurah": 14,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5637,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 15,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5638,
            "text": "آيا پيشينيان را نابود نكرديم؟",
            "numberInSurah": 16,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5639,
            "text": "سپس به دنبال آنان واپسينان را روان نساختيم؟",
            "numberInSurah": 17,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5640,
            "text": "با گناهكاران چنين رفتار كنيم‌",
            "numberInSurah": 18,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5641,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 19,
            "juz": 29,
            "manzil": 7,
            "page": 580,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5642,
            "text": "آيا شما را از آبى بى‌مقدار نيافريده‌ايم؟",
            "numberInSurah": 20,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5643,
            "text": "كه سپس آن را در جايگاهى استوار قرار داديم‌",
            "numberInSurah": 21,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5644,
            "text": "تا هنگامى معين‌",
            "numberInSurah": 22,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5645,
            "text": "پس سامان داديم و چه نيك سامانگريم‌",
            "numberInSurah": 23,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5646,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 24,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5647,
            "text": "آيا زمين را فراگير نساختيم؟",
            "numberInSurah": 25,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5648,
            "text": "چه براى زندگان، چه براى مردگان‌",
            "numberInSurah": 26,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5649,
            "text": "و در آن كوههاى بلند پديد آورديم و به شما آبى شيرين نوشانديم‌",
            "numberInSurah": 27,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5650,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 28,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5651,
            "text": "به سوى چيزى كه انكارش مى‌كرديد برويد",
            "numberInSurah": 29,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5652,
            "text": "به سوى دودى داراى سه شاخه برويد",
            "numberInSurah": 30,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5653,
            "text": "كه نه سايه‌گستر است و نه از لهيب آتش باز مى‌دارد",
            "numberInSurah": 31,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5654,
            "text": "آن شراره‌هايى همچون كوشك مى‌پراكند",
            "numberInSurah": 32,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5655,
            "text": "گويى شترانى زرد سيه‌تاب هستند",
            "numberInSurah": 33,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5656,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 34,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5657,
            "text": "اين روزى است كه سخن نگويند",
            "numberInSurah": 35,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5658,
            "text": "و به ايشان اجازه ندهند كه عذرخواهى كنند",
            "numberInSurah": 36,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5659,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 37,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5660,
            "text": "اين روز داورى است، شما و پيشينيان را گرد آورده‌ايم‌",
            "numberInSurah": 38,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5661,
            "text": "اگر نيرنگى داريد براى من بسگاليد",
            "numberInSurah": 39,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5662,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 40,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 516,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5663,
            "text": "پرهيزگاران در سايه‌ساران و [كنار] چشمه‌سارانند",
            "numberInSurah": 41,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5664,
            "text": "و ميوه‌هايى كه دلخواهشان است‌",
            "numberInSurah": 42,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5665,
            "text": "به خاطر كارهايى كه كرده‌ايد، بخوريد و بنوشيد، [خوش و] گوارا",
            "numberInSurah": 43,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5666,
            "text": "ما بدين گونه نيكوكاران را جزا مى‌دهيم‌",
            "numberInSurah": 44,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5667,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 45,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5668,
            "text": "اندكى بخوريد و بهره‌مند باشيد، چرا كه شما گناهكاريد",
            "numberInSurah": 46,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5669,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 47,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5670,
            "text": "و چون به ايشان گفته مى‌شد نماز بگزاريد، نماز نمى‌گزاردند",
            "numberInSurah": 48,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5671,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 49,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          },
          {
            "number": 5672,
            "text": "پس به كدامين سخن پس از آن ايمان مى‌آورند",
            "numberInSurah": 50,
            "juz": 29,
            "manzil": 7,
            "page": 581,
            "ruku": 517,
            "hizbQuarter": 232,
            "sajda": false
          }
        ]
      },
      {
        "number": 78,
        "name": "سُورَةُ النَّبَإِ",
        "englishName": "An-Naba",
        "englishNameTranslation": "The Announcement",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5673,
            "text": "از چه از همديگر مى‌پرسند؟",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5674,
            "text": "از خبر بزرگ‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5675,
            "text": "همانكه ايشان در آن اختلاف دارند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5676,
            "text": "چنين نيست، زودا كه بدانند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5677,
            "text": "باز چنين نيست زودا كه بدانند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5678,
            "text": "آيا زمين را زيرانداز نساخته‌ايم؟",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5679,
            "text": "و كوهها را مانند ميخها",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5680,
            "text": "و شما را به صورت جفتها[ى گوناگون‌] آفريده‌ايم‌",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5681,
            "text": "و خوابتان را مايه آرامش گردانده‌ايم‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5682,
            "text": "و شب را همچون پوششى ساخته‌ايم‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5683,
            "text": "و روز را وقت تلاش معاش قرار داده‌ايم‌",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5684,
            "text": "و برفرازتان هفت آسمان استوار برافراشته‌ايم‌",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5685,
            "text": "و چراغى درخشان پديد آورده‌ايم‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5686,
            "text": "و از ابرها آبى ريزان فرو فرستاده‌ايم‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5687,
            "text": "تا بدان دانه و گياه برآوريم‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5688,
            "text": "و بوستانهايى انبوه‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5689,
            "text": "بى‌گمان روز داورى، هنگامى معين است‌",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5690,
            "text": "روزى كه در صور دميده شود و فوج فوج بياييد",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5691,
            "text": "و آسمان گشوده شود و دروازه دروازه باشد",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5692,
            "text": "و كوهها روان كرده شود و چون سرابى باشد",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5693,
            "text": "بى‌گمان جهنم كمينگاهى است‌",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5694,
            "text": "و بازگشتگاه سركشان است‌",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5695,
            "text": "كه روزگارانى در آن به سر برند",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5696,
            "text": "در آن نه خنكى بينند نه نوشابه‌اى چشند",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5697,
            "text": "مگر آب جوش و چركابه‌",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5698,
            "text": "كه جزايى موافق [اعمال ايشان‌] است‌",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5699,
            "text": "آنان حساب و كتابى را اميد نمى‌داشتند",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5700,
            "text": "و آيات ما را سخت تكذيب مى‌كردند",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5701,
            "text": "و هر چيزى را در نامه‌اى به شمار آورده‌ايم‌",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5702,
            "text": "پس بچشيد كه هرگز جز به عذابتان نمى‌افزاييم‌",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 582,
            "ruku": 518,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5703,
            "text": "بى‌گمان پرهيزگاران را رستگارى است",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5704,
            "text": "بوستانها و درختان انگور",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5705,
            "text": "و [حوريان‌] نارپستان همسال‌",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5706,
            "text": "و جامهاى سرشار",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5707,
            "text": "در آنجا نه لغوى بشنوند و نه دروغى‌",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5708,
            "text": "پاداشى است از سوى پروردگارت و بخششى بسنده است‌",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5709,
            "text": "پروردگار آسمانها و زمين و مابين آنها كه خداى رحمان است، و از جانب او اجازه سخن گفتن ندارند",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5710,
            "text": "روزى كه روح [جبرئيل‌] و فرشتگان به صف ايستند و هيچ‌يك جز كسى كه خداوند رحمان به او اجازه دهد، و صواب گويد، سخن نگويند",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5711,
            "text": "اين روز واقعى است پس هر كه خواهد بازگشتگاهى به سوى پروردگار خويش بجويد",
            "numberInSurah": 39,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5712,
            "text": "ما شما را از عذابى نزديك هشدار داديم، از روزى كه هر انسانى به [نتيجه‌] آنچه به دستان خويش پيش فرستاده است، بنگرد و كافر [از سر حسرت‌] گويد كاش من خاك بودم‌",
            "numberInSurah": 40,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 519,
            "hizbQuarter": 233,
            "sajda": false
          }
        ]
      },
      {
        "number": 79,
        "name": "سُورَةُ النَّازِعَاتِ",
        "englishName": "An-Naazi'aat",
        "englishNameTranslation": "Those who drag forth",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5713,
            "text": "سوگند به فرشتگانى كه در گرفتن جان [كافران‌] به سختى كوشند",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5714,
            "text": "و سوگند به فرشتگانى كه جان [مؤمنان‌] به نرمى گيرند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5715,
            "text": "و سوگند به فرشتگانى كه شنا كنان هبوط كنند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5716,
            "text": "و سوگند به فرشتگان پيشتاز",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5717,
            "text": "و به فرشتگانى كه كار سازند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5718,
            "text": "روزى كه [زمين‌] لرزنده بلرزد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5719,
            "text": "و پيايند در پى آن آيد",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5720,
            "text": "در چنين روز دلهايى ترسان و لرزان باشند",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5721,
            "text": "ديدگان ايشان فروشده‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5722,
            "text": "گويند آيا ما به حالت نخستين [زندگى‌] باز گردانده شويم‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5723,
            "text": "آنگاه كه استخوانهايى پوسيده شديم؟",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5724,
            "text": "گويند اين در آن صورت بازگشتى زيانبار خواهد بود",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5725,
            "text": "آن همين يك بانگ يگانه است‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5726,
            "text": "آنگاه ايشان در هامون [رستاخيز] باشند",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5727,
            "text": "آيا داستان موسى به تو رسيده است؟",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 583,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5728,
            "text": "چون پروردگارش او را در وادى مقدس طوى ندا داد",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5729,
            "text": "[فرمود] به سوى فرعون برو كه او سر به طغيان بر داشته است‌",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5730,
            "text": "و بگو آيا بر آن هستى كه پاكدلى پيشه كنى؟",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5731,
            "text": "و تو را به سوى پروردگارت راه نمايم و خشيت يابى؟",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5732,
            "text": "و به او معجزه سترگ نشان داد",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5733,
            "text": "پس او انكار كرد و سركشيد",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5734,
            "text": "سپس شتابان روى برتافت‌",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5735,
            "text": "و [سپاه جادوگران را] گردآورد و ندا [به ادعا] در داد",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5736,
            "text": "و گفت من پروردگار برتر شما هستم‌",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5737,
            "text": "آنگاه خداوند به عقوبت [گناه‌] واپسين و نخستين او را فرو گرفت‌",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5738,
            "text": "بى‌گمان در اين براى هر كس كه خشيت داشته باشد، عبرتى است‌",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 520,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5739,
            "text": "آيا شما استوار آفريده‌تريد يا آسمان كه آن را برافراشت‌",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5740,
            "text": "سقف آن را بلند بركشيد و آن را استوار كرد",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5741,
            "text": "و شب آن را تاريك كرد و روز آن را برآورد",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5742,
            "text": "و بعد از آن زمين را گستراند",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5743,
            "text": "از آن آب و چراگاه بيرون آورد",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5744,
            "text": "و كوهها را استوار بداشت‌",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5745,
            "text": "براى برخوردارى شما و چارپايانتان‌",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5746,
            "text": "آنگاه كه هنگامه بزرگ فرارسد",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5747,
            "text": "روزى كه انسان از آنچه كوشيده است، ياد آورد",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5748,
            "text": "و جهنم براى هر كه درنگرد آشكار شود",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5749,
            "text": "آنگاه هر كس طغيان‌ورزيده باشد",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5750,
            "text": "و زندگانى دنيا را برگزيده باشد",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5751,
            "text": "بى‌شك جهنم جايگاه اوست‌",
            "numberInSurah": 39,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5752,
            "text": "و اما هر كس كه از ايستادن [در موضع حساب و حشر] در پيشگاه پروردگارش ترسيده و نفس را از هوى و هوس بازداشته باشد",
            "numberInSurah": 40,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5753,
            "text": "بى‌گمان بهشت جايگاه اوست‌",
            "numberInSurah": 41,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5754,
            "text": "از تو درباره قيامت پرسند كه استقرار آن چه وقت است؟",
            "numberInSurah": 42,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5755,
            "text": "تو در مقامى نيستى كه از آن ياد كنى‌",
            "numberInSurah": 43,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5756,
            "text": "منتهاى علم آن با پروردگارت توست‌",
            "numberInSurah": 44,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5757,
            "text": "تو فقط هشداردهنده كسى هستى كه از آن بهراسد",
            "numberInSurah": 45,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          },
          {
            "number": 5758,
            "text": "روزى كه آن را بنگرند گويى جز شبانگاهى يا بامدادش [در دنيا] به سر نبرده‌اند",
            "numberInSurah": 46,
            "juz": 30,
            "manzil": 7,
            "page": 584,
            "ruku": 521,
            "hizbQuarter": 233,
            "sajda": false
          }
        ]
      },
      {
        "number": 80,
        "name": "سُورَةُ عَبَسَ",
        "englishName": "Abasa",
        "englishNameTranslation": "He frowned",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5759,
            "text": "ترشرويى كرد و روى بر تافت‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5760,
            "text": "از اينكه آن نابينا به نزد او آمد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5761,
            "text": "و تو چه دانى چه بسا او پاكدلى ورزد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5762,
            "text": "يا پندگيرد و پندش سود بخشد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5763,
            "text": "اما كسى كه بى‌نيازى نشان مى‌دهد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5764,
            "text": "[چرا] تو به او مى‌پردازى‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5765,
            "text": "و اگر هم پاكدلى پيشه نكند، ايرادى بر تو نيست‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5766,
            "text": "و اما كسى كه شتابان به سويت آمد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5767,
            "text": "و او خشيت مى‌ورزد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5768,
            "text": "تو از او به ديگرى مى‌پردازى‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5769,
            "text": "چنين نيست، آن پندآموزى است‌",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5770,
            "text": "هر كه خواهد آن را ياد كند",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5771,
            "text": "در ميان صحيفه‌هاى ارجمند است‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5772,
            "text": "كه بلندمرتبه است و پاك داشته‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5773,
            "text": "به دست نويسندگانى‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5774,
            "text": "كه گراميانند و نيكان‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5775,
            "text": "مرگ بر انسان [كافر] چقدر كافركيش است‌",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5776,
            "text": "مگر از چه چيزى او را آفريده است‌",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5777,
            "text": "او را از نطفه‌اى آفريده است و سر و سامان بخشيده است‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5778,
            "text": "سپس راه را بر او آسان داشته است‌",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5779,
            "text": "سپس او را ميرانيد و او را در گور كرد",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5780,
            "text": "سپس هر زمان كه خواست او را برانگيزد",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5781,
            "text": "حاشا، او آنچه به او فرمان داده بود، انجام نداد",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5782,
            "text": "پس بايد انسان به خوراك خويش بنگرد",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5783,
            "text": "ما آب را به فراوانى فرو ريخته‌ايم‌",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5784,
            "text": "سپس زمين را به نيكى برشكافته‌ايم‌",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5785,
            "text": "و در آن دانه [ها] رويانده‌ايم‌",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5786,
            "text": "و نيز انگور و سبزيجات‌",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5787,
            "text": "و درخت زيتون و خرما",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5788,
            "text": "و بوستانهاى انبوه‌",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5789,
            "text": "و ميوه و علف‌",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5790,
            "text": "كالايى براى شما و براى چارپايانتان‌",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5791,
            "text": "چون بانگ گوش‌فرسا در آيد",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5792,
            "text": "روزى كه انسان از برادرش بگريزد",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5793,
            "text": "و از مادرش و پدرش‌",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5794,
            "text": "و همسرش و پسرانش‌",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5795,
            "text": "براى هر كس از آنان در چنين روز كارى باشد كه او را از همه باز دارد",
            "numberInSurah": 37,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5796,
            "text": "در چنين روز چهره‌هايى تابناك باشد",
            "numberInSurah": 38,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5797,
            "text": "خندان و شادمان‌",
            "numberInSurah": 39,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5798,
            "text": "و در چنين روز بر چهره‌هايى غبار باشد",
            "numberInSurah": 40,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5799,
            "text": "تيرگى آنها را فرو گيرد",
            "numberInSurah": 41,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5800,
            "text": "اينان همان كافركيشان نافرمانند",
            "numberInSurah": 42,
            "juz": 30,
            "manzil": 7,
            "page": 585,
            "ruku": 522,
            "hizbQuarter": 234,
            "sajda": false
          }
        ]
      },
      {
        "number": 81,
        "name": "سُورَةُ التَّكۡوِيرِ",
        "englishName": "At-Takwir",
        "englishNameTranslation": "The Overthrowing",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5801,
            "text": "آنگاه كه خورشيد تاريك شود",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5802,
            "text": "و آنگاه كه ستارگان رو به خاموشى نهند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5803,
            "text": "و آنگاه كه كوهها روان كرده شوند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5804,
            "text": "و آنگاه كه شتران آبستن ده‌ماهه‌[ى بس عزيز] وانهاده گردند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5805,
            "text": "و آنگاه كه جانوران گرد آورده شوند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5806,
            "text": "و آنگاه كه درياها افروخته گردد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5807,
            "text": "و آنگاه كه جانها را قرين همدگر كنند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5808,
            "text": "و آنگاه كه از دختر زنده به گور شده پرسيده شود",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5809,
            "text": "كه به كدامين گناه [به ناحق‌] كشته شده است‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5810,
            "text": "و چون كارنامه‌ها گشوده شود",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5811,
            "text": "و چون آسمان پاره پاره گردد",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5812,
            "text": "و چون دوزخ فروزانده گردد",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5813,
            "text": "و چون بهشت نزديك آورده شود",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5814,
            "text": "هر كس بداند كه چه آماده ساخته است‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5815,
            "text": "پس سوگند مى‌خورم به اختران باز گردنده‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5816,
            "text": "سيارگان پنهان رونده‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5817,
            "text": "و سوگند به شب چون برود",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5818,
            "text": "و سوگند به صبح چون بدمد",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5819,
            "text": "كه آن بر خوانده پيام‌آورى گرامى است‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5820,
            "text": "نيرومندى كه نزد [خداوند] صاحب عرش، صاحب مقام است‌",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5821,
            "text": "در آنجا فرمانش برند و امينش شمرند",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5822,
            "text": "و همسخن شما ديوانه نيست‌",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5823,
            "text": "و به راستى او [جبرئيل‌] را در افق آشكار ديده است‌",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5824,
            "text": "و او در [گزاردن‌] وحى غيبى، بخيل نيست‌",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5825,
            "text": "و آن سخن شيطان مطرود نيست‌",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5826,
            "text": "پس به كجا مى‌رويد؟",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5827,
            "text": "آن جز پندآموزى براى جهانيان نيست‌",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5828,
            "text": "براى هر كس از شما كه بخواهد راستى و درستى پيشه كند",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          },
          {
            "number": 5829,
            "text": "و نخواهيد مگر آنچه خداوند، كه پروردگار جهانيان است، بخواهد",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 586,
            "ruku": 523,
            "hizbQuarter": 234,
            "sajda": false
          }
        ]
      },
      {
        "number": 82,
        "name": "سُورَةُ الانفِطَارِ",
        "englishName": "Al-Infitaar",
        "englishNameTranslation": "The Cleaving",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5830,
            "text": "چون آسمان گسسته شود",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5831,
            "text": "و چون ستارگان فروريزند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5832,
            "text": "و چون درياها به هم راه يابند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5833,
            "text": "و چون گورها زير و زبر شود",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5834,
            "text": "هر كس بداند كه چه چيزى پيش داشته و چه چيزى را فرو گذاشته است‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5835,
            "text": "هان اى انسان چه چيزى تو را در حق پروردگار بخشنده‌ات غره كرد؟",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5836,
            "text": "همان كه تو را آفريد و استوار كرد و متعادل ساخت‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5837,
            "text": "به هر صورتى كه خواست [وجود] تو را تركيب كرد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5838,
            "text": "چنين نيست، بلكه [روز] جزا را انكار داريد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5839,
            "text": "و بر شما نگهبانانى معين شده است‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5840,
            "text": "كه نويسندگانى گرامى‌اند",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5841,
            "text": "آنچه مى‌كنيد مى‌دانند",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5842,
            "text": "بى‌گمان نيكان در ناز و نعمت [بهشتى‌]اند",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5843,
            "text": "و بى‌گمان نافرمانان در آتش دوزخند",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5844,
            "text": "در روز جزا وارد آن شوند",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5845,
            "text": "و از آن بدور [و در امان‌] نيستند",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5846,
            "text": "و چه دانى روز جزا چيست‌",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5847,
            "text": "باز چه دانى كه روز جزا چيست‌",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5848,
            "text": "روزى كه هيچ كس، اختيارى در حق هيچ كس ندارد، و در چنين روز كار با خداوند است‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 524,
            "hizbQuarter": 235,
            "sajda": false
          }
        ]
      },
      {
        "number": 83,
        "name": "سُورَةُ المُطَفِّفِينَ",
        "englishName": "Al-Mutaffifin",
        "englishNameTranslation": "Defrauding",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5849,
            "text": "واى بر كم‌فروشان‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5850,
            "text": "كسانى كه چون از مردمان پيمانه مى‌ستانند، كامل مى‌گيرند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5851,
            "text": "و چون به ايشان پيمانه دهند يا برايشان [كالايى‌] بركشند، كم مى‌گذارند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5852,
            "text": "آيا اينان نمى‌دانند كه برانگيخته خواهند شد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5853,
            "text": "در روزى بزرگ‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5854,
            "text": "روزى كه مردم در پيشگاه پروردگار جهانيان بايستند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 587,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5855,
            "text": "چنين نيست، بى‌گمان كارنامه نافرمانان در سجين است‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5856,
            "text": "و تو چه دانى سجين چيست؟",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5857,
            "text": "كتابى است نگاشته‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5858,
            "text": "در چنين روز واى بر منكران‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5859,
            "text": "كسانى كه روز جزا را منكرند",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5860,
            "text": "و جز هر تجاوزگر گناهكار هيچ كس آن را انكار نكند",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5861,
            "text": "كه چون آيات ما بر او خوانده شود، گويد افسانه‌هاى پيشينيان است‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5862,
            "text": "چنين نيست، بلكه آنچه كرده‌اند بر دلهايشان زنگار نهاده است‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5863,
            "text": "چنين نيست، آنان در چنين روز از ديدار پروردگارشان در پرده باشند",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5864,
            "text": "سپس آنان در آينده به دوزخند",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5865,
            "text": "سپس گفته شود اين همان است كه به آن انكار مى‌ورزيديد",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5866,
            "text": "چنين نيست، بى‌گمان كارنامه نيكان در عليين است‌",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5867,
            "text": "و تو چه دانى عليين چيست‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5868,
            "text": "كتابى است نگاشته‌",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5869,
            "text": "كه مقربان شاهد آنند",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5870,
            "text": "بى‌گمان نيكان در ناز و نعمت [بهشتى‌]اند",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5871,
            "text": "بر او رنگها [نشسته‌اند و] مى‌نگرند",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5872,
            "text": "در چهره‌هايشان خرمى ناز و نعمت را باز مى‌شناسى‌",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5873,
            "text": "از شرابى مهر شده به آنان نوشانند",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5874,
            "text": "كه مهر آن از مشك است و در اين راه است كه رقابت‌كوشان بايد به رقابت كوشند",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5875,
            "text": "و آميزه آن از تسنيم است‌",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5876,
            "text": "چشمه‌اى كه مقربان از آن مى‌نوشند",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5877,
            "text": "بى‌گمان كسانى كه گناه كرده‌اند به مؤمنان مى‌خنديدند",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5878,
            "text": "و چون از كنار ايشان مى‌گذشتند، به چشم و ابرو اشارت مى‌كردند",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5879,
            "text": "و چون به سوى خانواده‌شان باز مى‌گشتند، ريشخند كنان باز مى‌گشتند",
            "numberInSurah": 31,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5880,
            "text": "و چون ايشان را مى‌ديدند مى‌گفتند اينان گمراهانند",
            "numberInSurah": 32,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5881,
            "text": "و آنان را بر ايشان نگهبان نفرستاده بودند",
            "numberInSurah": 33,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5882,
            "text": "حال امروز مؤمنان به كافران مى‌خندند",
            "numberInSurah": 34,
            "juz": 30,
            "manzil": 7,
            "page": 588,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5883,
            "text": "كه بر او رنگها مى‌نگرند",
            "numberInSurah": 35,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          },
          {
            "number": 5884,
            "text": "كه آيا به كافران در برابر آنچه كرده بودند جزا داده مى‌شود",
            "numberInSurah": 36,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 525,
            "hizbQuarter": 235,
            "sajda": false
          }
        ]
      },
      {
        "number": 84,
        "name": "سُورَةُ الانشِقَاقِ",
        "englishName": "Al-Inshiqaaq",
        "englishNameTranslation": "The Splitting Open",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5885,
            "text": "آنگاه كه آسمان بشكافد",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5886,
            "text": "و به [امر] پروردگارش گوش بسپارد و خود چنين سزد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5887,
            "text": "و آنگاه كه زمين بازكشيده گردد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5888,
            "text": "و هر چه در دل آن است بيرون اندازد و تهى گردد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5889,
            "text": "و به [امر] پروردگارش گوش بسپارد و خود چنين سزد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5890,
            "text": "هان اى انسان تو در راه پروردگارت سخت كوشيده‌اى [و رنج برده‌اى‌] و به لقاى او نايل خواهى شد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5891,
            "text": "اما هر كس كه كارنامه‌اش به دست راستش داده شود",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5892,
            "text": "زودا كه حسابى آسان، با او محاسبه گردد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5893,
            "text": "و شادمان به سوى خانواده‌اش بازگردد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5894,
            "text": "و اما هر كس كه كارنامه‌اش از پس پشتش به او داده شود",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5895,
            "text": "زودا كه زارى سر دهد",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5896,
            "text": "و به دوزخ در آيد",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5897,
            "text": "چرا كه او در ميان خانواده‌اش [به ناحق‌] شادمان بود",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5898,
            "text": "و مى‌پنداشت كه هرگز [به قيامت‌] باز نگردد",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5899,
            "text": "آرى بى‌گمان پروردگارش به [احوال‌] او بينا بود",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5900,
            "text": "پس سوگند مى‌خورم به شفق‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5901,
            "text": "و به شب و آنچه گرد آورد",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5902,
            "text": "و سوگند به ماه چون كامل گردد",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5903,
            "text": "كه شما از حالى به حالى ديگر در آييد",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5904,
            "text": "پس ايشان را چه مى‌شود كه ايمان نمى‌آورند",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5905,
            "text": "و چون بر آنان قرآن خوانند، به سجده نيفتند",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": {
              "id": 14,
              "recommended": true,
              "obligatory": false
            }
          },
          {
            "number": 5906,
            "text": "بلكه كافران انكار مى‌ورزند",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5907,
            "text": "و خداوند به آنچه در دل مى‌دارند داناتر است‌",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5908,
            "text": "پس ايشان را از عذابى دردناك خبر ده‌",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5909,
            "text": "مگر كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، كه ايشان را پاداشى است ناكاسته [/بى‌منت‌]",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 589,
            "ruku": 526,
            "hizbQuarter": 236,
            "sajda": false
          }
        ]
      },
      {
        "number": 85,
        "name": "سُورَةُ البُرُوجِ",
        "englishName": "Al-Burooj",
        "englishNameTranslation": "The Constellations",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5910,
            "text": "سوگند به آسمانى كه داراى برجهاست‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5911,
            "text": "و سوگند به روز موعود",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5912,
            "text": "و سوگند به گواه و مورد گواهى‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5913,
            "text": "مرگ بر آتش افروزان گودال پرآتش‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5914,
            "text": "آتشى داراى هيزم‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5915,
            "text": "آنگاه كه آنان بر كناره آن نشسته بودند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5916,
            "text": "و آنان بر آنچه در حق مؤمنان مى‌كردند، گواه بودند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5917,
            "text": "و به آنان كينه نورزيدند، مگر از آن روى كه به خداوند پيروزمند ستوده ايمان مى‌ورزيدند",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5918,
            "text": "كسى كه فرمانروايى آسمانها و زمين او راست، و خداوند بر همه چيز گواه است‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5919,
            "text": "كسانى كه مردان مؤمن و زنان مؤمن را آزار دادند و سپس توبه نكردند، براى آنان عذاب جهنم [مهيا] است و عذاب آتش [در پيش‌] دارند",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5920,
            "text": "بى‌گمان كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، ايشان راست بوستانهايى [بهشتى‌] كه جويباران از فرودست آن جارى است، اين رستگارى بزرگ است‌",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5921,
            "text": "بى‌گمان سختگيرى پروردگارت، سهمگين است‌",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5922,
            "text": "اوست كه [آفرينش را] آغاز كرده است و باز مى‌گرداند",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5923,
            "text": "و اوست آمرزگار دوستدار",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5924,
            "text": "صاحب ارجمند عرش‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5925,
            "text": "كردگار خواسته خويش‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5926,
            "text": "آيا داستان سپاهيان به تو رسيده است؟",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5927,
            "text": "فرعون و قوم ثمود",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5928,
            "text": "آرى كافران در انكارند",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5929,
            "text": "و خداوند بر فراز ايشان چيره است‌",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5930,
            "text": "حق اين است كه آن قرآن مجيد است‌",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5931,
            "text": "در لوحى محفوظ",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 590,
            "ruku": 527,
            "hizbQuarter": 236,
            "sajda": false
          }
        ]
      },
      {
        "number": 86,
        "name": "سُورَةُ الطَّارِقِ",
        "englishName": "At-Taariq",
        "englishNameTranslation": "The Morning Star",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5932,
            "text": "سوگند به آسمان و شباينده‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5933,
            "text": "و تو چه دانى شباينده چيست‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5934,
            "text": "ستاره درخشان است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5935,
            "text": "هيچ جانى نيست مگر آنكه بر او نگهبانى هست‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5936,
            "text": "پس انسان بنگرد كه از چه آفريده شده است‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5937,
            "text": "از آبى جهيده آفريده شده است‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5938,
            "text": "كه از ميانه پشت و سينه‌ها بيرون آيد",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5939,
            "text": "بى‌گمان او بر باز گرداندن وى تواناست‌",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5940,
            "text": "روزى كه رازها از پرده بيرون افتد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5941,
            "text": "آنگاه او را يارا و ياورى نيست‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5942,
            "text": "و سوگند به آسمان باران‌دار",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5943,
            "text": "و سوگند به زمين برشكافته به گياه‌",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5944,
            "text": "كه آن [قرآن‌] سخنى قاطع است‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5945,
            "text": "و هزل نيست‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5946,
            "text": "آنان نيرنگى مى‌سگالند",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5947,
            "text": "من نيز تدبيرى مى‌انديشم‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          },
          {
            "number": 5948,
            "text": "پس كافران را مهلت ده، و اندك زمانى فرو گذارشان‌",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 528,
            "hizbQuarter": 236,
            "sajda": false
          }
        ]
      },
      {
        "number": 87,
        "name": "سُورَةُ الأَعۡلَىٰ",
        "englishName": "Al-A'laa",
        "englishNameTranslation": "The Most High",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5949,
            "text": "به نام پروردگارت كه بلندمرتبه است، تسبيح گوى‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5950,
            "text": "همانكه آفريد و سامان بخشيد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5951,
            "text": "همان كسى كه اندازه آفرينى و رهنمايى كرد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5952,
            "text": "و همان كسى كه چراگاهها[ى سبز و خرم‌] رويانيد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5953,
            "text": "و سرانجام آن را خاشاكى [پژمرده و] سياه گردانيد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5954,
            "text": "زودا كه تو را به خواندن آوريم و از ياد مگذارى‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5955,
            "text": "مگر آنچه خدا خواهد، كه او آشكار و نهان را مى‌داند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5956,
            "text": "و در كار تو آسانى پديد آوريم‌",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5957,
            "text": "پس اندرز ده، اگر [دهى‌] اندرز سود دهد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5958,
            "text": "زودا كسى كه خشيت پيشه كرده است، پند گيرد",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5959,
            "text": "و شقاوت‌پيشه از آن دورى كند",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5960,
            "text": "همان كسى كه به آتش سترگ درآيد",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5961,
            "text": "آنگاه در آنجا نه بميرد و نه زنده بماند",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5962,
            "text": "به راستى هر كس پاكدلى پيشه كرد، رستگار شد",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5963,
            "text": "و [هر كس‌] كه نام پروردگارش را ياد كرد و نيايش كرد",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 591,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5964,
            "text": "حق اين است كه شما زندگانى دنيا را بر مى‌گزينيد",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5965,
            "text": "و حال آنكه آخرت بهتر و پاينده‌تر است‌",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5966,
            "text": "اين [نكته‌] در كتب آسمانى پيشين هم هست‌",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5967,
            "text": "در صحف ابراهيم و موسى‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 529,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
      },
      {
        "number": 88,
        "name": "سُورَةُ الغَاشِيَةِ",
        "englishName": "Al-Ghaashiya",
        "englishNameTranslation": "The Overwhelming",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5968,
            "text": "آيا داستان هنگامه فراگير به تو رسيده است؟",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5969,
            "text": "در چنين روز چهره‌هايى خاكسار باشند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5970,
            "text": "سختى ديده و محنت كشيده‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5971,
            "text": "كه به آتشى بس سوزان درآيند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5972,
            "text": "از چشمه‌اى گرم به آنان نوشانده شود",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5973,
            "text": "خوراكى غير از خار خشك زهردار ندارند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5974,
            "text": "كه نه فربه گرداند، و نه گرسنگى را چاره كند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5975,
            "text": "در چنين روز چهره‌هايى خرم باشند",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5976,
            "text": "از كوشش [پيشين‌] خود خشنود",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5977,
            "text": "در بهشت برين‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5978,
            "text": "در آنجا لغوى نشنوند",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5979,
            "text": "در آنجا چشمه‌اى روان باشد",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5980,
            "text": "در آنجا تختهايى است بالا بلند",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5981,
            "text": "و كوزه‌هايى پيش نهاده‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5982,
            "text": "و بالشهايى رديف شده‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5983,
            "text": "و فرشهاى زرباف گسترده‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5984,
            "text": "آيا در شتر نمى‌نگرند كه چگونه [بديع‌] آفريده شده است؟",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5985,
            "text": "و نيز به آسمان كه چگونه برافراشته شده است‌",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5986,
            "text": "و نيز به كوهها كه چگونه بر قرار گرديده است‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5987,
            "text": "و نيز به زمين كه چگونه گسترده شده است‌",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5988,
            "text": "پس اندرز ده كه همانا تو اندرزگويى‌",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5989,
            "text": "[و] بر آنان مسلط [و حكمفرما] نيستى‌",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5990,
            "text": "مگر كسى كه روى برتابد و كفر پيشه كند",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5991,
            "text": "كه خداوند او را به عذابى سهمگين، عذاب كند",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5992,
            "text": "بى‌گمان بازگشت آنان به نزد ماست‌",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5993,
            "text": "سپس حساب آنان با ماست‌",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 592,
            "ruku": 530,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
      },
      {
        "number": 89,
        "name": "سُورَةُ الفَجۡرِ",
        "englishName": "Al-Fajr",
        "englishNameTranslation": "The Dawn",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 5994,
            "text": "سوگند به سپيده دم‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5995,
            "text": "و سوگند به شبهاى ده‌گانه‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5996,
            "text": "و سوگند به زوج و فرد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5997,
            "text": "و سوگند به شب چون سرآيد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5998,
            "text": "آيا در اينها خردمندان را سوگندى سزاوار است؟",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 5999,
            "text": "آيا نينديشيده‌اى كه پروردگارت در حق [قوم و سرزمين‌] عاد چگونه عمل كرد؟",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6000,
            "text": "كه ارم ستون‌دار داشت‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6001,
            "text": "همان كه مانندش در [هيچ‌يك از] شهرها ساخته نشده بود",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6002,
            "text": "و نيز قوم ثمود كه در وادى‌القرى، [خانه از] سنگ مى‌بريدند [و مى‌تراشيدند]",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6003,
            "text": "و فرعون صاحب سپاه‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6004,
            "text": "كسانى كه در شهرها سر به طغيان برداشته بودند",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6005,
            "text": "و در آنجا چه بسيار فتنه و فساد مى‌كردند",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6006,
            "text": "و سرانجام پروردگارت تازيانه عذاب را بر سر آنان كشيد",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6007,
            "text": "بى‌گمان پروردگارت در كمينگاه است‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6008,
            "text": "اما انسان چون پروردگارش او را بيازمايد و او را گرامى دارد و به او نعمت بخشد، [شاد و مغرور شود و] گويد پروردگارم مرا گرامى داشت‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6009,
            "text": "و اما چون او را [به بلا و محنت‌] بيازمايد و روزى او را بر او تنگ گيرد، [بى‌صبرى و ناسپاسى كند و] گويد پروردگارم مرا خوار داشت‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6010,
            "text": "چنين نيست، بلكه يتيم را نمى‌نوازيد",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6011,
            "text": "و بر اطعام بينوا ترغيب نمى‌كنيد",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6012,
            "text": "و ميراث را [به حق و ناحق‌] يكسره مى‌خوريد",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6013,
            "text": "و مال را چه بسيار دوست مى‌داريد",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6014,
            "text": "چنين نيست، آنگاه كه زمين پخش و پراكنده شود",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6015,
            "text": "و امر پروردگارت فرارسد و فرشتگان صف در صف آيند",
            "numberInSurah": 22,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6016,
            "text": "و در چنين روز جهنم را به ميان آورند، در چنين روز انسان پندگيرد و او را چه جاى پند گرفتن است‌",
            "numberInSurah": 23,
            "juz": 30,
            "manzil": 7,
            "page": 593,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6017,
            "text": "[از سر حسرت‌] گويد كاش من براى اين زندگانى [اخروى‌]ام پيش انديشى كرده بودم‌",
            "numberInSurah": 24,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6018,
            "text": "پس در چنين روز هيچ كس چون عذاب او [خداوند]، عذاب نكند",
            "numberInSurah": 25,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6019,
            "text": "و هيچ كس چون بند نهادن او، بند ننهد",
            "numberInSurah": 26,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6020,
            "text": "هان اى نفس مطمئنه‌",
            "numberInSurah": 27,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6021,
            "text": "به سوى پروردگارت كه تو از او خشنودى و او از تو خشنود است، باز گرد",
            "numberInSurah": 28,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6022,
            "text": "و در زمره بندگان من درآى‌",
            "numberInSurah": 29,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          },
          {
            "number": 6023,
            "text": "و به بهشت من وارد شو",
            "numberInSurah": 30,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 531,
            "hizbQuarter": 237,
            "sajda": false
          }
        ]
      },
      {
        "number": 90,
        "name": "سُورَةُ البَلَدِ",
        "englishName": "Al-Balad",
        "englishNameTranslation": "The City",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6024,
            "text": "سوگند به اين شهر مى‌خورم‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6025,
            "text": "و تو [يكچند] در اين شهر [حكمرواى مطلق و] دست گشاده‌اى‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6026,
            "text": "و سوگند به پدر و فرزندى كه پديد آورده است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6027,
            "text": "به راستى انسان را در رنج و محنت كشيدن آفريده‌ايم‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6028,
            "text": "آيا مى‌پندارد كه هرگز كسى بر او دست ندارد؟",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6029,
            "text": "گويد مالى فراوان [در راه مبارزه با پيامبر] بر باد دادم‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6030,
            "text": "آيا مى‌پندارد كه هيچ كس [كار و بار] او را نديده است؟",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6031,
            "text": "آيا براى او دو چشم نيافريده‌ايم؟",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6032,
            "text": "و زبانى و دو لب‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6033,
            "text": "و او را بر سر [هر يك از] دو راه [خير و شر] نياورده‌ايم؟",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6034,
            "text": "ولى خود در پى عقبه نبود",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6035,
            "text": "و تو چه دانى عقبه چيست‌",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6036,
            "text": "آزادسازى برده‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6037,
            "text": "يا اطعامى [از بينوايان‌] در روزى گرسنگى زده‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6038,
            "text": "در حق يتيمى خويشاوند",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6039,
            "text": "يا بينوايى خاك‌نشين‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6040,
            "text": "تا سپس از كسانى باشد كه ايمان آورده‌اند و همديگر را به شكيبايى و همديگر را به مهربانى سفارش كرده‌اند",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6041,
            "text": "اينانند كه اصحاب يمين‌اند",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6042,
            "text": "و كسانى كه به آيات ما انكار ورزيده‌اند، آنانند كه اصحاب شمال‌اند",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6043,
            "text": "بر آنان آتشى چند لايه، [چيره‌] باشد",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 594,
            "ruku": 532,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 91,
        "name": "سُورَةُ الشَّمۡسِ",
        "englishName": "Ash-Shams",
        "englishNameTranslation": "The Sun",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6044,
            "text": "سوگند به خورشيد و پرتوافشانى‌اش‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6045,
            "text": "و سوگند به ماه چون از آن پيروى كند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6046,
            "text": "و سوگند به روز چون روشنش دارد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6047,
            "text": "و سوگند به شب چون فرو پوشد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6048,
            "text": "و سوگند به آسمان و آنكه آن را برافراشت‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6049,
            "text": "و سوگند به زمين و آنكه آن را بگسترد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6050,
            "text": "و سوگند به نفس انسان و آنكه آن را سامان داد",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6051,
            "text": "آنگاه نافرمانى و پرهيزگارى‌اش را در آن الهام كرد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6052,
            "text": "به راستى هر كس كه آن را پاكيزه داشت، رستگار شد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6053,
            "text": "و به راستى نوميد شد هر كس كه آن را فرومايه داشت‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6054,
            "text": "قوم ثمود با طغيانش انكار پيشه كرد",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6055,
            "text": "آنگاه كه شقاوت‌پيشه‌ترينشان برپا خاست‌",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6056,
            "text": "حال آنكه پيامبر خدا به آنان گفته بود اين شتر خداوند است، او و بهره آبش را رعايت كنيد",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6057,
            "text": "سپس او را دروغ‌زن شمردند و آن [شتر] را پى كردند، آنگاه پروردگارشان آنان را به گناهشان، به يكسان نابود ساخت‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6058,
            "text": "و از عاقبت كارش نترسد",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 533,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 92,
        "name": "سُورَةُ اللَّيۡلِ",
        "englishName": "Al-Lail",
        "englishNameTranslation": "The Night",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6059,
            "text": "سوگند به شب چون فرو پوشاند",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6060,
            "text": "و سوگند به روز چون روشن شود",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6061,
            "text": "و سوگند به آنكه نرينه و مادينه آفريد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6062,
            "text": "كه كوشش شما پراكنده [و گونه‌گون‌] است‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6063,
            "text": "حال اگر كسى [مالى‌] بخشيد و پروا و پرهيز ورزيد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6064,
            "text": "و [وعده‌] بهشت را استوار داشت‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6065,
            "text": "زودا كه راهش را به سوى خير و آسانى هموار كنيم‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6066,
            "text": "و اما كسى كه دريغ‌ورزيد و بى‌نيازى نمود",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6067,
            "text": "و وعده بهشت را دروغ انگاشت‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6068,
            "text": "زودا كه راهش را به سوى شر و دشوارى هموار كنيم‌",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6069,
            "text": "و چون به هلاكت افتد مالش به كار او نيايد",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6070,
            "text": "بى‌گمان راهنمايى [بندگان‌] بر ماست‌",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6071,
            "text": "و بى‌گمان [جهان‌] واپسين و نخستين ما راست‌",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6072,
            "text": "پس شما را از آتشى كه زبانه زند هشدار دهم‌",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 595,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6073,
            "text": "كه جز شقاوت پيشه وارد آن نشود",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6074,
            "text": "همان كسى كه [حق را] انكار كرد و روى بر تافت‌",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6075,
            "text": "و تقواپيشه از آن دور داشته خواهد شد",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6076,
            "text": "همان كسى كه مالش را مى‌بخشد كه پاكدلى يابد",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6077,
            "text": "و بر او [منت از] نعمت كسى نيست كه بايد جزايش را بدهد",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6078,
            "text": "[و نمى‌كند آنچه مى‌كند] مگر در طلب خشنودى پروردگار بلندمرتبه‌اش‌",
            "numberInSurah": 20,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6079,
            "text": "و زودا كه خشنود شود",
            "numberInSurah": 21,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 534,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 93,
        "name": "سُورَةُ الضُّحَىٰ",
        "englishName": "Ad-Dhuhaa",
        "englishNameTranslation": "The Morning Hours",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6080,
            "text": "سوگند به روز روشن‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6081,
            "text": "و سوگند به شب چون بيارمد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6082,
            "text": "كه پروردگارت با تو بدرود نكرده و بى‌مهر نشده است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6083,
            "text": "و بى‌شك جهان واپسين براى تو بهتر از نخستين است‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6084,
            "text": "و پروردگارت به زودى به تو [مقام شفاعت‌] مى‌بخشد و خشنود مى‌شوى‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6085,
            "text": "آيا يتيمت نيافت كه سر و سامانت داد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6086,
            "text": "و تو را سرگشته يافت و رهنمايى كرد",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6087,
            "text": "و تو را تهيدست يافت و بى‌نيازت كرد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6088,
            "text": "پس با يتيم تندى مكن‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6089,
            "text": "و بر خواهنده بانگ مزن [و او را مران‌]",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          },
          {
            "number": 6090,
            "text": "و اما درباره نعمت پروردگارت [با سپاس‌] سخن بگوى‌",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 535,
            "hizbQuarter": 238,
            "sajda": false
          }
        ]
      },
      {
        "number": 94,
        "name": "سُورَةُ الشَّرۡحِ",
        "englishName": "Ash-Sharh",
        "englishNameTranslation": "The Consolation",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6091,
            "text": "آيا دلت را برايت گشاده نداشتيم؟",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6092,
            "text": "و بارت را [از دوشت‌] برنگرفتيم؟",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6093,
            "text": "همان كه مى‌خواست پشت را بشكند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6094,
            "text": "و آوازه‌ات را بلند گردانديم‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6095,
            "text": "بى‌گمان در جنب دشوارى، آسانى است‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6096,
            "text": "آرى در جنب دشوارى آسانى است‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6097,
            "text": "پس چون فراغت يافتى، [در دعا] بكوش‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6098,
            "text": "و به سوى پروردگارت بگراى‌",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 596,
            "ruku": 536,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 95,
        "name": "سُورَةُ التِّينِ",
        "englishName": "At-Tin",
        "englishNameTranslation": "The Fig",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6099,
            "text": "سوگند به [سرزمين قدسى‌] انجير و زيتون‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6100,
            "text": "و سوگند به طور سينا",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6101,
            "text": "و سوگند به اين شهر [و حرم‌] امن‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6102,
            "text": "به راستى كه انسان را در بهترين قوام آفريده‌ايم‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6103,
            "text": "سپس او را به فرودين فرود باز گردانديم‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6104,
            "text": "مگر كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، كه ايشان را پاداشى ناكاسته [/بى‌منت‌] است‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6105,
            "text": "[اى انسان‌] با اين حال چه چيزى تو را به انكار روز جزا كشانيد؟",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6106,
            "text": "آيا خداوند داورترين داوران نيست؟",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 537,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 96,
        "name": "سُورَةُ العَلَقِ",
        "englishName": "Al-Alaq",
        "englishNameTranslation": "The Clot",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6107,
            "text": "بخوان به نام پروردگارت كه آفريده است‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6108,
            "text": "كه انسان را از [نطفه و سپس‌] خون بسته آفريده است‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6109,
            "text": "بخوان و پروردگار تو بس گرامى است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6110,
            "text": "همان كه با قلم [و كتابت انسان را] آموزش داد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6111,
            "text": "به انسان چيزى را كه نمى‌دانست، آموخت‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6112,
            "text": "چنين نيست، بى‌گمان انسان سر به طغيان برآورد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6113,
            "text": "از اين كه خود را بى‌نياز [و توانگر] بيند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6114,
            "text": "همانا بازگشت به سوى پروردگار توست‌",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6115,
            "text": "آيا نگريسته‌اى كسى را كه باز مى‌دارد",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6116,
            "text": "بنده‌اى را كه به نماز برخيزد",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6117,
            "text": "آيا انديشيده‌اى كه اگر [پيامبر و پيرو او] بر طريق هدايت باشد",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6118,
            "text": "يا امر به پرهيزگارى كند [بر حق است‌]",
            "numberInSurah": 12,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6119,
            "text": "آيا انديشيده‌اى كه اگر انكار پيشه كند و روى برتابد [فقط خود را نابود سازد]",
            "numberInSurah": 13,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6120,
            "text": "آيا نمى‌داند كه همانا خداوند [همه چيز را] مى‌بيند؟",
            "numberInSurah": 14,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6121,
            "text": "حاشا، اگر از آن كار دست بر ندارد، موى پيشانى او را به سختى بگيريم‌",
            "numberInSurah": 15,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6122,
            "text": "موى پيشانى دروغ‌زن خطاپيشه را",
            "numberInSurah": 16,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6123,
            "text": "پس [مذبوحانه‌] هم‌مجلسانش را [به كمك‌] بخواند",
            "numberInSurah": 17,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6124,
            "text": "ما نيز آتشبانان دوزخ را فراخوانيم‌",
            "numberInSurah": 18,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6125,
            "text": "حاشا، از او پيروى مكن، و سجده بر و تقرب بجوى‌",
            "numberInSurah": 19,
            "juz": 30,
            "manzil": 7,
            "page": 597,
            "ruku": 538,
            "hizbQuarter": 239,
            "sajda": {
              "id": 15,
              "recommended": false,
              "obligatory": true
            }
          }
        ]
      },
      {
        "number": 97,
        "name": "سُورَةُ القَدۡرِ",
        "englishName": "Al-Qadr",
        "englishNameTranslation": "The Power, Fate",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6126,
            "text": "ما آن [قرآن‌] را در شب قدر نازل كرده‌ايم‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6127,
            "text": "و تو چه دانى شب قدر چيست؟",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6128,
            "text": "شب قدر بهتر از هزار ماه [عادى‌] است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6129,
            "text": "در آن [شب‌] فرشتگان و جبرئيل به اذن پروردگارشان براى انجام هر كار فرود آيند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6130,
            "text": "[اين شب‌] تا دميدن سپيده‌دم آكنده از سلامت [و امن و امان‌] است‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 539,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 98,
        "name": "سُورَةُ البَيِّنَةِ",
        "englishName": "Al-Bayyina",
        "englishNameTranslation": "The Evidence",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 6131,
            "text": "كافران از ميان اهل كتاب و مشركان، دست بردار نبودند، تا آنكه برايشان حجت هويدا آمد",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6132,
            "text": "پيامبرى از سوى خداوند كه بر آنان صحيفه‌هاى پاك [آسمانى‌] مى‌خواند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6133,
            "text": "كه در آنها نوشته‌هاى استوارى هست‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6134,
            "text": "و اهل كتاب تفرقه پيشه نكردند، مگر بعد از آنكه برايشان حجت هويدا آمد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6135,
            "text": "و جز اين فرمان نيافته بودند كه خداوند را بپرستند و پاكدينانه دين خود را براى او خالص دارند، و نماز را بر پا دارند و زكات را بپردازند، و اين دين استوار است‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6136,
            "text": "كافران از ميان اهل كتاب و مشركان در آتش جهنمند، و جاودانه در آنند، اينانند كه خود بدترين آفريدگانند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6137,
            "text": "كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، اينانند كه خود بهترين آفريدگانند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 598,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6138,
            "text": "پاداش آنان در نزد پروردگارشان بهشتهاى عدن است كه جويباران از فرودست آن جارى است، و جاودانه در آنند، خداوند از ايشان خشنود است و آنان هم از او خشنودند، اين از آن كسى است كه از پروردگارش بترسد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 540,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 99,
        "name": "سُورَةُ الزَّلۡزَلَةِ",
        "englishName": "Az-Zalzala",
        "englishNameTranslation": "The Earthquake",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 6139,
            "text": "آنگاه كه زمين به زلزله‌[ى واپسين‌]اش بلرزد",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6140,
            "text": "و زمين بارهايش را بيرون ريزد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6141,
            "text": "و انسان گويد آن را چه مى‌شود؟",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6142,
            "text": "در چنين روز رازهايش را باز گويد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6143,
            "text": "از آنكه پروردگارت به او الهام فرستاده است‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6144,
            "text": "در چنين روز انسانها گروه گروه باز گردند تا [نتيجه و حاصل‌] كارهايشان را به آنان بنمايانند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6145,
            "text": "پس هر كس هم سنگ ذره‌اى عمل خير انجام داده باشد، [پاداش‌] آن را مى‌بيند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6146,
            "text": "و هركس هم سنگ ذره‌اى عمل ناشايست انجام داده باشد، [كيفر] آن را مى‌بيند",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 541,
            "hizbQuarter": 239,
            "sajda": false
          }
        ]
      },
      {
        "number": 100,
        "name": "سُورَةُ العَادِيَاتِ",
        "englishName": "Al-Aadiyaat",
        "englishNameTranslation": "The Chargers",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6147,
            "text": "سوگند به اسبان تيز تك [جهادگر] كه نفس نفس مى‌زنند",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6148,
            "text": "و سوگند به اخگرانگيزان [از برخورد سمها]",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6149,
            "text": "و سوگند به تكاوران بامدادى‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6150,
            "text": "كه در آنجا گرد برانگيزند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6151,
            "text": "و با همديگر به ميانه آن [معركه‌] در آيند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6152,
            "text": "كه انسان در برابر پروردگارش ناسپاس است‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6153,
            "text": "و او بر اين امر گواه است‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6154,
            "text": "و او [انسان‌] از مالدوستى بخيل است‌",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 239,
            "sajda": false
          },
          {
            "number": 6155,
            "text": "آيا نمى‌داند كه چون آنچه در گورهاست زير و زبر شود",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 599,
            "ruku": 542,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6156,
            "text": "و راز دلها آشكار گردانيده شود",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 542,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6157,
            "text": "بى‌گمان پروردگارت در چنين روز از حال آنان آگاه است‌",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 542,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 101,
        "name": "سُورَةُ القَارِعَةِ",
        "englishName": "Al-Qaari'a",
        "englishNameTranslation": "The Calamity",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6158,
            "text": "درهم كوب‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6159,
            "text": "درهم كوب چيست؟",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6160,
            "text": "و تو چه دانى كه درهم كوب چيست؟",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6161,
            "text": "روزى كه مردم مانند پروانه‌هاى پراكنده باشند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6162,
            "text": "و كوهها مانند پشمهاى زده باشند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6163,
            "text": "در آن حال هر كس كفه‌ها[ى اعمال خير]ش سنگينى كند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6164,
            "text": "او در زندگانى پسنديده است‌",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6165,
            "text": "و اما كسى كه كفه‌هايش سبكى يابد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6166,
            "text": "بازگشتگاه او دوزخ است‌",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6167,
            "text": "و تو چه دانى آن چيست؟",
            "numberInSurah": 10,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6168,
            "text": "آتشى است [بس‌] سوزان‌",
            "numberInSurah": 11,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 543,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 102,
        "name": "سُورَةُ التَّكَاثُرِ",
        "englishName": "At-Takaathur",
        "englishNameTranslation": "Competition",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6169,
            "text": "فزون‌طلبى شما را بازى داد",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6170,
            "text": "تا آنكه با گورها رو در رو شديد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6171,
            "text": "حاشا، زودا كه بدانيد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6172,
            "text": "باز حاشا، زودا كه بدانيد",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6173,
            "text": "حاشا اگر به علم‌اليقين بدانيد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6174,
            "text": "بى‌شبهه دوزخ را ببينيد",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6175,
            "text": "آرى آن را به عين‌اليقين بنگريد",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6176,
            "text": "سپس در چنين روز از شما درباره نعمتها[ى كفران شده دنيا] بازخواست شود",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 600,
            "ruku": 544,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 103,
        "name": "سُورَةُ العَصۡرِ",
        "englishName": "Al-Asr",
        "englishNameTranslation": "The Declining Day, Epoch",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6177,
            "text": "سوگند به روزگار",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 545,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6178,
            "text": "كه بى‌گمان انسان در زيانكارى است‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 545,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6179,
            "text": "مگر كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند و همديگر را به حق سفارش كرده‌اند، و همديگر را به شكيبايى سفارش كرده‌اند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 545,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 104,
        "name": "سُورَةُ الهُمَزَةِ",
        "englishName": "Al-Humaza",
        "englishNameTranslation": "The Traducer",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6180,
            "text": "واى بر هر عيب‌جوى طعنه‌زن‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6181,
            "text": "همان كه مالى گردآورد و شماره‌اش كرد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6182,
            "text": "پندارد كه مالش او را جاودانه مى‌سازد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6183,
            "text": "حاشا، بى‌شبهه به كام درهم شكن انداخته شود",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6184,
            "text": "و تو چه دانى كه درهم شكن چيست‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6185,
            "text": "آتش الهى فروزان‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6186,
            "text": "همان كه بر دلها راه يابد",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6187,
            "text": "بر ايشان لايه لايه فرو افتد",
            "numberInSurah": 8,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6188,
            "text": "در ستونهايى بالا بلند",
            "numberInSurah": 9,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 546,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 105,
        "name": "سُورَةُ الفِيلِ",
        "englishName": "Al-Fil",
        "englishNameTranslation": "The Elephant",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6189,
            "text": "آيا ندانسته‌اى كه پروردگارت با پيل‌سواران چگونه رفتار كرد؟",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6190,
            "text": "آيا نيرنگشان را بى‌اثر نساخت؟",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6191,
            "text": "و بر سر آنان پرندگانى فوج‌فوج فرستاد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6192,
            "text": "كه بر آنان سنگريزه‌هايى از سنگ گل فرو انداختند",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6193,
            "text": "و سرانجام آنان را مانند برگ كاه نيمه جويده ساخت‌",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 601,
            "ruku": 547,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 106,
        "name": "سُورَةُ قُرَيۡشٍ",
        "englishName": "Quraish",
        "englishNameTranslation": "Quraysh",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6194,
            "text": "براى همبستگى قريش‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6195,
            "text": "همبستگيشان در كوچ زمستانى و تابستانى‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6196,
            "text": "پس بايد صاحب اين خانه را بپرستند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6197,
            "text": "همان كه ايشان را پس از گرسنگى [آذوقه و] خوراك داد و پس از ترس [و نگرانى‌]، ايمن داشت‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 548,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 107,
        "name": "سُورَةُ المَاعُونِ",
        "englishName": "Al-Maa'un",
        "englishNameTranslation": "Almsgiving",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6198,
            "text": "آيا ملاحظه كرده‌اى كسى را كه روز جزا را انكار مى‌كند؟",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6199,
            "text": "اين همان كسى است كه يتيم را مى‌راند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6200,
            "text": "و بر اطعام بينوا، ترغيب نمى‌كند",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6201,
            "text": "پس واى بر آن نمازگزاران‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6202,
            "text": "همان كسانى كه از نماز خويش غافلند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6203,
            "text": "كسانى كه ايشان [در نماز هم‌] رياكارى مى‌كنند",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6204,
            "text": "و زكات را باز مى‌دارند",
            "numberInSurah": 7,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 549,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 108,
        "name": "سُورَةُ الكَوۡثَرِ",
        "englishName": "Al-Kawthar",
        "englishNameTranslation": "Abundance",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6205,
            "text": "ما به تو كوثر بخشيده‌ايم‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 550,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6206,
            "text": "پس براى پروردگارت نماز بگزار و قربانى كن‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 550,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6207,
            "text": "بى‌گمان دشمن تو بى‌سرانجام [و بلاعقب‌] است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 602,
            "ruku": 550,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 109,
        "name": "سُورَةُ الكَافِرُونَ",
        "englishName": "Al-Kaafiroon",
        "englishNameTranslation": "The Disbelievers",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6208,
            "text": "بگو هان اى كافران‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6209,
            "text": "من معبود شما را نمى‌پرستم‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6210,
            "text": "و شما هم پرستندگان معبود من نيستيد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6211,
            "text": "و من پرستنده آنچه شما مى‌پرستيد نيستم‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6212,
            "text": "و شما هم پرستندگان معبود من نيستيد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6213,
            "text": "شما را دين شما، و مرا دين من‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 551,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 110,
        "name": "سُورَةُ النَّصۡرِ",
        "englishName": "An-Nasr",
        "englishNameTranslation": "Divine Support",
        "revelationType": "Medinan",
        "ayahs": [
          {
            "number": 6214,
            "text": "آنگاه كه نصرت الهى و پيروزى فراز آيد",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6215,
            "text": "و مردم را بينى كه گروه گروه به دين الهى در آيند",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6216,
            "text": "پس سپاسگزارانه پروردگارت را نيايش كن و از او آمرزش بخواه كه او بس توبه‌پذير است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 552,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 111,
        "name": "سُورَةُ المَسَدِ",
        "englishName": "Al-Masad",
        "englishNameTranslation": "The Palm Fibre",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6217,
            "text": "زيانكار باد دستان ابولهب، و خود او هم زيانكار شد",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6218,
            "text": "مالش و دستاوردش به كارش نيامد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6219,
            "text": "زودا كه به آتشى شعله‌ور در آيد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6220,
            "text": "و زنش هيزم‌كش [و آتش‌افروز معركه‌] است‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6221,
            "text": "و ريسمانى از ليف خرماى تافته بر گردن دارد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 603,
            "ruku": 553,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 112,
        "name": "سُورَةُ الإِخۡلَاصِ",
        "englishName": "Al-Ikhlaas",
        "englishNameTranslation": "Sincerity",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6222,
            "text": "بگو او خداوند يگانه است‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6223,
            "text": "خداوند صمد",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6224,
            "text": "نه فرزند آرد و نه از كسى زاده است‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6225,
            "text": "و او را هيچ كس همتا نيست‌",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 554,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 113,
        "name": "سُورَةُ الفَلَقِ",
        "englishName": "Al-Falaq",
        "englishNameTranslation": "The Dawn",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6226,
            "text": "بگو به پروردگار فلق پناه مى‌برم‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6227,
            "text": "از شر هر آنچه آفريده است‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6228,
            "text": "و از شر شب چون در آيد",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6229,
            "text": "و از شر زنان افسونگر دمنده در گره‌ها",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6230,
            "text": "و از شر رشك‌برى كه رشك برد",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 555,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      },
      {
        "number": 114,
        "name": "سُورَةُ النَّاسِ",
        "englishName": "An-Naas",
        "englishNameTranslation": "Mankind",
        "revelationType": "Meccan",
        "ayahs": [
          {
            "number": 6231,
            "text": "بگو به پروردگار مردم پناه مى‌برم‌",
            "numberInSurah": 1,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6232,
            "text": "فرمانرواى مردم‌",
            "numberInSurah": 2,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6233,
            "text": "خداى مردم‌",
            "numberInSurah": 3,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6234,
            "text": "از شر وسوسه‌گر پنهانكار",
            "numberInSurah": 4,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6235,
            "text": "كسى كه در دلهاى مردم وسوسه مى‌كند",
            "numberInSurah": 5,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          },
          {
            "number": 6236,
            "text": "اعم از جن و انسان‌",
            "numberInSurah": 6,
            "juz": 30,
            "manzil": 7,
            "page": 604,
            "ruku": 556,
            "hizbQuarter": 240,
            "sajda": false
          }
        ]
      }
    ],
    "edition": {
      "identifier": "fa.khorramshahi",
